                            </p>

                            <Link
                                href="/results"
                                className="w-full py-4 bg-cyan-500 hover:bg-cyan-400 text-black font-black uppercase tracking-widest rounded-xl transition-all shadow-lg shadow-cyan-500/20 flex items-center justify-center gap-2 group"
                            >
                                View Results <ArrowRight className="w-4 h-4 group-hover:translate-x-1 transition-transform" />
                            </Link>
                        </div>
                    )}
                </motion.div>
            </div>
        </div>
    );
}

function LoadingFallback() {
    return (
        <div className="min-h-screen bg-black flex items-center justify-center p-4">
            <div className="max-w-md w-full">
                <div className="bg-neutral-900/50 border border-white/10 rounded-3xl p-8 md:p-12 text-center">
                    <div className="flex flex-col items-center">
                        <div className="w-16 h-16 rounded-full bg-cyan-500/10 flex items-center justify-center mb-6 animate-pulse">
                            <Loader2 className="w-8 h-8 text-cyan-400 animate-spin" />
                        </div>
                        <h1 className="text-2xl font-bold text-white mb-2">Loading...</h1>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default function CheckoutSuccessPage() {
    return (
        <Suspense fallback={<LoadingFallback />}>
            <CheckoutSuccessContent />
        </Suspense>
    );
