   267→  { id: 'eyes', name: 'Eyes', color: '#06b6d4', description: 'Eye shape, spacing, and canthal tilt' },
   268→  { id: 'nose', name: 'Nose', color: '#fbbf24', description: 'Nasal proportions and angles' },
   269→  { id: 'lips', name: 'Lips', color: '#ec4899', description: 'Lip proportions and projection' },
