   353→  affiliateLink: string;
   354→  affiliateType: "amazon" | "direct";
   355→  supplementId: string;
   356→  priority: number;
   357→  baseStackItem?: boolean;
   358→}
   359→
   360→export interface ProductRecommendation {
   361→  product: Product;
   362→  state: "flaw" | "ideal";
   363→  targetMetric: string;
   364→  message: string;
   365→  urgency: "high" | "medium" | "low";
   366→  matchedMetrics: string[];
   367→}
   368→
   369→export interface DailyStack {
