     1→'use client';
     2→
     3→import { useState, useRef, useCallback, useEffect } from 'react';
     4→import Image from 'next/image';
     5→import { motion } from 'framer-motion';
     6→import {
     7→  ChevronLeft,
     8→  ChevronRight,
     9→  ChevronUp,
    10→  ChevronDown,
    11→  Check,
    12→  Info,
    13→  Eye,
    14→  EyeOff,
    15→  Scan,
    16→  Loader2,
    17→  Move,
    18→  Focus,
    19→} from 'lucide-react';
    20→import {
    21→  LandmarkPoint,
    22→  FRONT_PROFILE_LANDMARKS,
    23→  SIDE_PROFILE_LANDMARKS,
    24→  getLandmarkColor,
    25→  FRONT_LANDMARK_CATEGORIES,
    26→  SIDE_LANDMARK_CATEGORIES,
    27→} from '@/lib/landmarks';
    28→import { getLandmarkImagePath, getLandmarkPlacementGuide } from '@/lib/landmarkImages';
    29→import { detectFromImageUrl } from '@/lib/mediapipeDetection';
    30→
    31→interface LandmarkAnalysisToolProps {
    32→  imageUrl: string;
    33→  mode: 'front' | 'side';
    34→  initialLandmarks?: LandmarkPoint[];
    35→  onLandmarksChange?: (landmarks: LandmarkPoint[]) => void;
    36→  onComplete?: (landmarks: LandmarkPoint[]) => void;
    37→  onBack?: () => void;
    38→}
    39→
    40→function getOrderedLandmarks(mode: 'front' | 'side'): string[] {
    41→  const landmarks = mode === 'front' ? FRONT_PROFILE_LANDMARKS : SIDE_PROFILE_LANDMARKS;
    42→  return landmarks.map(lm => lm.id);
    43→}
    44→
    45→export function LandmarkAnalysisTool({
    46→  imageUrl,
    47→  mode,
    48→  initialLandmarks,
    49→  onLandmarksChange,
    50→  onComplete,
    51→  onBack,
    52→}: LandmarkAnalysisToolProps) {
    53→  const containerRef = useRef<HTMLDivElement>(null);
    54→  const [imageDimensions, setImageDimensions] = useState<{ width: number; height: number } | null>(null);
    55→  const [containerSize, setContainerSize] = useState<{ width: number; height: number } | null>(null);
    56→  const defaultLandmarks = mode === 'front' ? FRONT_PROFILE_LANDMARKS : SIDE_PROFILE_LANDMARKS;
    57→  const categories = mode === 'front' ? FRONT_LANDMARK_CATEGORIES : SIDE_LANDMARK_CATEGORIES;
    58→  const orderedIds = getOrderedLandmarks(mode);
    59→
    60→  const [landmarks, setLandmarks] = useState<LandmarkPoint[]>(
    61→    initialLandmarks || defaultLandmarks
    62→  );
    63→  const [currentStepIndex, setCurrentStepIndex] = useState(0);
    64→  const [zoomLevel, setZoomLevel] = useState(2.5); // Start zoomed in for precision
    65→  const [pan, setPan] = useState({ x: 0, y: 0 });
    66→  const [isDragging, setIsDragging] = useState(false);
    67→  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
    68→  const [showAllPoints, setShowAllPoints] = useState(false);
    69→  const [pressedKeys, setPressedKeys] = useState<Set<string>>(new Set());
    70→  const [activeTab, setActiveTab] = useState<'info' | 'reference'>('info');
    71→  const [isDetecting, setIsDetecting] = useState(true);
    72→  const [hasAutoDetected, setHasAutoDetected] = useState(false);
    73→  const [autoZoomEnabled, setAutoZoomEnabled] = useState(true);
    74→  const [placedLandmarks, setPlacedLandmarks] = useState<Set<string>>(new Set());
    75→
    76→  const currentLandmarkId = orderedIds[currentStepIndex];
    77→  const currentLandmark = landmarks.find((lm) => lm.id === currentLandmarkId);
    78→  const totalSteps = orderedIds.length;
    79→  const progress = ((currentStepIndex + 1) / totalSteps) * 100;
    80→
    81→  const color = currentLandmark
    82→    ? getLandmarkColor(currentLandmark.id, categories)
    83→    : '#00f3ff';
    84→
    85→  // Load image dimensions on mount
    86→  useEffect(() => {
    87→    const img = new window.Image();
    88→    img.onload = () => {
    89→      setImageDimensions({ width: img.naturalWidth, height: img.naturalHeight });
    90→    };
    91→    img.src = imageUrl;
    92→  }, [imageUrl]);
    93→
    94→  // Track container size changes with ResizeObserver
    95→  useEffect(() => {
    96→    if (!containerRef.current) return;
    97→
    98→    const observer = new ResizeObserver((entries) => {
    99→      const { width, height } = entries[0].contentRect;
   100→      setContainerSize({ width, height });
   101→    });
   102→
   103→    observer.observe(containerRef.current);
   104→    return () => observer.disconnect();
   105→  }, []);
   106→
   107→  // Calculate the rendered image bounds within the container (accounting for object-contain)
   108→  const getImageBounds = useCallback(() => {
   109→    if (!containerRef.current || !imageDimensions || !containerSize) {
   110→      return { offsetX: 0, offsetY: 0, renderedWidth: 1, renderedHeight: 1, containerWidth: 1, containerHeight: 1 };
   111→    }
   112→
   113→    const containerWidth = containerSize.width;
   114→    const containerHeight = containerSize.height;
   115→    const imageAspect = imageDimensions.width / imageDimensions.height;
   116→    const containerAspect = containerWidth / containerHeight;
   117→
   118→    let renderedWidth: number;
   119→    let renderedHeight: number;
   120→    let offsetX = 0;
   121→    let offsetY = 0;
   122→
   123→    if (imageAspect > containerAspect) {
   124→      renderedWidth = containerWidth;
   125→      renderedHeight = containerWidth / imageAspect;
   126→      offsetY = (containerHeight - renderedHeight) / 2;
   127→    } else {
   128→      renderedHeight = containerHeight;
   129→      renderedWidth = containerHeight * imageAspect;
   130→      offsetX = (containerWidth - renderedWidth) / 2;
   131→    }
   132→
   133→    return { offsetX, offsetY, renderedWidth, renderedHeight, containerWidth, containerHeight };
   134→  }, [imageDimensions, containerSize]);
   135→
   136→  // Get what normalized coordinates the center orb points to on the image
   137→  const getCrosshairNormalizedPosition = useCallback(() => {
   138→    const bounds = getImageBounds();
   139→    if (!bounds.renderedWidth || !bounds.renderedHeight) {
   140→      return { x: 0.5, y: 0.5 };
   141→    }
   142→
   143→    // The container center (where the orb is) in screen coordinates
   144→    const containerCenterX = bounds.containerWidth / 2;
   145→    const containerCenterY = bounds.containerHeight / 2;
   146→
   147→    // The transform is: translate(pan.x, pan.y) scale(zoomLevel)
   148→    // To find what image point is at container center, we reverse this:
   149→    // 1. The point at container center after transform comes from:
   150→    //    screenPos = imagePos * zoomLevel + pan
   151→    // 2. Solving for imagePos:
   152→    //    imagePos = (screenPos - pan) / zoomLevel
   153→    const imageX = (containerCenterX - pan.x) / zoomLevel;
   154→    const imageY = (containerCenterY - pan.y) / zoomLevel;
   155→
   156→    // Convert to normalized (0-1) coordinates within the image
   157→    const normalizedX = (imageX - bounds.offsetX) / bounds.renderedWidth;
   158→    const normalizedY = (imageY - bounds.offsetY) / bounds.renderedHeight;
   159→
   160→    return {
   161→      x: Math.max(0, Math.min(1, normalizedX)),
   162→      y: Math.max(0, Math.min(1, normalizedY)),
   163→    };
   164→  }, [getImageBounds, pan, zoomLevel]);
   165→
   166→  // Convert normalized image coordinates (0-1) to pixel position within the container
   167→  // Note: This is used for landmarks INSIDE the transformed container, so no zoom/pan needed
   168→  const getLandmarkPixelPosition = useCallback((x: number, y: number) => {
   169→    const bounds = getImageBounds();
   170→    // Position relative to container (the parent transform handles zoom/pan)
   171→    const pixelX = bounds.offsetX + x * bounds.renderedWidth;
   172→    const pixelY = bounds.offsetY + y * bounds.renderedHeight;
   173→    return { left: pixelX, top: pixelY };
   174→  }, [getImageBounds]);
   175→
   176→  useEffect(() => {
   177→    onLandmarksChange?.(landmarks);
   178→  }, [landmarks, onLandmarksChange]);
   179→
   180→  // Confirm placement: set current landmark to where crosshair points
   181→  const confirmPlacement = useCallback(() => {
   182→    const pos = getCrosshairNormalizedPosition();
   183→    setLandmarks((prev) =>
   184→      prev.map((lm) =>
   185→        lm.id === currentLandmarkId ? { ...lm, x: pos.x, y: pos.y } : lm
   186→      )
   187→    );
   188→    setPlacedLandmarks((prev) => new Set(prev).add(currentLandmarkId));
   189→  }, [currentLandmarkId, getCrosshairNormalizedPosition]);
   190→
   191→  // Handle click - confirms placement at crosshair position
   192→  // eslint-disable-next-line @typescript-eslint/no-unused-vars
   193→  const handleContainerClick = useCallback(() => {
   194→    if (isDragging) return;
   195→    confirmPlacement();
   196→  }, [isDragging, confirmPlacement]);
   197→
   198→  // Mouse down - start dragging to pan
   199→  const handleMouseDown = useCallback(
   200→    (e: React.MouseEvent) => {
   201→      if (e.button === 0) {
   202→        setIsDragging(true);
   203→        setDragStart({ x: e.clientX - pan.x, y: e.clientY - pan.y });
   204→      }
   205→    },
   206→    [pan]
   207→  );
   208→
   209→  // Mouse move - pan the image
   210→  const handleMouseMove = useCallback(
   211→    (e: React.MouseEvent) => {
   212→      if (isDragging) {
   213→        setPan({
   214→          x: e.clientX - dragStart.x,
   215→          y: e.clientY - dragStart.y,
   216→        });
   217→      }
   218→    },
   219→    [isDragging, dragStart]
   220→  );
   221→
   222→  const handleMouseUp = useCallback(() => {
   223→    setIsDragging(false);
   224→  }, []);
   225→
   226→  const handleResetZoom = useCallback(() => {
   227→    setZoomLevel(2.5);
   228→    setPan({ x: 0, y: 0 });
   229→  }, []);
   230→
   231→  // Pan the image using arrow keys (moves the crosshair target point on the image)
   232→  const panImage = useCallback((direction: 'up' | 'down' | 'left' | 'right') => {
   233→    const step = 20; // pixels to pan per keypress
   234→    setPan((prev) => {
   235→      switch (direction) {
   236→        case 'up': return { ...prev, y: prev.y + step };
   237→        case 'down': return { ...prev, y: prev.y - step };
   238→        case 'left': return { ...prev, x: prev.x + step };
   239→        case 'right': return { ...prev, x: prev.x - step };
   240→        default: return prev;
   241→      }
   242→    });
   243→  }, []);
   244→
   245→  const handleNext = useCallback(() => {
   246→    // Confirm placement at current crosshair position before moving to next
   247→    confirmPlacement();
   248→
   249→    if (currentStepIndex < totalSteps - 1) {
   250→      setCurrentStepIndex((prev) => prev + 1);
   251→      // Don't reset zoom - let zoomToLandmark handle it
   252→    } else {
   253→      onComplete?.(landmarks);
   254→    }
   255→  }, [currentStepIndex, totalSteps, landmarks, onComplete, confirmPlacement]);
   256→
   257→  const handlePrevious = useCallback(() => {
   258→    if (currentStepIndex > 0) {
   259→      setCurrentStepIndex((prev) => prev - 1);
   260→      handleResetZoom();
   261→    } else {
   262→      onBack?.();
   263→    }
   264→  }, [currentStepIndex, handleResetZoom, onBack]);
   265→
   266→  // Auto-zoom to the current landmark's region - centers the landmark under the fixed orb
   267→  const zoomToLandmark = useCallback((landmarkId: string) => {
   268→    if (!autoZoomEnabled || !containerRef.current || !containerSize || !imageDimensions) return;
   269→
   270→    const landmark = landmarks.find(lm => lm.id === landmarkId);
   271→    if (!landmark) return;
   272→
   273→    const bounds = getImageBounds();
   274→    if (bounds.renderedWidth <= 1 || bounds.renderedHeight <= 1) return; // Bounds not ready
   275→
   276→    // Zoom to 2.5x for good precision
   277→    const newZoom = 2.5;
   278→    setZoomLevel(newZoom);
   279→
   280→    // Calculate pan so the landmark appears at container center (under the fixed orb)
   281→    const containerCenterX = bounds.containerWidth / 2;
   282→    const containerCenterY = bounds.containerHeight / 2;
   283→
   284→    // Where the landmark is in unzoomed image space
   285→    const landmarkX = bounds.offsetX + landmark.x * bounds.renderedWidth;
   286→    const landmarkY = bounds.offsetY + landmark.y * bounds.renderedHeight;
   287→
   288→    // Pan needed: after zoom, landmark should be at container center
   289→    // Transform is: translate(pan) scale(zoom) with origin at 0,0
   290→    // A point at landmarkPos ends up at: landmarkPos * zoom + pan
   291→    // We want it at containerCenter, so: pan = containerCenter - landmarkPos * zoom
   292→    setPan({
   293→      x: containerCenterX - landmarkX * newZoom,
   294→      y: containerCenterY - landmarkY * newZoom,
   295→    });
   296→  }, [autoZoomEnabled, landmarks, getImageBounds, containerSize, imageDimensions]);
   297→
   298→  // Auto-detect landmarks using MediaPipe
   299→  const handleAutoDetect = useCallback(async () => {
   300→    setIsDetecting(true);
   301→    try {
   302→      const result = await detectFromImageUrl(imageUrl, mode);
   303→      console.log('[DEBUG] Detection result:', result);
   304→      console.log('[DEBUG] Sample landmarks:', result?.landmarks.slice(0, 5));
   305→      if (result && result.landmarks.length > 0) {
   306→        // Update landmarks with detected positions
   307→        setLandmarks(prev =>
   308→          prev.map(lm => {
   309→            const detected = result.landmarks.find(d => d.id === lm.id);
   310→            if (detected) {
   311→              return { ...lm, x: detected.x, y: detected.y };
   312→            }
   313→            return lm;
   314→          })
   315→        );
   316→        setHasAutoDetected(true);
   317→        // Zoom to first landmark after detection
   318→        zoomToLandmark(orderedIds[0]);
   319→      }
   320→    } catch (error) {
   321→      console.error('Auto-detection failed:', error);
   322→    } finally {
   323→      setIsDetecting(false);
   324→    }
   325→  }, [imageUrl, mode, orderedIds, zoomToLandmark]);
   326→
   327→  // Auto-detect after image dimensions are loaded (prevents race condition)
   328→  useEffect(() => {
   329→    if (imageDimensions) {
   330→      handleAutoDetect();
   331→    }
   332→    // eslint-disable-next-line react-hooks/exhaustive-deps
   333→  }, [imageDimensions]); // Run after dimensions are loaded
   334→
   335→  // Auto-zoom when step changes or container size becomes available
   336→  useEffect(() => {
