   345→      if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight', 'Enter', 'Backspace', 'r', 'R'].includes(e.key)) {
   346→        e.preventDefault();
   347→      }
   348→
   349→      setPressedKeys((prev) => new Set(prev).add(e.key));
   350→
   351→      switch (e.key) {
   352→        case 'ArrowUp': panImage('up'); break;
   353→        case 'ArrowDown': panImage('down'); break;
   354→        case 'ArrowLeft': panImage('left'); break;
   355→        case 'ArrowRight': panImage('right'); break;
   356→        case 'Enter': handleNext(); break;
   357→        case 'Backspace': handlePrevious(); break;
   358→        case 'r':
   359→        case 'R': handleResetZoom(); break;
   360→      }
   361→    };
   362→
   363→    const handleKeyUp = (e: KeyboardEvent) => {
   364→      setPressedKeys((prev) => {
   365→        const next = new Set(prev);
   366→        next.delete(e.key);
   367→        return next;
   368→      });
   369→    };
   370→
   371→    window.addEventListener('keydown', handleKeyDown);
   372→    window.addEventListener('keyup', handleKeyUp);
   373→    return () => {
   374→      window.removeEventListener('keydown', handleKeyDown);
   375→      window.removeEventListener('keyup', handleKeyUp);
   376→    };
   377→  }, [panImage, handleNext, handlePrevious, handleResetZoom]);
