    33→  initialLandmarks?: LandmarkPoint[];
    34→  onLandmarksChange?: (landmarks: LandmarkPoint[]) => void;
    35→  onComplete?: (landmarks: LandmarkPoint[]) => void;
    36→}
    37→
    38→// Keyboard key component with 3D effect
    39→function KeyboardKey({ isPressed, icon }: { isPressed: boolean; icon: React.ReactNode }) {
    40→  return (
    41→    <div className="relative w-9 h-9">
    42→      <div
    43→        className={`absolute inset-0 rounded-md transition-colors duration-75 ${
    44→          isPressed ? 'bg-accent/50' : 'bg-white/20'
    45→        }`}
    46→        style={{ transform: 'translateY(3px)' }}
    47→      />
    48→      <div
    49→        className={`relative w-full h-full rounded-md border flex items-center justify-center transition-colors duration-75 ${
    50→          isPressed
    51→            ? 'bg-accent border-accent text-black'
    52→            : 'bg-background-secondary border-border text-foreground-dim'
    53→        }`}
    54→        style={{
    55→          boxShadow: isPressed
    56→            ? 'none'
    57→            : 'rgba(0, 0, 0, 0.2) 0px -1px 0px inset, rgba(0, 0, 0, 0.1) 0px 1px 2px',
    58→          transform: isPressed ? 'translateY(2px)' : 'none',
    59→        }}
    60→      >
    61→        {icon}
    62→      </div>
    63→    </div>
    64→  );
    65→}
    66→
    67→export function GuidedLandmarkPlacement({
    68→  imageUrl,
    69→  initialLandmarks,
    70→  onLandmarksChange,
    71→  onComplete,
    72→}: GuidedLandmarkPlacementProps) {
    73→  const containerRef = useRef<HTMLDivElement>(null);
    74→  const [landmarks, setLandmarks] = useState<LandmarkPoint[]>(
    75→    initialLandmarks || FRONT_PROFILE_LANDMARKS
    76→  );
    77→  const [currentStepIndex, setCurrentStepIndex] = useState(0);
    78→  const [zoomLevel, setZoomLevel] = useState<1 | 2 | 3>(1); // 1=2x, 2=4x, 3=8x
    79→  const zoom = zoomLevel === 1 ? 2 : zoomLevel === 2 ? 4 : 8;
    80→  const [pan, setPan] = useState({ x: 0, y: 0 });
    81→  const [isDragging, setIsDragging] = useState(false);
    82→  const [showAllPoints, setShowAllPoints] = useState(false);
    83→  const [isFullscreen, setIsFullscreen] = useState(false);
    84→  const [referenceImageError, setReferenceImageError] = useState(false);
    85→  const [activeTab, setActiveTab] = useState<'photo' | 'diagram' | 'howToFind'>('photo');
    86→  const [pressedKey, setPressedKey] = useState<string | null>(null);
    87→
    88→  // Use landmark order from FRONT_PROFILE_LANDMARKS
    89→  const landmarkOrder = FRONT_PROFILE_LANDMARKS.map(lm => lm.id);
    90→  const currentLandmarkId = landmarkOrder[currentStepIndex];
    91→  const currentLandmark = landmarks.find((lm) => lm.id === currentLandmarkId);
    92→  const totalSteps = landmarkOrder.length;
    93→  const progress = ((currentStepIndex + 1) / totalSteps) * 100;
    94→
    95→  useEffect(() => {
    96→    onLandmarksChange?.(landmarks);
    97→  }, [landmarks, onLandmarksChange]);
    98→
    99→  useEffect(() => {
   100→    // Reset reference image error when step changes
   101→    setReferenceImageError(false);
   102→  }, [currentStepIndex]);
   103→
   104→  const updateLandmarkPosition = useCallback(
   105→    (x: number, y: number) => {
   106→      setLandmarks((prev) =>
   107→        prev.map((lm) =>
   108→          lm.id === currentLandmarkId ? { ...lm, x, y } : lm
   109→        )
   110→      );
   111→    },
   112→    [currentLandmarkId]
   113→  );
   114→
   115→  const handleContainerClick = useCallback(
   116→    (e: React.MouseEvent<HTMLDivElement>) => {
   117→      if (!containerRef.current || isDragging) return;
   118→
   119→      const rect = containerRef.current.getBoundingClientRect();
   120→      const rawX = (e.clientX - rect.left - rect.width / 2 - pan.x) / zoom + rect.width / 2;
   121→      const rawY = (e.clientY - rect.top - rect.height / 2 - pan.y) / zoom + rect.height / 2;
   122→
   123→      const x = Math.max(0, Math.min(1, rawX / rect.width));
   124→      const y = Math.max(0, Math.min(1, rawY / rect.height));
   125→
   126→      updateLandmarkPosition(x, y);
   127→    },
   128→    [isDragging, pan, zoom, updateLandmarkPosition]
   129→  );
   130→
   131→  const handleMouseDown = useCallback(
   132→    (e: React.MouseEvent) => {
   133→      if (e.button === 0 && zoom > 1) {
   134→        // Only allow panning when zoomed in
   135→        setIsDragging(true);
   136→      }
   137→    },
   138→    [zoom]
   139→  );
   140→
   141→  const handleMouseMove = useCallback(
   142→    (e: React.MouseEvent) => {
   143→      if (isDragging && zoom > 1) {
   144→        setPan((prev) => ({
   145→          x: prev.x + e.movementX,
   146→          y: prev.y + e.movementY,
   147→        }));
   148→      }
   149→    },
   150→    [isDragging, zoom]
   151→  );
   152→
   153→  const handleMouseUp = useCallback(() => {
   154→    setIsDragging(false);
   155→  }, []);
   156→
   157→  const handleZoomIn = () => {
   158→    if (zoomLevel >= 3) return;
   159→    const newLevel = (zoomLevel + 1) as 2 | 3;
   160→    // Adjust pan to keep the same center point visible
   161→    const oldZoom = zoom;
   162→    const newZoom = newLevel === 2 ? 4 : 8;
   163→    const zoomRatio = newZoom / oldZoom;
   164→    setPan(prev => ({
   165→      x: prev.x * zoomRatio,
   166→      y: prev.y * zoomRatio,
   167→    }));
   168→    setZoomLevel(newLevel);
   169→  };
   170→
   171→  const handleZoomOut = () => {
   172→    if (zoomLevel <= 1) return;
   173→    const newLevel = (zoomLevel - 1) as 1 | 2;
   174→    if (newLevel === 1) {
   175→      setPan({ x: 0, y: 0 });
   176→    } else {
   177→      // Adjust pan to keep the same center point visible
   178→      const oldZoom = zoom;
   179→      const newZoom = 4; // level 2 = 4x
   180→      const zoomRatio = newZoom / oldZoom;
   181→      setPan(prev => ({
   182→        x: prev.x * zoomRatio,
   183→        y: prev.y * zoomRatio,
   184→      }));
   185→    }
   186→    setZoomLevel(newLevel);
   187→  };
   188→
   189→  const handleResetZoom = () => {
   190→    setZoomLevel(1);
   191→    setPan({ x: 0, y: 0 });
   192→  };
   193→
   194→  const handleSetZoomLevel = useCallback((level: 1 | 2 | 3) => {
   195→    if (level === 1) {
   196→      // Reset to level 1: no pan
   197→      setZoomLevel(1);
   198→      setPan({ x: 0, y: 0 });
   199→    } else {
   200→      // Adjust pan to keep the same center point visible when zoom changes
   201→      // Transform is: scale(zoom) translate(pan.x / zoom, pan.y / zoom) with origin center
   202→      // Effective offset = pan / zoom, so to maintain same offset: newPan / newZoom = oldPan / oldZoom
   203→      // Therefore: newPan = oldPan * newZoom / oldZoom
   204→      const newZoom = level === 2 ? 4 : 8;
   205→      const zoomRatio = newZoom / zoom;
   206→      setPan(prev => ({
   207→        x: prev.x * zoomRatio,
   208→        y: prev.y * zoomRatio,
   209→      }));
   210→      setZoomLevel(level);
   211→    }
   212→  }, [zoom]);
   213→
   214→  // Arrow key movement for fine landmark adjustment
   215→  const moveLandmark = useCallback((direction: 'up' | 'down' | 'left' | 'right') => {
   216→    const step = 0.002; // Small step for fine adjustment
   217→    setLandmarks((prev) =>
   218→      prev.map((lm) => {
   219→        if (lm.id !== currentLandmarkId) return lm;
   220→        let newX = lm.x;
   221→        let newY = lm.y;
   222→        switch (direction) {
   223→          case 'up': newY = Math.max(0, lm.y - step); break;
   224→          case 'down': newY = Math.min(1, lm.y + step); break;
   225→          case 'left': newX = Math.max(0, lm.x - step); break;
   226→          case 'right': newX = Math.min(1, lm.x + step); break;
   227→        }
   228→        return { ...lm, x: newX, y: newY };
   229→      })
   230→    );
   231→  }, [currentLandmarkId]);
   232→
   233→  const handleNext = useCallback(() => {
   234→    if (currentStepIndex < totalSteps - 1) {
   235→      setCurrentStepIndex((prev) => prev + 1);
   236→      handleResetZoom();
   237→    } else {
   238→      onComplete?.(landmarks);
   239→    }
   240→  }, [currentStepIndex, totalSteps, landmarks, onComplete]);
   241→
   242→  const handlePrevious = useCallback(() => {
   243→    if (currentStepIndex > 0) {
   244→      setCurrentStepIndex((prev) => prev - 1);
   245→      handleResetZoom();
   246→    }
   247→  }, [currentStepIndex]);
   248→
   249→  const handleSkipToEnd = useCallback(() => {
   250→    onComplete?.(landmarks);
   251→  }, [landmarks, onComplete]);
   252→
   253→  // Keyboard event handler
   254→  useEffect(() => {
   255→    const handleKeyDown = (e: KeyboardEvent) => {
   256→      // Prevent default for our shortcuts
   257→      if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight', 'Enter', 'Backspace', 'r', 'R', '1', '2', '3'].includes(e.key)) {
   258→        e.preventDefault();
   259→      }
   260→
   261→      setPressedKey(e.key);
   262→
   263→      switch (e.key) {
   264→        case 'ArrowUp':
   265→          moveLandmark('up');
   266→          break;
   267→        case 'ArrowDown':
   268→          moveLandmark('down');
   269→          break;
   270→        case 'ArrowLeft':
   271→          moveLandmark('left');
   272→          break;
   273→        case 'ArrowRight':
   274→          moveLandmark('right');
   275→          break;
   276→        case 'Enter':
   277→          handleNext();
   278→          break;
   279→        case 'Backspace':
   280→          handlePrevious();
   281→          break;
   282→        case 'r':
   283→        case 'R':
   284→          handleResetZoom();
   285→          break;
   286→        case '1':
   287→          handleSetZoomLevel(1);
   288→          break;
   289→        case '2':
   290→          handleSetZoomLevel(2);
   291→          break;
   292→        case '3':
   293→          handleSetZoomLevel(3);
   294→          break;
   295→      }
   296→    };
   297→
   298→    const handleKeyUp = () => {
   299→      setPressedKey(null);
   300→    };
   301→
   302→    window.addEventListener('keydown', handleKeyDown);
   303→    window.addEventListener('keyup', handleKeyUp);
   304→    return () => {
   305→      window.removeEventListener('keydown', handleKeyDown);
   306→      window.removeEventListener('keyup', handleKeyUp);
   307→    };
   308→  }, [moveLandmark, handleNext, handlePrevious, handleSetZoomLevel]);
   309→
   310→  const referenceImageUrl = currentLandmarkId
   311→    ? getLandmarkImagePath(currentLandmarkId, 'front', 'infographic')
   312→    : null;
   313→
   314→  const placementGuide = currentLandmarkId
   315→    ? getLandmarkPlacementGuide(currentLandmarkId, 'front')
   316→    : '';
   317→
   318→  const color = currentLandmark
   319→    ? getLandmarkColor(currentLandmark.id, FRONT_LANDMARK_CATEGORIES)
   320→    : '#00f3ff';
   321→
   322→  return (
   323→    <div className="w-full max-w-6xl mx-auto">
   324→      {/* Progress Bar */}
   325→      <div className="mb-4">
   326→        <div className="flex items-center justify-between mb-2">
   327→          <span className="text-sm text-foreground-dim">
   328→            Step {currentStepIndex + 1} of {totalSteps}
   329→          </span>
   330→          <span className="text-sm text-foreground-dim">
   331→            {Math.round(progress)}% Complete
   332→          </span>
   333→        </div>
   334→        <div className="h-2 bg-background-tertiary rounded-full overflow-hidden">
   335→          <motion.div
   336→            className="h-full bg-accent"
   337→            initial={{ width: 0 }}
   338→            animate={{ width: `${progress}%` }}
   339→            transition={{ duration: 0.3 }}
   340→          />
   341→        </div>
   342→      </div>
   343→
   344→      {/* Main Content Grid */}
   345→      <div className="grid lg:grid-cols-3 gap-6">
   346→        {/* Left Panel - Reference Image and Instructions */}
   347→        <div className="lg:col-span-1 space-y-4">
   348→          {/* Current Landmark Info Card */}
   349→          <motion.div
   350→            key={currentLandmarkId}
   351→            initial={{ opacity: 0, y: 10 }}
   352→            animate={{ opacity: 1, y: 0 }}
   353→            className="bg-background-secondary rounded-xl border border-border p-4"
   354→          >
   355→            <div className="flex items-center gap-3 mb-3">
   356→              <div
   357→                className="w-4 h-4 rounded-full border-2 border-white"
   358→                style={{ backgroundColor: color }}
   359→              />
   360→              <div>
   361→                <h3 className="text-lg font-semibold text-white">
