   427→                    />
   428→                  ) : (
   429→                    <div className="absolute inset-0 flex items-center justify-center p-4 text-center">
   430→                      <div>
   431→                        <div
   432→                          className="w-16 h-16 rounded-full mx-auto mb-3 border-4 border-dashed flex items-center justify-center"
   433→                          style={{ borderColor: color }}
   434→                        >
   435→                          <Circle className="w-6 h-6" style={{ color: color }} />
   436→                        </div>
   437→                        <p className="text-foreground-dim text-sm">Reference photo</p>
   438→                      </div>
   439→                    </div>
   440→                  )}
   441→                </>
   442→              )}
   443→
   444→              {activeTab === 'diagram' && (
   445→                <div className="absolute inset-0 flex items-center justify-center p-4 text-center">
   446→                  <div>
   447→                    <div
   448→                      className="w-16 h-16 rounded-full mx-auto mb-3 border-4 border-dashed flex items-center justify-center"
   449→                      style={{ borderColor: color }}
