   451→                      <Circle className="w-6 h-6" style={{ color: color }} />
   452→                    </div>
   453→                    <p className="text-foreground-dim text-sm">Anatomical diagram</p>
   454→                  </div>
   455→                </div>
   456→              )}
   457→
   458→              {activeTab === 'howToFind' && (
   459→                <div className="absolute inset-0 flex items-center justify-center p-6">
   460→                  <div className="text-center">
   461→                    <Info className="w-8 h-8 mx-auto mb-3" style={{ color: color }} />
   462→                    <p className="text-white text-sm">{placementGuide}</p>
   463→                  </div>
   464→                </div>
   465→              )}
   466→            </div>
   467→          </motion.div>
   468→
   469→          {/* Placement Guide Text */}
   470→          <div className="bg-accent/10 rounded-xl border border-accent/30 p-4">
   471→            <p className="text-sm text-accent">{placementGuide}</p>
   472→          </div>
   473→
   474→          {/* Quick Actions */}
   475→          <div className="flex gap-2">
   476→            <button
   477→              onClick={() => setShowAllPoints(!showAllPoints)}
   478→              className={`flex-1 py-2 px-3 rounded-lg text-sm font-medium transition-colors ${
   479→                showAllPoints
   480→                  ? 'bg-accent text-black'
   481→                  : 'bg-white/10 text-foreground-dim hover:bg-white/20'
   482→              }`}
   483→            >
   484→              {showAllPoints ? 'Hide Other Points' : 'Show All Points'}
   485→            </button>
   486→            <button
   487→              onClick={handleSkipToEnd}
   488→              className="py-2 px-4 rounded-lg text-sm font-medium bg-white/10 text-foreground-dim hover:bg-white/20 transition-colors"
   489→            >
   490→              Skip to End
   491→            </button>
   492→          </div>
   493→
   494→          {/* Keyboard Shortcuts Panel */}
   495→          <div className="hidden lg:block bg-background-secondary rounded-xl border border-border p-4">
   496→            <p className="text-xs text-foreground-dim font-medium uppercase tracking-wide mb-3">
   497→              Keyboard Shortcuts
   498→            </p>
   499→
   500→            {/* Arrow Keys Visual */}
   501→            <div className="flex justify-center mb-4">
   502→              <div className="flex flex-col gap-1 items-center">
   503→                <KeyboardKey
   504→                  isPressed={pressedKey === 'ArrowUp'}
   505→                  icon={<ChevronUp className="w-4 h-4" />}
   506→                />
   507→                <div className="flex gap-1">
   508→                  <KeyboardKey
   509→                    isPressed={pressedKey === 'ArrowLeft'}
   510→                    icon={<ChevronLeft className="w-4 h-4" />}
   511→                  />
   512→                  <KeyboardKey
   513→                    isPressed={pressedKey === 'ArrowDown'}
   514→                    icon={<ChevronDown className="w-4 h-4" />}
   515→                  />
   516→                  <KeyboardKey
   517→                    isPressed={pressedKey === 'ArrowRight'}
   518→                    icon={<ChevronRight className="w-4 h-4" />}
   519→                  />
   520→                </div>
   521→              </div>
   522→            </div>
   523→
   524→            {/* Shortcut List */}
   525→            <div className="space-y-2">
   526→              <div className="flex items-center gap-3">
   527→                <span className={`px-2 py-1 text-xs font-mono rounded border transition-colors ${
   528→                  pressedKey === 'Enter'
   529→                    ? 'bg-accent border-accent text-black'
   530→                    : 'bg-white/10 border-border text-foreground-dim'
   531→                }`}>
   532→                  Enter
   533→                </span>
   534→                <span className="text-xs text-foreground-dim">Next / Confirm</span>
   535→              </div>
   536→              <div className="flex items-center gap-3">
   537→                <span className={`px-2 py-1 text-xs font-mono rounded border transition-colors ${
   538→                  pressedKey === 'Backspace'
   539→                    ? 'bg-accent border-accent text-black'
   540→                    : 'bg-white/10 border-border text-foreground-dim'
   541→                }`}>
   542→                  Backspace
   543→                </span>
   544→                <span className="text-xs text-foreground-dim">Previous</span>
   545→              </div>
   546→              <div className="flex items-center gap-3">
   547→                <span className={`px-2 py-1 text-xs font-mono rounded border transition-colors ${
   548→                  pressedKey === 'r' || pressedKey === 'R'
   549→                    ? 'bg-accent border-accent text-black'
   550→                    : 'bg-white/10 border-border text-foreground-dim'
   551→                }`}>
   552→                  R
   553→                </span>
