   625→
   626→          {/* Image Canvas */}
   627→          <motion.div
   628→            layout
   629→            className={`relative rounded-2xl overflow-hidden bg-black border border-border ${
   630→              isFullscreen ? 'fixed inset-4 z-50' : 'aspect-[3/4]'
