   651→              }}
   652→            >
   653→              {/* Transformed Image Container */}
   654→              <div
   655→                className="absolute inset-0 transition-transform duration-100"
   656→                style={{
   657→                  transform: `scale(${zoom}) translate(${pan.x / zoom}px, ${pan.y / zoom}px)`,
   658→                  transformOrigin: 'center center',
   659→                }}
   660→              >
   661→                {/* User's Image */}
   662→                <Image
   663→                  src={imageUrl}
   664→                  alt="Your photo"
   665→                  fill
   666→                  className="object-contain pointer-events-none select-none"
   667→                  unoptimized
   668→                  draggable={false}
   669→                />
   670→
   671→                {/* All Landmarks (dimmed) */}
   672→                {showAllPoints &&
   673→                  landmarks.map((landmark) => {
   674→                    if (landmark.id === currentLandmarkId) return null;
   675→                    const lmColor = getLandmarkColor(landmark.id, FRONT_LANDMARK_CATEGORIES);
   676→                    return (
   677→                      <div
   678→                        key={landmark.id}
   679→                        className="absolute w-3 h-3 -translate-x-1/2 -translate-y-1/2 pointer-events-none"
   680→                        style={{
