   691→                    );
   692→                  })}
   693→
   694→                {/* Current Landmark (highlighted) */}
   695→                {currentLandmark && (
   696→                  <motion.div
   697→                    key={currentLandmarkId}
   698→                    initial={{ scale: 0 }}
   699→                    animate={{ scale: 1 }}
   700→                    className="absolute w-6 h-6 -translate-x-1/2 -translate-y-1/2 z-20"
   701→                    style={{
   702→                      left: `${currentLandmark.x * 100}%`,
   703→                      top: `${currentLandmark.y * 100}%`,
   704→                    }}
   705→                  >
   706→                    {/* Pulsing ring */}
   707→                    <motion.div
   708→                      className="absolute inset-0 rounded-full"
   709→                      style={{ borderColor: color }}
   710→                      animate={{
   711→                        scale: [1, 1.5, 1],
   712→                        opacity: [0.8, 0, 0.8],
   713→                      }}
   714→                      transition={{
   715→                        duration: 2,
   716→                        repeat: Infinity,
   717→                        ease: 'easeInOut',
   718→                      }}
   719→                    >
   720→                      <div
   721→                        className="w-full h-full rounded-full border-2"
   722→                        style={{ borderColor: color }}
   723→                      />
   724→                    </motion.div>
   725→
   726→                    {/* Main dot */}
   727→                    <div
   728→                      className="absolute inset-1 rounded-full border-2 border-white shadow-lg"
   729→                      style={{
   730→                        backgroundColor: color,
   731→                        boxShadow: `0 0 20px ${color}`,
   732→                      }}
   733→                    />
   734→
   735→                    {/* Crosshair */}
   736→                    <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
   737→                      <div
   738→                        className="absolute w-8 h-[2px] -translate-x-1/2 left-1/2"
   739→                        style={{ backgroundColor: `${color}80` }}
   740→                      />
   741→                      <div
   742→                        className="absolute h-8 w-[2px] -translate-y-1/2 top-1/2"
   743→                        style={{ backgroundColor: `${color}80` }}
   744→                      />
   745→                    </div>
   746→                  </motion.div>
   747→                )}
   748→              </div>
   749→
   750→              {/* Zoom indicator in corner */}
   751→              {zoom > 1 && (
   752→                <div className="absolute top-3 left-3 px-2 py-1 rounded bg-black/70 text-white text-xs">
   753→                  {Math.round(zoom * 100)}% - Drag to pan
   754→                </div>
   755→              )}
   756→            </div>
   757→
   758→            {/* Instructions overlay */}
   759→            <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 to-transparent p-4">
   760→              <p className="text-center text-white text-sm">
   761→                Click on the image to place the{' '}
   762→                <span style={{ color: color }}>{currentLandmark?.label}</span> landmark
   763→              </p>
   764→            </div>
   765→          </motion.div>
   766→
   767→          {/* Navigation Buttons */}
   768→          <div className="flex items-center justify-between mt-4">
   769→            <button
   770→              onClick={handlePrevious}
   771→              disabled={currentStepIndex === 0}
   772→              className="flex items-center gap-2 px-4 py-3 rounded-lg bg-white/10 text-white hover:bg-white/20 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
   773→            >
   774→              <ChevronLeft className="w-5 h-5" />
   775→              Previous
   776→            </button>
   777→
   778→            <div className="flex items-center gap-1">
   779→              {/* Step indicators - show subset around current */}
   780→              {Array.from({ length: Math.min(7, totalSteps) }).map((_, i) => {
   781→                let stepIndex: number;
   782→                if (totalSteps <= 7) {
   783→                  stepIndex = i;
   784→                } else if (currentStepIndex < 3) {
   785→                  stepIndex = i;
   786→                } else if (currentStepIndex > totalSteps - 4) {
   787→                  stepIndex = totalSteps - 7 + i;
   788→                } else {
   789→                  stepIndex = currentStepIndex - 3 + i;
   790→                }
   791→
   792→                const isCompleted = stepIndex < currentStepIndex;
   793→                const isCurrent = stepIndex === currentStepIndex;
   794→
   795→                return (
   796→                  <button
   797→                    key={stepIndex}
   798→                    onClick={() => {
   799→                      setCurrentStepIndex(stepIndex);
   800→                      handleResetZoom();
