     1â†’'use client';
     2â†’
     3â†’import { useState, useCallback } from 'react';
     4â†’import { motion, AnimatePresence } from 'framer-motion';
     5â†’import {
     6â†’  BookOpen,
     7â†’  Dumbbell,
     8â†’  Sparkles,
     9â†’  Clock,
    10â†’  ChevronRight,
    11â†’  Search,
    12â†’  Brain,
    13â†’  Wrench,
    14â†’  TrendingDown,
    15â†’  Calendar,
    16â†’  Target,
    17â†’  Heart,
    18â†’  Utensils,
    19â†’  Droplet,
    20â†’  X,
    21â†’  ArrowLeft,
    22â†’  MessageSquare,
    23â†’  ExternalLink,
    24â†’  ShoppingCart,
    25â†’  CheckCircle2,
    26â†’} from 'lucide-react';
    27â†’import Image from 'next/image';
    28â†’import Link from 'next/link';
    29â†’import { TabContent } from '../ResultsLayout';
    30â†’import {
    31â†’  GUIDE_CATEGORIES,
    32â†’  getGuidesByCategory,
    33â†’  getTotalGuideCount,
    34â†’  getTotalReadTime,
    35â†’  searchGuides,
    36â†’  getGuideProductById,
    37â†’} from '@/data/guides';
    38â†’import { Guide, GuideCategory, GuideMedia } from '@/types/guides';
    39â†’import { useRegion } from '@/contexts/RegionContext';
    40â†’import ReactMarkdown from 'react-markdown';
    41â†’import remarkGfm from 'remark-gfm';
    42â†’
    43â†’// ============================================
    44â†’// ICON MAPPING
    45â†’// ============================================
    46â†’
    47â†’const ICON_MAP: Record<string, React.ReactNode> = {
    48â†’  BookOpen: <BookOpen size={20} />,
    49â†’  Dumbbell: <Dumbbell size={20} />,
    50â†’  Sparkles: <Sparkles size={20} />,
    51â†’  Brain: <Brain size={20} />,
    52â†’  Wrench: <Wrench size={20} />,
    53â†’  TrendingDown: <TrendingDown size={20} />,
    54â†’  Calendar: <Calendar size={20} />,
    55â†’  Target: <Target size={20} />,
    56â†’  Heart: <Heart size={20} />,
    57â†’  Utensils: <Utensils size={20} />,
    58â†’  Droplet: <Droplet size={20} />,
    59â†’};
    60â†’
    61â†’function getIconComponent(iconName: string, size = 20): React.ReactNode {
    62â†’  // Check icon map first
    63â†’  const mappedIcon = ICON_MAP[iconName];
    64â†’  if (mappedIcon) return mappedIcon;
    65â†’
    66â†’  // Fallback to BookOpen
    67â†’  return <BookOpen size={size} />;
    68â†’}
    69â†’
    70â†’// ============================================
    71â†’// MEDIA RENDERER
    72â†’// ============================================
    73â†’
    74â†’interface MediaRendererProps {
    75â†’  media: GuideMedia;
    76â†’}
    77â†’
    78â†’function MediaRenderer({ media }: MediaRendererProps) {
    79â†’  const [hasError, setHasError] = useState(false);
    80â†’  const [isLoading, setIsLoading] = useState(true);
    81â†’
    82â†’  const handleError = useCallback(() => {
    83â†’    setHasError(true);
    84â†’    setIsLoading(false);
    85â†’  }, []);
    86â†’
    87â†’  const handleLoad = useCallback(() => {
    88â†’    setIsLoading(false);
    89â†’  }, []);
    90â†’
    91â†’  const placementClasses = {
    92â†’    inline: 'max-w-lg mx-auto',
    93â†’    hero: 'w-full',
    94â†’    'full-width': 'w-full',
    95â†’  };
    96â†’
    97â†’  const isGif = media.type === 'gif' || media.url.endsWith('.gif');
    98â†’
    99â†’  // Show styled placeholder if image failed to load or doesn't exist
   100â†’  if (hasError) {
   101â†’    return (
   102â†’      <figure className={`my-10 ${placementClasses[media.placement || 'inline']}`}>
   103â†’        <div className="relative rounded-2xl overflow-hidden bg-gradient-to-br from-neutral-900 to-neutral-950 border border-neutral-800">
   104â†’          <div className="aspect-video flex items-center justify-center p-8">
   105â†’            <div className="text-center max-w-sm">
   106â†’              <div className="w-16 h-16 bg-gradient-to-br from-cyan-500/20 to-blue-500/20 rounded-2xl flex items-center justify-center mx-auto mb-4 border border-cyan-500/20">
   107â†’                {isGif ? (
   108â†’                  <div className="w-6 h-6 border-2 border-cyan-400 border-t-transparent rounded-full animate-spin" />
   109â†’                ) : (
   110â†’                  <Sparkles size={28} className="text-cyan-400" />
   111â†’                )}
   112â†’              </div>
   113â†’              <p className="text-neutral-300 font-medium mb-2">{media.alt}</p>
   114â†’              {media.caption && (
   115â†’                <p className="text-neutral-500 text-sm">{media.caption}</p>
   116â†’              )}
   117â†’            </div>
   118â†’          </div>
   119â†’        </div>
   120â†’      </figure>
   121â†’    );
   122â†’  }
   123â†’
   124â†’  return (
   125â†’    <figure className={`my-10 ${placementClasses[media.placement || 'inline']}`}>
   126â†’      <div className="relative rounded-2xl overflow-hidden bg-neutral-900 border border-neutral-800">
   127â†’        {/* Loading skeleton */}
   128â†’        {isLoading && (
   129â†’          <div className="absolute inset-0 bg-neutral-900 animate-pulse flex items-center justify-center">
   130â†’            <div className="w-8 h-8 border-2 border-cyan-500 border-t-transparent rounded-full animate-spin" />
   131â†’          </div>
   132â†’        )}
   133â†’
   134â†’        {isGif ? (
   135â†’          // Use img for GIFs to preserve animation
   136â†’          // eslint-disable-next-line @next/next/no-img-element
   137â†’          <img
   138â†’            src={media.url}
   139â†’            alt={media.alt}
   140â†’            className={`w-full h-auto transition-opacity duration-300 ${isLoading ? 'opacity-0' : 'opacity-100'}`}
   141â†’            loading="lazy"
   142â†’            onError={handleError}
   143â†’            onLoad={handleLoad}
   144â†’          />
   145â†’        ) : (
   146â†’          <Image
   147â†’            src={media.url}
   148â†’            alt={media.alt}
   149â†’            width={media.width || 800}
   150â†’            height={media.height || 450}
   151â†’            className={`w-full h-auto transition-opacity duration-300 ${isLoading ? 'opacity-0' : 'opacity-100'}`}
   152â†’            onError={handleError}
