                        <th className="px-4 py-3 text-sm font-semibold text-cyan-400 uppercase tracking-wider">
                          {children}
                        </th>
                      ),
                      td: ({ children }) => (
                        <td className="px-4 py-3 text-neutral-300">
                          {children}
