/**
 * Guides Data Index
 * Central export for all guide-related data
 */

import { Guide, GuideCategory } from '@/types/guides';

// Product Registry
export {
  GUIDE_PRODUCTS,
  getGuideProductById,
  getGuideProductsByCategory,
  getBaseStackProducts,
  getGuideProductsByPriority,
  getProductCategories,
  getGuideProductsByIds,
  searchGuideProducts,
  getTotalProductCount,
  getCategoryDisplayInfo,
} from './products-registry';

// Individual Guides
export { mindsetGuide } from './mindset';
export { maintenanceGuide } from './maintenance';
export { bodyFatGuide } from './body-fat';
export { vTaperGuide } from './v-taper';
export { trainingGuide } from './training';
export { coreNeckGuide } from './core-neck';
export { cardioGuide } from './cardio';
export { dietGuide } from './diet';
export { skincareGuide } from './skincare';

// ============================================
// GUIDE REGISTRY
// ============================================

import { mindsetGuide } from './mindset';
import { maintenanceGuide } from './maintenance';
import { bodyFatGuide } from './body-fat';
import { vTaperGuide } from './v-taper';
import { trainingGuide } from './training';
import { coreNeckGuide } from './core-neck';
import { cardioGuide } from './cardio';
import { dietGuide } from './diet';
import { skincareGuide } from './skincare';

/**
 * All guides in display order
 */
export const ALL_GUIDES: Guide[] = [
  mindsetGuide,
  maintenanceGuide,
  bodyFatGuide,
  vTaperGuide,
  trainingGuide,
  coreNeckGuide,
  cardioGuide,
  dietGuide,
  skincareGuide,
].sort((a, b) => a.order - b.order);

/**
 * Guide lookup by ID
 */
export const GUIDES_BY_ID: Record<string, Guide> = ALL_GUIDES.reduce(
  (acc, guide) => {
    acc[guide.id] = guide;
    return acc;
  },
  {} as Record<string, Guide>
);

/**
 * Guide lookup by slug
 */
export const GUIDES_BY_SLUG: Record<string, Guide> = ALL_GUIDES.reduce(
  (acc, guide) => {
    acc[guide.slug] = guide;
    return acc;
  },
  {} as Record<string, Guide>
);

// ============================================
// GUIDE CATEGORIES
// ============================================

export const GUIDE_CATEGORIES: GuideCategory[] = [
  {
    id: 'fundamentals',
    name: 'Fundamentals',
    description: 'Start here. The basics everyone needs.',
    icon: 'BookOpen',
    color: 'blue',
    guideIds: ['mindset', 'maintenance', 'body-fat'],
  },
  {
    id: 'physique',
    name: 'Physique',
    description: 'Build the body that commands respect.',
    icon: 'Dumbbell',
    color: 'purple',
    guideIds: ['v-taper', 'training', 'core-neck', 'cardio'],
  },
  {
    id: 'appearance',
    name: 'Appearance',
    description: 'Dial in the details that matter.',
    icon: 'Sparkles',
    color: 'amber',
    guideIds: ['diet', 'skincare'],
  },
];

// ============================================
// HELPER FUNCTIONS
// ============================================

/**
 * Get guide by ID
 */
export function getGuideById(id: string): Guide | undefined {
  return GUIDES_BY_ID[id];
}

/**
 * Get guide by slug
 */
export function getGuideBySlug(slug: string): Guide | undefined {
  return GUIDES_BY_SLUG[slug];
}

/**
 * Get all guides in a category
 */
export function getGuidesByCategory(categoryId: string): Guide[] {
  const category = GUIDE_CATEGORIES.find(c => c.id === categoryId);
  if (!category) return [];
  return category.guideIds
    .map(id => GUIDES_BY_ID[id])
    .filter((g): g is Guide => g !== undefined);
}

/**
 * Get related guides for a given guide
 */
export function getRelatedGuides(guideId: string): Guide[] {
  const guide = GUIDES_BY_ID[guideId];
  if (!guide || !guide.relatedGuides) return [];
  return guide.relatedGuides
    .map(id => GUIDES_BY_ID[id])
    .filter((g): g is Guide => g !== undefined);
}

/**
 * Search guides by title or description
 */
export function searchGuides(query: string): Guide[] {
  const lowered = query.toLowerCase();
  return ALL_GUIDES.filter(
    g =>
      g.title.toLowerCase().includes(lowered) ||
      g.description.toLowerCase().includes(lowered) ||
      (g.subtitle && g.subtitle.toLowerCase().includes(lowered)) ||
      (g.tags && g.tags.some(tag => tag.toLowerCase().includes(lowered)))
  );
}

/**
 * Get total guide count
 */
export function getTotalGuideCount(): number {
  return ALL_GUIDES.length;
}

/**
 * Get total estimated reading time
 */
export function getTotalReadTime(): number {
  return ALL_GUIDES.reduce((sum, guide) => sum + guide.estimatedReadTime, 0);
}

/**
 * Get all unique product IDs across all guides
 */
export function getAllGuideProductIds(): string[] {
  const ids = new Set<string>();
  ALL_GUIDES.forEach(guide => {
    guide.productIds?.forEach(id => ids.add(id));
    guide.sections.forEach(section => {
      section.products?.forEach(id => ids.add(id));
    });
  });
  return Array.from(ids);
}

'use client';

import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Image from 'next/image';
import {
  LayoutDashboard,
  User,
  ScanFace,
  Sparkles,
  Settings,
  HelpCircle,
  Menu,
  X,
  ChevronRight,
  Trophy,
  Users,
  Gauge,
  Shapes,
  BookOpen,
  Gift,
} from 'lucide-react';
import { useResults } from '@/contexts/ResultsContext';
import { useLeaderboardOptional } from '@/contexts/LeaderboardContext';
import { ResultsTab } from '@/types/results';
import { ScoreCircle, RankBadge } from './shared';
import { api } from '@/lib/api';

// ============================================
// TAB NAVIGATION
// ============================================

interface TabConfig {
  id: ResultsTab;
  label: string;
  icon: React.ReactNode;
}

const TABS: TabConfig[] = [
  { id: 'overview', label: 'Overview', icon: <LayoutDashboard size={18} /> },
  { id: 'front-ratios', label: 'Front Ratios', icon: <User size={18} /> },
  { id: 'side-ratios', label: 'Side Ratios', icon: <ScanFace size={18} /> },
  { id: 'leaderboard', label: 'Leaderboard', icon: <Trophy size={18} /> },
  { id: 'psl', label: 'PSL Rating', icon: <Gauge size={18} /> },
  { id: 'archetype', label: 'Archetype', icon: <Shapes size={18} /> },
  { id: 'plan', label: 'Your Plan', icon: <Sparkles size={18} /> },
  { id: 'guides', label: 'Guides', icon: <BookOpen size={18} /> },
  { id: 'community', label: 'Community', icon: <Users size={18} /> },
  { id: 'referrals', label: 'Referrals', icon: <Gift size={18} /> },
  { id: 'options', label: 'Options', icon: <Settings size={18} /> },
  { id: 'support', label: 'Support', icon: <HelpCircle size={18} /> },
];

// ============================================
// SIDEBAR
// ============================================

interface SidebarProps {
  onClose?: () => void;
}

function Sidebar({ onClose }: SidebarProps) {
  const { activeTab, setActiveTab, overallScore, frontPhoto, pslRating } = useResults();

  return (
    <div className="flex flex-col h-full bg-neutral-950 border-r border-neutral-800">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b border-neutral-800">
        <div className="flex items-center gap-2">
          <div className="w-8 h-8 rounded-lg bg-gradient-to-br from-cyan-500 to-blue-600 flex items-center justify-center">
            <ScanFace size={18} className="text-white" />
          </div>
          <span className="font-semibold text-white">LOOKSMAXX</span>
        </div>
        {onClose && (
          <button
            onClick={onClose}
            className="p-2 hover:bg-neutral-800 rounded-lg transition-colors md:hidden"
          >
            <X size={20} className="text-neutral-400" />
          </button>
        )}
      </div>

      {/* Profile Photo & Score */}
      <div className="p-4 border-b border-neutral-800">
        <div className="relative aspect-square rounded-xl overflow-hidden bg-neutral-900 mb-4">
          {frontPhoto ? (
            <Image
              src={frontPhoto}
              alt="Your face"
              fill
              className="object-cover"
              unoptimized
            />
          ) : (
            <div className="w-full h-full flex items-center justify-center">
              <User size={48} className="text-neutral-700" />
            </div>
          )}

      {/* Footer */}
      <div className="p-4 border-t border-neutral-800">
        <button
          onClick={() => window.location.href = '/gender'}
          className="w-full py-2.5 px-4 bg-gradient-to-r from-cyan-500 to-blue-600 text-white font-medium rounded-lg hover:from-cyan-400 hover:to-blue-500 transition-all"
        >
          New Analysis
        </button>
      </div>
    </div>
  );
}

// ============================================
// FLOATING RANK CARD (Desktop)
// ============================================

function FloatingRankCard() {
  const { setActiveTab } = useResults();
  const leaderboard = useLeaderboardOptional();
  const userRank = leaderboard?.userRank;
  const isAuthenticated = !!api.getToken();

  // Auto-fetch user's rank on mount if authenticated
  useEffect(() => {
    if (isAuthenticated && !userRank && leaderboard?.fetchMyRank) {
      leaderboard.fetchMyRank();
    }
  }, [isAuthenticated, userRank, leaderboard]);

  return (
    <motion.button
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      onClick={() => setActiveTab('leaderboard')}
      className="flex items-center gap-3 px-4 py-3 bg-neutral-900/95 backdrop-blur-sm border border-neutral-800 rounded-xl shadow-lg hover:border-yellow-500/30 transition-all group"
    >
      {userRank ? (
        <>
          <div className="flex items-center justify-center w-10 h-10 rounded-lg bg-gradient-to-br from-yellow-500/20 to-amber-500/20 border border-yellow-500/30">
            <Trophy size={20} className="text-yellow-400" />
          </div>
          <div className="text-left">
            <p className="text-xs text-neutral-500 uppercase tracking-wide">Your Rank</p>
            <div className="flex items-baseline gap-2">
              <span className="text-xl font-bold text-yellow-400">#{userRank.genderRank}</span>
              <span className="text-xs text-neutral-500">Top {userRank.percentile.toFixed(1)}%</span>
            </div>
          </div>
          <ChevronRight size={16} className="text-neutral-600 group-hover:text-yellow-400 transition-colors ml-1" />
        </>
      ) : (
        <>
          <div className="flex items-center justify-center w-10 h-10 rounded-lg bg-neutral-800 border border-neutral-700">
            <Trophy size={20} className="text-neutral-500" />
          </div>
          <div className="text-left">
            <p className="text-xs text-neutral-500 uppercase tracking-wide">Your Rank</p>
            <span className="text-sm text-neutral-400">
              {isAuthenticated ? 'Not ranked yet' : 'Sign in to rank'}
            </span>
          </div>
          <ChevronRight size={16} className="text-neutral-600 group-hover:text-neutral-400 transition-colors ml-1" />
        </>
      )}
    </motion.button>
  );
}

// ============================================
// MOBILE HEADER
// ============================================

interface MobileHeaderProps {
  onMenuClick: () => void;
}

function MobileHeader({ onMenuClick }: MobileHeaderProps) {
  const { activeTab, overallScore } = useResults();
  const currentTab = TABS.find((t) => t.id === activeTab);

  return (
    <header className="flex items-center justify-between px-4 py-3 bg-neutral-950 border-b border-neutral-800">
      <button
        onClick={onMenuClick}
        className="p-2 hover:bg-neutral-800 rounded-lg transition-colors"
      >
        <Menu size={20} className="text-neutral-400" />
      </button>
      <div className="flex items-center gap-2">
        {currentTab?.icon}
        <span className="font-medium text-white">{currentTab?.label}</span>
      </div>
      <div className="flex items-center gap-2">
        <RankBadge size="sm" alwaysShow />
        <div className="w-6 h-6 rounded-full bg-cyan-500/20 flex items-center justify-center">
          <span className="text-xs text-cyan-400 font-bold">{typeof overallScore === 'number' ? Math.round(overallScore) : overallScore}</span>
        </div>
      </div>
    </header>

