    26→  PostAuthor,
    27→} from '@/types/forum';
    28→
    29→const API_URL = process.env.NEXT_PUBLIC_API_URL || 'http://localhost:8000';
    30→
    31→// API Response types (snake_case from backend)
    32→interface LeaderboardApiResponse {
    33→  user_id: string;
    34→  score: number;
    35→  global_rank: number;
    36→  gender_rank: number;
    37→  percentile: number;
    38→  total_users: number;
    39→  gender_total: number;
    40→  anonymous_name: string;
    41→  updated_at: string;
    42→}
    43→
    44→interface LeaderboardEntryResponse {
    45→  user_id: string;
    46→  rank: number;
    47→  score: number;
    48→  anonymous_name: string;
    49→  gender: 'male' | 'female';
    50→  face_photo_url: string | null;
    51→  is_current_user: boolean;
    52→}
    53→
    54→interface LeaderboardListResponse {
    55→  entries: LeaderboardEntryResponse[];
    56→  total_count: number;
    57→  user_rank: LeaderboardApiResponse | null;
    58→}
    59→
    60→interface UserProfileResponse {
    61→  user_id: string;
    62→  rank: number;
    63→  score: number;
    64→  anonymous_name: string;
    65→  gender: 'male' | 'female';
    66→  face_photo_url: string | null;
    67→  top_strengths: string[];
    68→  top_improvements: string[];
    69→}
    70→
    71→// Transform functions (snake_case -> camelCase)
    72→function transformUserRank(data: LeaderboardApiResponse): UserRank {
    73→  return {
    74→    userId: data.user_id,
    75→    score: data.score,
    76→    globalRank: data.global_rank,
    77→    genderRank: data.gender_rank,
    78→    percentile: data.percentile,
    79→    totalUsers: data.total_users,
    80→    genderTotal: data.gender_total,
    81→    anonymousName: data.anonymous_name,
