          />

          {/* Recommendations List */}
          {hasRecommendations ? (
            <motion.div className="space-y-4" layout>
              <AnimatePresence mode="popLayout">
                {filteredRecommendations.map((rec, index) => (
                  <motion.div
                    key={rec.ref_id}
