     1→'use client';
     2→
     3→import { useState, useMemo, useCallback } from 'react';
     4→import { motion, AnimatePresence } from 'framer-motion';
     5→import {
     6→  Sparkles,
     7→  TrendingUp,
     8→  Target,
     9→  Zap,
    10→  ChevronRight,
    11→  Lock,
    12→  CheckCircle,
    13→  AlertCircle,
    14→  CheckCircle2,
    15→  Scale,
    16→  Dumbbell,
    17→  Salad,
    18→  ArrowDown,
    19→  ArrowUp,
    20→  Minus,
    21→  Package,
    22→  ShoppingCart,
    23→} from 'lucide-react';
    24→import { useResults } from '@/contexts/ResultsContext';
    25→import { useAuth } from '@/contexts/AuthContext';
    26→import { usePricing } from '@/contexts/PricingContext';
    27→import { usePhysiqueOptional } from '@/contexts/PhysiqueContext';
    28→import { TabContent } from '../ResultsLayout';
    29→import { EnhancedRecommendationCard } from '../cards/EnhancedRecommendationCard';
    30→import { ScoreCircle, PhaseBadge } from '../shared';
    31→import { BeforeAfterPreview } from '../visualization/BeforeAfterPreview';
    32→import { TreatmentTimeline } from '../visualization/TreatmentTimeline';
    33→import { RecommendationPhase, ProductRecommendation } from '@/types/results';
    34→import { DailyStackCard } from '../cards/DailyStackCard';
    35→import { ProductCard } from '../cards/ProductCard';
    36→import { WeakPointCard } from '../cards/WeakPointCard';
    37→import { ProgressComparisonCard } from '../cards/ProgressComparisonCard';
    38→import { MedicalPrescriptionCard } from '../cards/MedicalPrescriptionCard';
    39→import { generateDailyStack } from '@/lib/daily-stack';
    40→import { getProductRecommendations } from '@/lib/product-recommendations';
    41→import { SUPPLEMENTS } from '@/lib/recommendations/supplements';
    42→
    43→// ============================================
    44→// POTENTIAL SCORE CARD
    45→// ============================================
    46→
    47→function PotentialScoreCard() {
    48→  const { overallScore, recommendations } = useResults();
    49→  const numericScore = typeof overallScore === 'number' ? overallScore : 0;
    50→
    51→  // Calculate potential improvement
    52→  const potentialImprovement = useMemo(() => {
    53→    if (recommendations.length === 0) return 0;
    54→    const totalImpact = recommendations.slice(0, 5).reduce((sum, r) => sum + r.impact, 0);
    55→    return Math.min(totalImpact * 1.5, 10 - numericScore);
    56→  }, [recommendations, numericScore]);
    57→
    58→  const potentialScore = Math.min(10, numericScore + potentialImprovement);
    59→
    60→  return (
    61→    <motion.div
    62→      className="bg-gradient-to-br from-neutral-900 to-neutral-950 border border-neutral-800 rounded-2xl p-6"
    63→      initial={{ opacity: 0, y: 20 }}
    64→      animate={{ opacity: 1, y: 0 }}
    65→    >
    66→      <div className="flex items-center gap-2 mb-4">
    67→        <Sparkles size={20} className="text-cyan-400" />
    68→        <h3 className="font-semibold text-white">Your Potential</h3>
    69→      </div>
    70→
    71→      <div className="flex items-center justify-center gap-8 mb-6">
    72→        {/* Current */}
    73→        <div className="text-center">
    74→          <p className="text-xs text-neutral-500 mb-2">Current</p>
    75→          <ScoreCircle score={overallScore} size="lg" animate={false} />
    76→        </div>
    77→
    78→        {/* Arrow */}
    79→        <div className="flex flex-col items-center gap-1">
    80→          <ChevronRight size={24} className="text-cyan-400" />
    81→          <span className="text-xs text-green-400">+{potentialImprovement.toFixed(1)}</span>
