   229→function YourPhaseCard({ bodyFatPercent, gender }: YourPhaseCardProps) {
   230→  // Determine phase based on body fat and gender
   231→  const getPhase = (): { phase: BodyPhase; message: string; color: string; icon: React.ReactNode } => {
   232→    if (!bodyFatPercent) {
   233→      return {
