   240→
   241→    // Male thresholds: <12% = bulk, 12-18% = maintain, >18% = cut
   242→    // Female thresholds: <18% = bulk, 18-25% = maintain, >25% = cut
   243→    const bulkThreshold = gender === 'male' ? 12 : 18;
   244→    const cutThreshold = gender === 'male' ? 18 : 25;
   245→
   246→    if (bodyFatPercent < bulkThreshold) {
   247→      return {
   248→        phase: 'bulk',
   249→        message: `At ${bodyFatPercent.toFixed(1)}% body fat, you're lean enough to build muscle. Focus on a slight caloric surplus with progressive overload.`,
   250→        color: 'from-green-600 to-emerald-700',
