   286→        </div>
   287→        <div className="flex-1">
   288→          <div className="flex items-center gap-2 mb-1">
   289→            <h3 className="font-semibold text-white text-lg">Your Phase</h3>
   290→            <div className="px-2 py-0.5 bg-white/20 rounded-full text-xs font-medium text-white">
   291→              {phaseLabels[phase]}
   292→            </div>
   293→          </div>
