'use client';

import { useState, useRef, useCallback, useEffect } from 'react';
import Image from 'next/image';
import { motion } from 'framer-motion';
import {
  ChevronLeft,
  ChevronRight,
  ChevronUp,
  ChevronDown,
  Check,
  Info,
  Eye,
  EyeOff,
  Scan,
  Loader2,
  Crosshair,
} from 'lucide-react';
import {
  LandmarkPoint,
  FRONT_PROFILE_LANDMARKS,
  SIDE_PROFILE_LANDMARKS,
  getLandmarkColor,
  FRONT_LANDMARK_CATEGORIES,
  SIDE_LANDMARK_CATEGORIES,
} from '@/lib/landmarks';
import { getLandmarkImagePath, getLandmarkPlacementGuide } from '@/lib/landmarkImages';
import { detectFromImageUrl } from '@/lib/mediapipeDetection';

interface LandmarkAnalysisToolProps {
  imageUrl: string;
  mode: 'front' | 'side';
  initialLandmarks?: LandmarkPoint[];
  onLandmarksChange?: (landmarks: LandmarkPoint[]) => void;
  onComplete?: (landmarks: LandmarkPoint[]) => void;
  onBack?: () => void;
}

function getOrderedLandmarks(mode: 'front' | 'side'): string[] {
  const landmarks = mode === 'front' ? FRONT_PROFILE_LANDMARKS : SIDE_PROFILE_LANDMARKS;
  return landmarks.map(lm => lm.id);
}

export function LandmarkAnalysisTool({
  imageUrl,
  mode,
  initialLandmarks,
  onLandmarksChange,
  onComplete,
  onBack,
}: LandmarkAnalysisToolProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const [imageDimensions, setImageDimensions] = useState<{ width: number; height: number } | null>(null);
  const defaultLandmarks = mode === 'front' ? FRONT_PROFILE_LANDMARKS : SIDE_PROFILE_LANDMARKS;
  const categories = mode === 'front' ? FRONT_LANDMARK_CATEGORIES : SIDE_LANDMARK_CATEGORIES;
  const orderedIds = getOrderedLandmarks(mode);

  const [landmarks, setLandmarks] = useState<LandmarkPoint[]>(
    initialLandmarks || defaultLandmarks
  );
  const [currentStepIndex, setCurrentStepIndex] = useState(0);
  const [zoomLevel, setZoomLevel] = useState(1);
  const [pan, setPan] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [showAllPoints, setShowAllPoints] = useState(false);
  const [pressedKeys, setPressedKeys] = useState<Set<string>>(new Set());
  const [activeTab, setActiveTab] = useState<'info' | 'reference'>('info');
  const [isDetecting, setIsDetecting] = useState(true); // Start detecting immediately
  const [hasAutoDetected, setHasAutoDetected] = useState(false);
  const [autoZoomEnabled, setAutoZoomEnabled] = useState(true);

  const currentLandmarkId = orderedIds[currentStepIndex];
  const currentLandmark = landmarks.find((lm) => lm.id === currentLandmarkId);
  const totalSteps = orderedIds.length;
  const progress = ((currentStepIndex + 1) / totalSteps) * 100;

  const color = currentLandmark
    ? getLandmarkColor(currentLandmark.id, categories)
    : '#00f3ff';

  // Load image dimensions on mount
  useEffect(() => {
    const img = new window.Image();
    img.onload = () => {
      setImageDimensions({ width: img.naturalWidth, height: img.naturalHeight });
    };
    img.src = imageUrl;
  }, [imageUrl]);

  // Calculate the rendered image bounds within the container (accounting for object-contain)
  const getImageBounds = useCallback(() => {
    if (!containerRef.current || !imageDimensions) {
      return { offsetX: 0, offsetY: 0, renderedWidth: 1, renderedHeight: 1, containerWidth: 1, containerHeight: 1 };
    }

    const container = containerRef.current;
    const containerWidth = container.clientWidth;
    const containerHeight = container.clientHeight;
    const imageAspect = imageDimensions.width / imageDimensions.height;
    const containerAspect = containerWidth / containerHeight;

    let renderedWidth: number;
    let renderedHeight: number;
    let offsetX = 0;
    let offsetY = 0;

    if (imageAspect > containerAspect) {
      // Image is wider - letterboxing top/bottom
      renderedWidth = containerWidth;
      renderedHeight = containerWidth / imageAspect;
      offsetY = (containerHeight - renderedHeight) / 2;
    } else {
      // Image is taller - letterboxing left/right
      renderedHeight = containerHeight;
      renderedWidth = containerHeight * imageAspect;
      offsetX = (containerWidth - renderedWidth) / 2;
    }

    return { offsetX, offsetY, renderedWidth, renderedHeight, containerWidth, containerHeight };
  }, [imageDimensions]);

  // Convert normalized image coordinates (0-1) to container pixel position
  const getLandmarkPosition = useCallback((x: number, y: number) => {
    const bounds = getImageBounds();
    const pixelX = bounds.offsetX + x * bounds.renderedWidth;
    const pixelY = bounds.offsetY + y * bounds.renderedHeight;
    // Convert to percentage of container
    return {
      left: `${(pixelX / bounds.containerWidth) * 100}%`,
      top: `${(pixelY / bounds.containerHeight) * 100}%`,
    };
  }, [getImageBounds]);

  useEffect(() => {
    onLandmarksChange?.(landmarks);
  }, [landmarks, onLandmarksChange]);

  const updateLandmarkPosition = useCallback(
    (x: number, y: number) => {
      setLandmarks((prev) =>
        prev.map((lm) =>
          lm.id === currentLandmarkId ? { ...lm, x, y } : lm
        )
      );
    },
    [currentLandmarkId]
  );

  const handleContainerClick = useCallback(
    (e: React.MouseEvent<HTMLDivElement>) => {
      if (!containerRef.current || isDragging) return;

      const rect = containerRef.current.getBoundingClientRect();
      const bounds = getImageBounds();

      // Get click position in container space (accounting for zoom and pan)
      const rawX = (e.clientX - rect.left - rect.width / 2 - pan.x) / zoomLevel + rect.width / 2;
      const rawY = (e.clientY - rect.top - rect.height / 2 - pan.y) / zoomLevel + rect.height / 2;

      // Convert container coordinates to normalized image coordinates (accounting for object-contain)
      const x = Math.max(0, Math.min(1, (rawX - bounds.offsetX) / bounds.renderedWidth));
      const y = Math.max(0, Math.min(1, (rawY - bounds.offsetY) / bounds.renderedHeight));

      updateLandmarkPosition(x, y);
    },
    [isDragging, pan, zoomLevel, updateLandmarkPosition, getImageBounds]
  );

  const handleMouseDown = useCallback(
    (e: React.MouseEvent) => {
      if (e.button === 0 && zoomLevel > 1) {
        setIsDragging(true);
      }
    },
    [zoomLevel]
  );

  const handleMouseMove = useCallback(
    (e: React.MouseEvent) => {
      if (isDragging && zoomLevel > 1) {
        setPan((prev) => ({
          x: prev.x + e.movementX,
          y: prev.y + e.movementY,
        }));
      }
    },
    [isDragging, zoomLevel]
  );

  const handleMouseUp = useCallback(() => {
    setIsDragging(false);
  }, []);

  const handleResetZoom = useCallback(() => {
    setZoomLevel(1);
    setPan({ x: 0, y: 0 });
  }, []);

  const moveLandmark = useCallback((direction: 'up' | 'down' | 'left' | 'right') => {
    const step = 0.002;
    setLandmarks((prev) =>
      prev.map((lm) => {
        if (lm.id !== currentLandmarkId) return lm;
        let newX = lm.x;
        let newY = lm.y;
        switch (direction) {
          case 'up': newY = Math.max(0, lm.y - step); break;
          case 'down': newY = Math.min(1, lm.y + step); break;
          case 'left': newX = Math.max(0, lm.x - step); break;
          case 'right': newX = Math.min(1, lm.x + step); break;
        }
        return { ...lm, x: newX, y: newY };
      })
    );
  }, [currentLandmarkId]);

  const handleNext = useCallback(() => {
    if (currentStepIndex < totalSteps - 1) {
      setCurrentStepIndex((prev) => prev + 1);
      handleResetZoom();
    } else {
      onComplete?.(landmarks);
    }
  }, [currentStepIndex, totalSteps, landmarks, onComplete, handleResetZoom]);

  const handlePrevious = useCallback(() => {
    if (currentStepIndex > 0) {
      setCurrentStepIndex((prev) => prev - 1);
      handleResetZoom();
    } else {
      onBack?.();
    }
  }, [currentStepIndex, handleResetZoom, onBack]);

  // Auto-zoom to the current landmark's region
  const zoomToLandmark = useCallback((landmarkId: string) => {
    if (!autoZoomEnabled || !containerRef.current) return;

    const landmark = landmarks.find(lm => lm.id === landmarkId);
    if (!landmark) return;

    // Zoom to 2x and center on the landmark
    const newZoom = 2;
    setZoomLevel(newZoom);

    // Calculate pan to center the landmark (accounting for object-contain)
    const bounds = getImageBounds();
    const centerX = bounds.containerWidth / 2;
    const centerY = bounds.containerHeight / 2;
    // Convert normalized landmark coords to actual pixel position
    const landmarkX = bounds.offsetX + landmark.x * bounds.renderedWidth;
    const landmarkY = bounds.offsetY + landmark.y * bounds.renderedHeight;

    setPan({
      x: (centerX - landmarkX) * newZoom,
      y: (centerY - landmarkY) * newZoom,
    });
  }, [autoZoomEnabled, landmarks, getImageBounds]);

  // Auto-detect landmarks using MediaPipe
  const handleAutoDetect = useCallback(async () => {
    setIsDetecting(true);
    try {
      const result = await detectFromImageUrl(imageUrl, mode);
      if (result && result.landmarks.length > 0) {
        // Update landmarks with detected positions
        setLandmarks(prev =>
          prev.map(lm => {
            const detected = result.landmarks.find(d => d.id === lm.id);
            if (detected) {
              return { ...lm, x: detected.x, y: detected.y };
            }
            return lm;
          })
        );
        setHasAutoDetected(true);
        // Zoom to first landmark after detection
        zoomToLandmark(orderedIds[0]);
      }
    } catch (error) {
      console.error('Auto-detection failed:', error);
    } finally {
      setIsDetecting(false);
    }
  }, [imageUrl, mode, orderedIds, zoomToLandmark]);

  // Auto-detect on mount
  useEffect(() => {
    handleAutoDetect();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // Run once on mount

  // Auto-zoom when step changes
  useEffect(() => {
    if (hasAutoDetected && autoZoomEnabled && currentLandmarkId) {
      zoomToLandmark(currentLandmarkId);
    }
  }, [currentStepIndex, currentLandmarkId, hasAutoDetected, autoZoomEnabled, zoomToLandmark]);

  // Keyboard handlers with proper state tracking
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight', 'Enter', 'Backspace', 'r', 'R'].includes(e.key)) {
        e.preventDefault();
      }

      setPressedKeys((prev) => new Set(prev).add(e.key));

      switch (e.key) {
        case 'ArrowUp': moveLandmark('up'); break;
        case 'ArrowDown': moveLandmark('down'); break;
        case 'ArrowLeft': moveLandmark('left'); break;
        case 'ArrowRight': moveLandmark('right'); break;
        case 'Enter': handleNext(); break;
        case 'Backspace': handlePrevious(); break;
        case 'r':
        case 'R': handleResetZoom(); break;
      }
    };

    const handleKeyUp = (e: KeyboardEvent) => {
      setPressedKeys((prev) => {
        const next = new Set(prev);
        next.delete(e.key);
        return next;
      });
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [moveLandmark, handleNext, handlePrevious, handleResetZoom]);

  const isKeyPressed = (key: string) => pressedKeys.has(key);

  return (
    <div className="fixed inset-0 bg-black">
      <div className="flex flex-col md:flex-row h-full gap-3 p-3 md:p-4">
        {/* Sidebar - Right on desktop */}
        <div className="w-full md:w-80 flex-shrink-0 flex flex-col gap-3 md:order-2 overflow-hidden md:overflow-y-auto">
          {/* Progress Card */}
          <div className="bg-neutral-900 rounded-xl border border-neutral-800 overflow-hidden flex-shrink-0">
            <div className="p-4">
              <div className="flex items-center justify-between text-xs text-neutral-400 mb-2">
                <span>Step {currentStepIndex + 1} of {totalSteps}</span>
                <span>{Math.round(progress)}%</span>
              </div>
              <div className="h-1.5 bg-neutral-800 rounded-full overflow-hidden mb-3">
                <motion.div
                  className="h-full rounded-full"
                  style={{ width: `${progress}%`, backgroundColor: '#00f3ff' }}
                  transition={{ duration: 0.3 }}
                />
              </div>
              <div className="flex items-center gap-2">
                <div className="w-6 h-6 bg-[#00f3ff] rounded-full flex items-center justify-center flex-shrink-0">
                  <Check className="w-3.5 h-3.5 text-black" />
                </div>
                <span className="text-sm font-medium text-white">
                  {currentStepIndex} placed
                </span>
              </div>
            </div>

            {/* View Tabs */}
            <div className="flex border-t border-neutral-800">
              <button
                onClick={() => setActiveTab('info')}
                className={`flex-1 py-2.5 text-xs font-medium transition-colors ${
                  activeTab === 'info'
                    ? 'text-[#00f3ff] border-b-2 border-[#00f3ff]'
                    : 'text-neutral-500 hover:text-neutral-300'
                }`}
              >
                Current Point
              </button>
              <button
                onClick={() => setActiveTab('reference')}
                className={`flex-1 py-2.5 text-xs font-medium transition-colors ${
                  activeTab === 'reference'
                    ? 'text-[#00f3ff] border-b-2 border-[#00f3ff]'
                    : 'text-neutral-500 hover:text-neutral-300'
                }`}
              >
                Reference
