@tailwind base;
@tailwind components;
@tailwind utilities;

/* Dark theme with cyan/neon accents - FaceIQ structure */
:root {
  /* Background colors - Dark theme */
  --background: #000000;
  --background-secondary: #0a0a0a;
  --background-tertiary: #111111;

  /* Text colors */
  --foreground: #ffffff;
  --foreground-muted: #e5e5e5;
  --foreground-dim: #a3a3a3;
  --foreground-light: #737373;

  /* Border colors */
  --border: #262626;
  --border-hover: #404040;
  --border-focus: #525252;

  /* Accent - Cyan/Neon */
  --accent: #00f3ff;
  --accent-dim: #00c4cc;
  --accent-glow: rgba(0, 243, 255, 0.3);

  /* Status colors */
  --success: #22c55e;
  --warning: #eab308;
  --error: #ef4444;
}

* {
  box-sizing: border-box;
  padding: 0;
  margin: 0;
}

html,
body {
  max-width: 100vw;
  overflow-x: hidden;
}

body {
  color: var(--foreground);
  background: var(--background);
  font-family: var(--font-manrope), -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Scrollbar styling - dark theme */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--background-secondary);
}

::-webkit-scrollbar-thumb {
  background: var(--border-hover);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--foreground-light);
}

/* Selection styling */
::selection {
  background: var(--accent);
  color: var(--background);
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }
}

@layer components {
  /* Primary Button - Cyan accent with glow */
  .btn-primary {
    @apply inline-flex items-center justify-center gap-2;
    @apply h-11 px-5 rounded-lg;
    @apply text-black text-sm font-medium;
    @apply transition-all;
    background-color: var(--accent);
  }

  .btn-primary:hover {
    box-shadow: 0 0 20px var(--accent-glow);
  }

  .btn-primary:disabled {
    @apply bg-neutral-800 text-neutral-500 cursor-not-allowed;
    box-shadow: none;
  }

  /* Secondary Button - Outline */
  .btn-secondary {
    @apply inline-flex items-center justify-center gap-2;
    @apply h-11 px-5 rounded-lg;
    @apply border border-neutral-700 bg-transparent text-white text-sm font-medium;
    @apply transition-all;
  }

  .btn-secondary:hover {
    @apply border-neutral-500 bg-white/5;
  }

  /* Input - Dark */
  .input {
    @apply h-11 px-3.5 text-sm;
    @apply bg-black border border-neutral-700 rounded-lg text-white;
    @apply placeholder:text-neutral-500;
    @apply focus:outline-none focus:border-neutral-500;
    @apply disabled:bg-neutral-900 disabled:text-neutral-500 disabled:cursor-not-allowed;
    @apply transition-all;
  }

  /* Card - Dark */
  .card {
    @apply bg-neutral-900/50 border border-neutral-800 rounded-xl p-6;
    @apply transition-all;
  }

  .card:hover {
    @apply border-neutral-700;
  }
}
