    "cheekbone_height": {
      ideal: [80.0, 95.0],
      mean: 87.5,
      std_dev: 7.5,
      flaws: {
        low: ["Gaunt/Hollow face"]
      }
    },

    // JAW - Slightly softer angles acceptable
    "gonial_angle": {
      ideal: [118, 128],
      mean: 123.0,
      std_dev: 5.0,
      flaws: {
        high: ["Weak/soft jaw structure"]
      }
    },
    "mandibularPlaneAngle": {
