      mean: 25.0,
      std_dev: 5.0,
      flaws: {
        low: ["Flat mandibular plane"],
        high: ["Steep mandibular plane"]
      }
    },

    // FACIAL PROPORTIONS - Wider faces more common
    "faceWidthToHeight": {
      ideal: [1.35, 1.50],
