     8→// ============================================
     9→// TYPES
    10→// ============================================
    11→
    12→interface Threshold {
    13→  operator: '<' | '>';
    14→  value: number;
    15→}
    16→
    17→interface TriggerRules {
    18→  metrics: string[];
    19→  condition: 'OR' | 'AND';
    20→  thresholds: Record<string, Threshold>;
    21→}
    22→
    23→// FaceIQ-style effectiveness rating
    24→interface Effectiveness {
    25→  level: 'high' | 'medium' | 'low';
    26→  score: number;  // 1-5
    27→  confidence: number;  // 0-1
    28→}
    29→
    30→// How a treatment impacts a specific ratio
    31→interface RatioImpact {
    32→  direction: 'increase' | 'decrease';
    33→  percentage: number;  // Expected % change
    34→}
    35→
    36→interface PlanContent {
    37→  description: string;
    38→  cost_min: number;
    39→  cost_max: number;
    40→  time_min?: string;
    41→  time_max?: string;
    42→  risks?: string;
    43→  citations?: string[];
    44→  tags?: string[];
    45→  // FaceIQ parity - new metadata fields
    46→  priority_score?: number;  // 1-5, higher = more important
    47→  effectiveness?: Effectiveness;
    48→  ratios_impacted?: Record<string, RatioImpact>;
    49→  pillars?: string[];  // e.g., ['angularity', 'harmony', 'symmetry']
    50→}
    51→
    52→interface RawPlan {
    53→  id: string;
    54→  title: string;
    55→  trigger_rules: TriggerRules;
    56→  content: PlanContent;
    57→}
    58→
    59→interface TriggerResult {
    60→  metric: string;
    61→  value: number;
    62→  threshold: number;
    63→  operator: '<' | '>';
    64→}
    65→
