   130→
   131→/**
   132→ * Find treatments that target a specific metric
   133→ */
   134→export function findTreatmentsForMetric(metricId: string): (Treatment | Surgery)[] {
   135→  return ALL_TREATMENTS.filter(t =>
   136→    t.targetMetrics.some(m =>
   137→      m.toLowerCase().includes(metricId.toLowerCase()) ||
   138→      metricId.toLowerCase().includes(m.toLowerCase())
   139→    )
   140→  );
   141→}
   142→
   143→/**
   144→ * Score treatment relevance for a metric analysis
   145→ */
   146→function scoreTreatmentRelevance(
   147→  treatment: Treatment | Surgery,
   148→  analysis: MetricAnalysis
   149→): number {
   150→  let score = 0;
   151→
   152→  // Check if treatment targets this metric
   153→  const targetsMetric = treatment.targetMetrics.some(m =>
   154→    m.toLowerCase().includes(analysis.metricId.toLowerCase())
   155→  );
   156→  if (targetsMetric) score += 30;
   157→
   158→  // Check if treatment addresses the issue
   159→  if (analysis.issue) {
   160→    const addressesIssue = treatment.targetIssues.some(i =>
   161→      i.toLowerCase().includes(analysis.issue!.toLowerCase()) ||
   162→      analysis.issue!.toLowerCase().includes(i.toLowerCase())
   163→    );
   164→    if (addressesIssue) score += 40;
   165→  }
   166→
   167→  // Factor in severity
   168→  const severityMultiplier = {
   169→    severe: 1.5,
   170→    moderate: 1.2,
   171→    mild: 1.0,
   172→    optimal: 0.3,
   173→  };
   174→  score *= severityMultiplier[analysis.severity.severity];
   175→
   176→  // Factor in treatment effectiveness
   177→  score += treatment.effectiveness * 2;
   178→
   179→  // Factor in PSL improvement potential
   180→  const avgPSLImprovement = (treatment.pslImprovement.min + treatment.pslImprovement.max) / 2;
   181→  score += avgPSLImprovement * 10;
   182→
   183→  return Math.round(score);
   184→}
   185→
   186→/**
   187→ * Generate treatment recommendations for a metric analysis
   188→ */
   189→export function getRecommendationsForMetric(
   190→  analysis: MetricAnalysis,
   191→  gender?: Gender,
   192→  maxRecommendations: number = 5
   193→): TreatmentRecommendation[] {
   194→  if (analysis.severity.severity === 'optimal') {
   195→    return []; // No treatment needed for optimal metrics
   196→  }
   197→
   198→  // Get treatment IDs from mapping
   199→  const { direction } = calculateDeviation(analysis.metricId, analysis.currentValue);
   200→  const treatmentIds = getTreatmentIdsForMetric(analysis.metricId, direction);
   201→
   202→  // Find matching treatments
   203→  let treatments: (Treatment | Surgery)[] = [];
   204→
   205→  // First, add treatments from the mapping
   206→  for (const id of treatmentIds) {
   207→    const treatment = findTreatment(id);
   208→    if (treatment && 'category' in treatment && treatment.category !== undefined) {
   209→      treatments.push(treatment as Treatment | Surgery);
   210→    }
   211→  }
   212→
   213→  // Then, find additional treatments that target this metric or issue
   214→  const additionalTreatments = [
   215→    ...findTreatmentsForMetric(analysis.metricId),
   216→    ...(analysis.issue ? findTreatmentsForIssue(analysis.issue) : []),
   217→  ];
   218→
   219→  // Remove duplicates
   220→  const seenIds = treatments.map(t => t.id);
   221→  for (const t of additionalTreatments) {
   222→    if (!seenIds.includes(t.id)) {
   223→      seenIds.push(t.id);
   224→      treatments.push(t);
   225→    }
   226→  }
   227→
   228→  // Filter by gender if specified
   229→  if (gender) {
   230→    treatments = treatments.filter(t =>
   231→      !t.genderSpecific || t.genderSpecific === gender
   232→    );
   233→  }
   234→
   235→  // Score and rank treatments
   236→  const scoredTreatments = treatments.map(treatment => ({
   237→    treatment,
   238→    relevanceScore: scoreTreatmentRelevance(treatment, analysis),
   239→  }));
   240→
   241→  // Sort by relevance score
   242→  scoredTreatments.sort((a, b) => b.relevanceScore - a.relevanceScore);
   243→
   244→  // Create recommendations
   245→  return scoredTreatments.slice(0, maxRecommendations).map(({ treatment, relevanceScore }) => {
   246→    const avgPSLImprovement = (treatment.pslImprovement.min + treatment.pslImprovement.max) / 2;
   247→
   248→    return {
   249→      treatment,
   250→      priority: relevanceScore >= 60 ? 'high' as const :
   251→                relevanceScore >= 40 ? 'medium' as const :
   252→                'low' as const,
   253→      relevanceScore,
   254→      targetMetrics: [analysis.metricId],
   255→      estimatedImprovement: avgPSLImprovement,
   256→      reasoning: generateReasoning(treatment, analysis),
   257→    };
   258→  });
   259→}
   260→
   261→/**
   262→ * Generate human-readable reasoning for a recommendation
   263→ */
   264→function generateReasoning(
   265→  treatment: Treatment | Surgery,
   266→  analysis: MetricAnalysis
   267→): string {
   268→  const parts: string[] = [];
   269→
   270→  // Issue description
   271→  if (analysis.issue) {
   272→    parts.push(`Addresses ${analysis.issue.replace(/_/g, ' ')}`);
   273→  }
   274→
   275→  // Effectiveness
   276→  parts.push(`Effectiveness: ${treatment.effectiveness}/10`);
   277→
   278→  // PSL improvement
   279→  const pslRange = `${treatment.pslImprovement.min}-${treatment.pslImprovement.max}`;
   280→  parts.push(`Expected improvement: ${pslRange} PSL points`);
   281→
   282→  // Timeline
   283→  parts.push(`Results in: ${treatment.timelineToResults}`);
   284→
   285→  return parts.join('. ') + '.';
   286→}
   287→
   288→// ============================================
   289→// RECOMMENDATION PLAN GENERATION
   290→// ============================================
   291→
   292→/**
   293→ * Generate a comprehensive recommendation plan
   294→ */
   295→export function generateRecommendationPlan(
   296→  metrics: MetricInput[],
   297→  overallScore: number,
   298→  harmonyPercent: number,
   299→  gender?: Gender
   300→): RecommendationPlan {
   301→  // Analyze all metrics
   302→  const metricAnalyses = analyzeAllMetrics(metrics, gender);
   303→
   304→  // Determine strengths and weaknesses
   305→  const strengths = metricAnalyses.filter(m =>
   306→    m.severity.severity === 'optimal' || m.score >= 85
   307→  );
   308→  const weaknesses = metricAnalyses.filter(m =>
   309→    m.severity.severity !== 'optimal' && m.score < 85
   310→  );
   311→
   312→  // Get recommendations for each weakness
   313→  const allRecommendations: TreatmentRecommendation[] = [];
   314→  for (const weakness of weaknesses) {
   315→    const recs = getRecommendationsForMetric(weakness, gender);
   316→    allRecommendations.push(...recs);
   317→  }
   318→
   319→  // Deduplicate recommendations (same treatment might help multiple metrics)
   320→  const uniqueRecommendations = deduplicateRecommendations(allRecommendations);
   321→
   322→  // Categorize recommendations
   323→  const lifestyle = uniqueRecommendations.filter(r =>
   324→    r.treatment.category === 'lifestyle' || r.treatment.category === 'softmaxxing'
   325→  );
   326→  const softmaxxing = uniqueRecommendations.filter(r =>
   327→    r.treatment.category === 'softmaxxing'
   328→  );
   329→  const nonSurgical = uniqueRecommendations.filter(r =>
   330→    r.treatment.category === 'non_surgical'
   331→  );
   332→  const minimallyInvasive = uniqueRecommendations.filter(r =>
   333→    r.treatment.category === 'minimally_invasive'
   334→  );
   335→  const surgical = uniqueRecommendations.filter(r =>
   336→    r.treatment.category === 'surgical'
   337→  );
   338→
   339→  // Get supplement recommendations
   340→  const supplements = getSupplementRecommendations(weaknesses, gender);
   341→
   342→  // Calculate PSL ratings
   343→  const pslRating = harmonyToPSL(harmonyPercent);
   344→  const treatmentImprovements = uniqueRecommendations.map(r => r.estimatedImprovement);
   345→  const potentialPSL = estimatePotentialPSL(pslRating.psl, treatmentImprovements);
   346→
   347→  // Generate order of operations
   348→  const orderOfOperations = generateOrderOfOperations(uniqueRecommendations);
   349→
   350→  // Get overall severity
   351→  const overallSeverity = getOverallSeverity(metricAnalyses.map(m => m.severity.severity));
   352→
   353→  return {
   354→    overallScore,
   355→    overallSeverity,
   356→    harmonyScore: harmonyPercent,
   357→    percentile: pslRating.percentile,
   358→    metricAnalyses,
   359→    strengths,
   360→    weaknesses,
   361→    lifestyle,
   362→    softmaxxing,
   363→    nonSurgical,
   364→    minimallyInvasive,
   365→    surgical,
   366→    supplements,
   367→    currentPSL: pslRating.psl,
   368→    potentialPSL: potentialPSL.potentialPSL,
   369→    potentialImprovement: potentialPSL.totalImprovement,
   370→    orderOfOperations,
   371→  };
   372→}
   373→
   374→/**
   375→ * Deduplicate recommendations, combining target metrics for same treatment
   376→ */
   377→function deduplicateRecommendations(
   378→  recommendations: TreatmentRecommendation[]
   379→): TreatmentRecommendation[] {
   380→  const byId = new Map<string, TreatmentRecommendation>();
   381→
   382→  for (const rec of recommendations) {
   383→    const existing = byId.get(rec.treatment.id);
   384→    if (existing) {
   385→      // Combine target metrics (deduplicate using filter)
   386→      const combined = [...existing.targetMetrics, ...rec.targetMetrics];
   387→      existing.targetMetrics = combined.filter((m, i) => combined.indexOf(m) === i);
   388→
   389→      // Take higher relevance score
   390→      existing.relevanceScore = Math.max(existing.relevanceScore, rec.relevanceScore);
   391→
   392→      // Update priority based on new relevance
   393→      existing.priority = existing.relevanceScore >= 60 ? 'high' :
   394→                         existing.relevanceScore >= 40 ? 'medium' :
   395→                         'low';
   396→    } else {
   397→      byId.set(rec.treatment.id, { ...rec });
   398→    }
   399→  }
   400→
   401→  return Array.from(byId.values()).sort((a, b) => b.relevanceScore - a.relevanceScore);
   402→}
   403→
   404→/**
   405→ * Get supplement recommendations based on weaknesses
   406→ */
   407→function getSupplementRecommendations(
   408→  weaknesses: MetricAnalysis[],
   409→  gender?: Gender
   410→): TreatmentRecommendation[] {
   411→  const recommendations: TreatmentRecommendation[] = [];
   412→
   413→  // Map weakness types to supplement categories
   414→  const hasSkinIssues = weaknesses.some(w =>
   415→    w.issue?.includes('skin') || w.metricId.includes('skin')
   416→  );
   417→  const hasHairIssues = weaknesses.some(w =>
   418→    w.issue?.includes('hair') || w.metricId.includes('hairline')
   419→  );
   420→  const hasGeneralAging = weaknesses.some(w =>
   421→    w.issue?.includes('aging') || w.severity.severity === 'moderate' || w.severity.severity === 'severe'
   422→  );
   423→
   424→  // Helper to create supplement recommendation
   425→  const createSupplementRec = (
   426→    supp: Supplement,
   427→    priority: 'high' | 'medium' | 'low',
   428→    relevanceScore: number,
   429→    targetMetrics: string[],
   430→    reasoning: string
   431→  ): TreatmentRecommendation => ({
   432→    treatment: {
   433→      id: supp.id,
   434→      name: supp.name,
   435→      category: 'lifestyle',
   436→      description: supp.description,
   437→      howItWorks: `Dosage: ${supp.dosage} ${supp.frequency}`,
   438→      effectiveness: supp.effectiveness,
   439→      evidenceLevel: supp.evidenceLevel,
   440→      pslImprovement: { min: 0.05, max: 0.15 },
   441→      costLevel: supp.costPerMonth.max > 30 ? 'medium' : 'low',
   442→      costRange: { min: supp.costPerMonth.min, max: supp.costPerMonth.max, currency: 'USD' },
   443→      costFrequency: 'monthly',
   444→      timelineToResults: supp.timelineToResults,
   445→      resultsDuration: 'Ongoing with continued use',
   446→      maintenanceRequired: true,
   447→      maintenanceFrequency: supp.frequency,
   448→      recoveryTime: 'None',
   449→      downtime: 'None',
   450→      painLevel: 'none',
   451→      riskLevel: supp.riskLevel,
   452→      sideEffects: supp.sideEffects,
   453→      contraindications: supp.contraindications,
   454→      targetMetrics,
   455→      targetIssues: supp.targetBenefits,
   456→      notes: supp.notes,
   457→      sources: supp.sources,
   458→    },
   459→    priority,
   460→    relevanceScore,
   461→    targetMetrics,
   462→    estimatedImprovement: 0.1,
   463→    reasoning,
   464→  });
   465→
   466→  // Add relevant supplements
   467→  if (hasSkinIssues || hasGeneralAging) {
   468→    const skinSupplements = SUPPLEMENTS.filter(s =>
   469→      s.category === 'skin' || s.category === 'anti-aging'
   470→    );
   471→    for (const supp of skinSupplements.slice(0, 3)) {
   472→      recommendations.push(createSupplementRec(
   473→        supp,
   474→        'medium',
   475→        50,
   476→        ['skin_quality'],
   477→        `${supp.name} supports skin health and anti-aging.`
   478→      ));
   479→    }
   480→  }
   481→
   482→  if (hasHairIssues && gender === 'male') {
   483→    const hairSupplements = SUPPLEMENTS.filter(s => s.category === 'hair');
   484→    for (const supp of hairSupplements.slice(0, 3)) {
   485→      recommendations.push(createSupplementRec(
   486→        supp,
   487→        'medium',
   488→        45,
   489→        ['hairline'],
   490→        `${supp.name} supports hair health.`
   491→      ));
   492→    }
   493→  }
   494→
   495→  // Always recommend foundational supplements
   496→  const foundational = SUPPLEMENTS.filter(s =>
   497→    ['vitamin_d3', 'omega_3', 'magnesium'].includes(s.id)
   498→  );
   499→  for (const supp of foundational) {
   500→    if (!recommendations.some(r => r.treatment.id === supp.id)) {
   501→      recommendations.push(createSupplementRec(
   502→        supp,
   503→        'low',
   504→        30,
   505→        ['general_health'],
   506→        `${supp.name} supports overall health and wellbeing.`
   507→      ));
   508→    }
   509→  }
   510→
   511→  return recommendations.slice(0, 10);
   512→}
   513→
   514→/**
   515→ * Generate order of operations for treatments
   516→ */
   517→function generateOrderOfOperations(
   518→  recommendations: TreatmentRecommendation[]
   519→): OrderOfOperation[] {
   520→  const operations: OrderOfOperation[] = [];
   521→
   522→  // Define category order: lifestyle -> softmaxxing -> non-surgical -> minimally invasive -> surgical
   523→  const categoryOrder: TreatmentCategory[] = [
   524→    'lifestyle',
   525→    'softmaxxing',
   526→    'non_surgical',
   527→    'minimally_invasive',
   528→    'surgical',
   529→  ];
   530→
   531→  // Group by category (only for valid treatment categories)
   532→  const validCategories: TreatmentCategory[] = ['lifestyle', 'softmaxxing', 'non_surgical', 'minimally_invasive', 'surgical'];
   533→  const byCategory = new Map<TreatmentCategory, TreatmentRecommendation[]>();
   534→  for (const rec of recommendations) {
   535→    const cat = rec.treatment.category as TreatmentCategory;
   536→    if (validCategories.includes(cat)) {
   537→      if (!byCategory.has(cat)) {
   538→        byCategory.set(cat, []);
   539→      }
   540→      byCategory.get(cat)!.push(rec);
   541→    }
   542→  }
   543→
   544→  let step = 1;
   545→  for (const category of categoryOrder) {
   546→    const recs = byCategory.get(category) || [];
   547→    // Sort by priority and relevance
   548→    recs.sort((a, b) => {
   549→      if (a.priority !== b.priority) {
   550→        const priorityOrder = { high: 0, medium: 1, low: 2 };
   551→        return priorityOrder[a.priority] - priorityOrder[b.priority];
   552→      }
   553→      return b.relevanceScore - a.relevanceScore;
   554→    });
   555→
   556→    for (const rec of recs.slice(0, 3)) { // Max 3 per category
   557→      const treatment = rec.treatment as Treatment | Surgery;
   558→      const operation: OrderOfOperation = {
   559→        step,
   560→        category,
   561→        treatment,
   562→        reasoning: getStepReasoning(step, category),
   563→      };
   564→
   565→      // Add prerequisites for surgical procedures
   566→      if (category === 'surgical') {
   567→        operation.prerequisites = ['Complete non-surgical options first', 'Consult with board-certified surgeon'];
   568→        operation.waitTime = '6-12 months recovery between major procedures';
   569→      }
   570→
   571→      operations.push(operation);
   572→      step++;
   573→    }
   574→  }
   575→
   576→  return operations;
   577→}
   578→
   579→/**
   580→ * Generate reasoning for a step in order of operations
   581→ */
   582→function getStepReasoning(
   583→  step: number,
   584→  category: TreatmentCategory
   585→): string {
   586→  if (step === 1) {
   587→    return 'Start with foundational changes before considering more invasive options.';
   588→  }
   589→
   590→  if (category === 'lifestyle' || category === 'softmaxxing') {
   591→    return 'Low-risk, reversible changes that can provide noticeable improvement.';
   592→  }
   593→
   594→  if (category === 'non_surgical') {
   595→    return 'Non-invasive treatments with minimal downtime to test results before committing to surgery.';
   596→  }
   597→
   598→  if (category === 'minimally_invasive') {
   599→    return 'Minimally invasive procedures can provide significant results with less risk than surgery.';
   600→  }
   601→
   602→  if (category === 'surgical') {
   603→    return 'Consider surgery only after exhausting less invasive options and with realistic expectations.';
   604→  }
   605→
   606→  return '';
   607→}
   608→
   609→// ============================================
   610→// UTILITY FUNCTIONS
   611→// ============================================
   612→
   613→/**
   614→ * Get top priority recommendations across all categories
   615→ */
   616→export function getTopRecommendations(
   617→  plan: RecommendationPlan,
   618→  count: number = 5
   619→): TreatmentRecommendation[] {
   620→  const all = [
   621→    ...plan.lifestyle,
   622→    ...plan.softmaxxing,
   623→    ...plan.nonSurgical,
   624→    ...plan.minimallyInvasive,
   625→    ...plan.surgical,
   626→  ];
   627→
   628→  return all
   629→    .sort((a, b) => b.relevanceScore - a.relevanceScore)
   630→    .slice(0, count);
   631→}
   632→
   633→/**
   634→ * Get recommendations filtered by cost level
   635→ */
   636→export function getRecommendationsByCost(
   637→  plan: RecommendationPlan,
   638→  maxCost: 'free' | 'low' | 'medium' | 'high' | 'very_high'
   639→): TreatmentRecommendation[] {
   640→  const costOrder = ['free', 'low', 'medium', 'high', 'very_high'];
   641→  const maxCostIndex = costOrder.indexOf(maxCost);
   642→
   643→  const all = [
   644→    ...plan.lifestyle,
   645→    ...plan.softmaxxing,
   646→    ...plan.nonSurgical,
   647→    ...plan.minimallyInvasive,
   648→    ...plan.surgical,
   649→  ];
   650→
   651→  return all.filter(r => {
   652→    const treatment = r.treatment as Treatment;
   653→    if ('costLevel' in treatment) {
   654→      return costOrder.indexOf(treatment.costLevel) <= maxCostIndex;
   655→    }
   656→    return true;
   657→  });
   658→}
   659→
   660→/**
   661→ * Get recommendations with minimal downtime
   662→ */
   663→export function getNoDowntimeRecommendations(
   664→  plan: RecommendationPlan
   665→): TreatmentRecommendation[] {
   666→  const all = [
   667→    ...plan.lifestyle,
   668→    ...plan.softmaxxing,
   669→    ...plan.nonSurgical,
   670→    ...plan.minimallyInvasive,
   671→  ];
   672→
   673→  return all.filter(r => {
   674→    const treatment = r.treatment as Treatment;
   675→    if ('downtime' in treatment) {
   676→      const downtime = treatment.downtime.toLowerCase();
   677→      return downtime.includes('none') || downtime.includes('minimal');
   678→    }
   679→    return true;
   680→  });
   681→}
   682→
   683→/**
   684→ * Format cost for display
   685→ */
   686→export function formatCost(treatment: Treatment | Surgery): string {
   687→  const { min, max, currency } = treatment.costRange;
   688→  const symbol = currency === 'USD' ? '$' : currency === 'EUR' ? '€' : currency === 'GBP' ? '£' : currency;
   689→
   690→  if (min === max) {
   691→    return `${symbol}${min.toLocaleString()}`;
   692→  }
   693→
   694→  return `${symbol}${min.toLocaleString()} - ${symbol}${max.toLocaleString()}`;
   695→}
   696→
   697→/**
   698→ * Get summary statistics for a plan
   699→ */
   700→export function getPlanSummary(plan: RecommendationPlan): {
   701→  totalRecommendations: number;
   702→  highPriority: number;
   703→  mediumPriority: number;
   704→  lowPriority: number;
   705→  categoryCounts: Record<string, number>;
   706→  estimatedTotalImprovement: number;
   707→} {
   708→  const all = [
   709→    ...plan.lifestyle,
   710→    ...plan.softmaxxing,
   711→    ...plan.nonSurgical,
   712→    ...plan.minimallyInvasive,
   713→    ...plan.surgical,
   714→  ];
   715→
   716→  return {
   717→    totalRecommendations: all.length,
   718→    highPriority: all.filter(r => r.priority === 'high').length,
   719→    mediumPriority: all.filter(r => r.priority === 'medium').length,
   720→    lowPriority: all.filter(r => r.priority === 'low').length,
   721→    categoryCounts: {
   722→      lifestyle: plan.lifestyle.length,
   723→      softmaxxing: plan.softmaxxing.length,
   724→      nonSurgical: plan.nonSurgical.length,
   725→      minimallyInvasive: plan.minimallyInvasive.length,
   726→      surgical: plan.surgical.length,
   727→      supplements: plan.supplements.length,
   728→    },
   729→    estimatedTotalImprovement: plan.potentialImprovement,
   730→  };
   731→}
