  1079→      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
  1080→        {guides.map((guide, idx) => (
  1081→          <GuideCard key={guide.id} guide={guide} index={idx} onOpen={onOpenGuide} />
  1082→        ))}
  1083→      </div>
  1084→    </div>
  1085→  );
  1086→}
  1087→
  1088→// ============================================
