'use client';

import React, { createContext, useContext, useState, ReactNode } from 'react';

interface CategoryScore {
  score: number;
  percentile: number;
  details: string[];
}

interface AnalysisResult {
  overallRating: number;
  categories: {
    eyes: CategoryScore;
    nose: CategoryScore;
    jawline: CategoryScore;
    lips: CategoryScore;
    symmetry: CategoryScore;
    skinQuality: CategoryScore;
  };
  timestamp: Date;
}

interface UserProfile {
  gender: 'male' | 'female' | 'other' | null;
  ethnicity: string | null;
  photoUrl: string | null;
}

interface AnalysisContextType {
  userProfile: UserProfile;
  analysisResult: AnalysisResult | null;
  setGender: (gender: 'male' | 'female' | 'other') => void;
  setEthnicity: (ethnicity: string) => void;
  setPhotoUrl: (url: string) => void;
  setAnalysisResult: (result: AnalysisResult) => void;
  resetAnalysis: () => void;
}

const defaultUserProfile: UserProfile = {
  gender: null,
  ethnicity: null,
  photoUrl: null,
};

const AnalysisContext = createContext<AnalysisContextType | undefined>(undefined);

export function AnalysisProvider({ children }: { children: ReactNode }) {
  const [userProfile, setUserProfile] = useState<UserProfile>(defaultUserProfile);
  const [analysisResult, setAnalysisResultState] = useState<AnalysisResult | null>(null);

  const setGender = (gender: 'male' | 'female' | 'other') => {
    setUserProfile((prev) => ({ ...prev, gender }));
  };

  const setEthnicity = (ethnicity: string) => {
    setUserProfile((prev) => ({ ...prev, ethnicity }));
  };

  const setPhotoUrl = (url: string) => {
    setUserProfile((prev) => ({ ...prev, photoUrl: url }));
  };

  const setAnalysisResult = (result: AnalysisResult) => {
    setAnalysisResultState(result);
  };

  const resetAnalysis = () => {
    setUserProfile(defaultUserProfile);
    setAnalysisResultState(null);
  };

  return (
    <AnalysisContext.Provider
      value={{
        userProfile,
        analysisResult,
        setGender,
        setEthnicity,
        setPhotoUrl,
        setAnalysisResult,
        resetAnalysis,
      }}
    >
      {children}
    </AnalysisContext.Provider>
  );
}

export function useAnalysis() {
  const context = useContext(AnalysisContext);
  if (context === undefined) {
    throw new Error('useAnalysis must be used within an AnalysisProvider');
  }
  return context;
}
