'use client';

import { motion } from 'framer-motion';

interface RatingDisplayProps {
  rating: number;
  size?: 'small' | 'medium' | 'large';
}

export function RatingDisplay({ rating, size = 'medium' }: RatingDisplayProps) {
  const sizes = {
    small: { container: 'w-16 h-16', text: 'text-xl', stroke: 4 },
    medium: { container: 'w-24 h-24', text: 'text-3xl', stroke: 6 },
    large: { container: 'w-32 h-32', text: 'text-4xl', stroke: 8 },
  };

  const { container, text, stroke } = sizes[size];
  const radius = 45;
  const circumference = 2 * Math.PI * radius;
  const progress = (rating / 10) * circumference;

  const getColor = (rating: number) => {
    if (rating >= 8) return '#22c55e'; // green
    if (rating >= 6) return '#eab308'; // yellow
    if (rating >= 4) return '#f97316'; // orange
    return '#ef4444'; // red
  };

  return (
    <div className={`relative ${container} mx-auto`}>
      <svg className="w-full h-full transform -rotate-90" viewBox="0 0 100 100">
        {/* Background circle */}
        <circle
          cx="50"
          cy="50"
          r={radius}
          fill="none"
          stroke="#374151"
          strokeWidth={stroke}
        />
        {/* Progress circle */}
        <motion.circle
          cx="50"
          cy="50"
          r={radius}
          fill="none"
          stroke={getColor(rating)}
          strokeWidth={stroke}
          strokeLinecap="round"
          strokeDasharray={circumference}
          initial={{ strokeDashoffset: circumference }}
          animate={{ strokeDashoffset: circumference - progress }}
          transition={{ duration: 1.5, ease: 'easeOut', delay: 0.5 }}
        />
      </svg>
      <div className="absolute inset-0 flex items-center justify-center">
        <motion.span
          initial={{ opacity: 0, scale: 0.5 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.8, duration: 0.5 }}
          className={`${text} font-bold text-white`}
        >
          {rating.toFixed(1)}
        </motion.span>
      </div>
    </div>
  );
}
