  1260→  matchedMetrics: MatchedMetric[];
  1261→}
  1262→
  1263→export interface ClassifiedStrength {
  1264→  insightId: string;
  1265→  title: string;
  1266→  description: string;
  1267→  grade: StrengthGrade;
  1268→  gradeLabel: string;  // Custom label from definition (e.g., "Ideal")
  1269→  avgScore: number;
  1270→  matchedMetrics: MatchedMetric[];
  1271→}
  1272→
  1273→export interface MatchedMetric {
  1274→  metricId: string;
  1275→  metricName: string;
  1276→  score: number;
  1277→  value: number;
  1278→  idealMin: number;
  1279→  idealMax: number;
  1280→  unit: string;
  1281→  category: string;
  1282→}
  1283→
  1284→export interface InsightClassificationResult {
  1285→  strengths: ClassifiedStrength[];
  1286→  weaknesses: ClassifiedWeakness[];
  1287→}
