  2441→/**
  2442→ * Determine quality tier based on score
  2443→ */
  2444→export function getQualityTier(score: number, maxScore: number = 10): QualityTier {
  2445→  const normalized = (score / maxScore) * 100;
  2446→
  2447→  if (normalized >= 90) return 'ideal';
  2448→  if (normalized >= 70) return 'excellent';
  2449→  if (normalized >= 50) return 'good';
  2450→  return 'below_average';
  2451→}
  2452→
  2453→/**
  2454→ * Determine severity level based on score
  2455→ */
  2456→export function getSeverityLevel(score: number, maxScore: number = 10): SeverityLevel {
  2457→  const normalized = (score / maxScore) * 100;
  2458→
  2459→  if (normalized >= 85) return 'optimal';
  2460→  if (normalized >= 70) return 'minor';
  2461→  if (normalized >= 50) return 'moderate';
  2462→  if (normalized >= 30) return 'major';
  2463→  if (normalized >= 15) return 'severe';
  2464→  return 'extremely_severe';
  2465→}
