  2467→/**
  2468→ * Calculate deviation description
  2469→ */
  2470→export function getDeviationDescription(
  2471→  value: number,
  2472→  idealMin: number,
  2473→  idealMax: number,
  2474→  unit: MeasurementUnit
  2475→): { deviation: number; direction: 'above' | 'below' | 'within'; description: string } {
  2476→  if (value >= idealMin && value <= idealMax) {
  2477→    return { deviation: 0, direction: 'within', description: 'within ideal range' };
  2478→  }
  2479→
  2480→  const unitLabel = getUnitLabel(unit);
  2481→
  2482→  if (value < idealMin) {
  2483→    const dev = idealMin - value;
  2484→    return {
  2485→      deviation: dev,
  2486→      direction: 'below',
  2487→      description: `${dev.toFixed(2)}${unitLabel} below ideal`,
  2488→    };
  2489→  }
  2490→
  2491→  const dev = value - idealMax;
  2492→  return {
  2493→    deviation: dev,
  2494→    direction: 'above',
  2495→    description: `${dev.toFixed(2)}${unitLabel} above ideal`,
  2496→  };
  2497→}
  2498→
  2499→function getUnitLabel(unit: MeasurementUnit): string {
  2500→  switch (unit) {
  2501→    case 'ratio': return 'x';
  2502→    case 'percent': return '%';
  2503→    case 'degrees': return '°';
  2504→    case 'mm': return 'mm';
  2505→    default: return '';
  2506→  }
  2507→}
