  2510→// MEASUREMENT CALCULATION UTILITIES
  2511→// ============================================
  2512→
  2513→/**
  2514→ * Calculate distance between two points
  2515→ */
  2516→export function distance(p1: Point, p2: Point): number {
  2517→  return Math.sqrt(Math.pow(p2.x - p1.x, 2) + Math.pow(p2.y - p1.y, 2));
  2518→}
  2519→
  2520→/**
  2521→ * Calculate angle at vertex point (in degrees)
  2522→ */
  2523→export function calculateAngle(p1: Point, vertex: Point, p2: Point): number {
  2524→  const v1 = { x: p1.x - vertex.x, y: p1.y - vertex.y };
  2525→  const v2 = { x: p2.x - vertex.x, y: p2.y - vertex.y };
  2526→
  2527→  const dot = v1.x * v2.x + v1.y * v2.y;
  2528→  const cross = v1.x * v2.y - v1.y * v2.x;
  2529→
  2530→  const angle = Math.atan2(cross, dot) * (180 / Math.PI);
  2531→  return Math.abs(angle);
