  1600→ * Maps score ranges to confidence levels inversely (lower score = higher confidence).
  1601→ *
  1602→ * @param avgScore - Average score of the flaw (0-10 scale)
  1603→ * @returns ConfidenceLevel
  1604→ */
  1605→export function calculateConfidenceFromScore(avgScore: number): ConfidenceLevel {
  1606→  // Very low scores = high confidence it's a real flaw
  1607→  if (avgScore <= 3) return 'confirmed';
  1608→  // Poor scores = moderate confidence
  1609→  if (avgScore <= 5) return 'likely';
  1610→  // Below average scores = low confidence
  1611→  return 'possible';
  1612→}
  1613→
  1614→/**
  1615→ * Calculate confidence for a set of matched metrics.
  1616→ * Uses Z-score when available, falls back to score-based calculation.
  1617→ *
  1618→ * @param matchedMetrics - Array of metrics contributing to the flaw
  1619→ * @param avgScore - Overall average score of the flaw
  1620→ * @returns ConfidenceLevel
  1621→ */
  1622→export function calculateFlawConfidence(
  1623→  matchedMetrics: MatchedMetric[],
  1624→  avgScore: number
  1625→): ConfidenceLevel {
  1626→  // Calculate Z-scores for each metric if we have the config
  1627→  const zScores: number[] = [];
  1628→
  1629→  for (const metric of matchedMetrics) {
  1630→    const config = MASTER_SCORING_DB[metric.metricId];
  1631→    if (config && config.std_dev > 0) {
  1632→      const zScore = calculateZScore(metric.value, config.mean, config.std_dev);
  1633→      zScores.push(zScore);
  1634→    }
  1635→  }
  1636→
  1637→  // If we have Z-scores, use the maximum (highest deviation = highest confidence)
  1638→  if (zScores.length > 0) {
  1639→    const maxZScore = Math.max(...zScores);
  1640→    return calculateConfidenceFromZScore(maxZScore);
  1641→  }
  1642→
  1643→  // Fall back to score-based confidence
  1644→  return calculateConfidenceFromScore(avgScore);
  1645→}
