   650→ *
   651→ * First pass: Creates grouped flaws from related low-scoring metrics (2+ metrics < 6).
   652→ * Second pass: Adds remaining individual flaws that weren't grouped.
   653→ *
   654→ * @param analysis - HarmonyAnalysis containing measurements and flaws
   655→ * @returns Array of Flaw objects sorted by contributing ratios count then impact
   656→ */
   657→export function generateFlawsFromAnalysis(analysis: HarmonyAnalysis): Flaw[] {
   658→  const flaws: Flaw[] = [];
   659→  const usedMetricIds = new Set<string>();
   660→  let rollingLost = 0;
   661→
   662→  // First pass: Create grouped flaws from related low-scoring metrics
   663→  Object.entries(FLAW_GROUPINGS).forEach(([groupId, groupConfig]) => {
   664→    // Find all low-scoring measurements that belong to this group
   665→    const groupMeasurements = analysis.measurements.filter(m =>
   666→      groupConfig.metrics.includes(m.metricId) &&
   667→      m.score < 6 &&
   668→      !usedMetricIds.has(m.metricId)
   669→    );
   670→
   671→    // Only create a grouped flaw if we have 2+ metrics with low scores
   672→    if (groupMeasurements.length >= 2) {
   673→      // Mark these metrics as used
   674→      groupMeasurements.forEach(m => usedMetricIds.add(m.metricId));
   675→
   676→      // Calculate total impact from all metrics in this group
   677→      const totalImpact = groupMeasurements.reduce((sum, m) => sum + (10 - m.score) * 0.4, 0);
   678→      rollingLost += totalImpact;
   679→
   680→      // Create grouped flaw with multiple contributing ratios
   681→      flaws.push({
   682→        id: `flaw_group_${groupId}`,
   683→        flawName: groupConfig.name,
   684→        summary: groupConfig.description,
   685→        harmonyPercentageLost: totalImpact,
   686→        standardizedImpact: totalImpact / 10,
   687→        categoryName: groupConfig.category,
   688→        responsibleRatios: groupMeasurements.map(m => ({
   689→          ratioName: m.name,
   690→          ratioId: m.metricId,
   691→          score: m.score,
   692→          value: m.value,
   693→          idealMin: m.idealMin,
   694→          idealMax: m.idealMax,
   695→          unit: m.unit,
   696→          category: m.category,
   697→        })),
   698→        rollingPointsDeducted: rollingLost,
   699→        rollingHarmonyPercentageLost: rollingLost,
   700→        rollingStandardizedImpact: rollingLost / 10,
   701→      });
   702→    }
   703→  });
   704→
   705→  // Second pass: Add remaining individual flaws that weren't grouped
   706→  analysis.flaws.forEach((f, i) => {
   707→    if (usedMetricIds.has(f.metricId)) return; // Skip if already used in a group
   708→
   709→    const matchingMeasurement = analysis.measurements.find(m => m.metricId === f.metricId);
   710→    const impact = matchingMeasurement ? (10 - matchingMeasurement.score) * 0.5 : 2;
   711→    rollingLost += impact;
   712→
   713→    flaws.push({
   714→      id: `flaw_${i}`,
   715→      flawName: f.metricName,
   716→      summary: f.reasoning,
   717→      harmonyPercentageLost: impact,
   718→      standardizedImpact: impact / 10,
   719→      categoryName: f.category,
   720→      responsibleRatios: [{
   721→        ratioName: f.metricName,
   722→        ratioId: f.metricId,
   723→        score: matchingMeasurement?.score || 3,
   724→        value: matchingMeasurement?.value || 0,
   725→        idealMin: matchingMeasurement?.idealMin || 0,
   726→        idealMax: matchingMeasurement?.idealMax || 1,
   727→        unit: matchingMeasurement?.unit || 'ratio',
