/**
 * Get the appropriate metric configuration for a given gender and ethnicity
 * Falls back to base config if no override exists
 */
export function getMetricConfig(
  metricId: string,
  gender: Gender = 'male',
  ethnicity: Ethnicity = 'white'
): MasterMetricConfig | undefined {
  const key = `${gender}_${ethnicity}`;
  const override = ETHNICITY_OVERRIDES[key]?.[metricId];
  const baseConfig = MASTER_SCORING_DB[metricId];

  if (!baseConfig) return undefined;

  // Merge override with base config
  if (override) {
    return {
      ...baseConfig,
      ...override,
      flaws: {
        ...baseConfig.flaws,
        ...override.flaws
      }
    } as MasterMetricConfig;
  }

  return baseConfig;
}

// ============================================
// Z-SCORE SEVERITY FUNCTIONS
// ============================================

/**
 * Calculate Z-score for a given value
 * Z = (value - mean) / std_dev
 */
export function calculateZScore(value: number, mean: number, stdDev: number): number {
  if (stdDev === 0) return 0;
  return Math.abs((value - mean) / stdDev);
}

/**
 * Determine severity level based on Z-score and ideal range (5-tier system)
 * - Ideal: Value is within ideal range
 * - Good: |z| < 0.5 (within 0.5 standard deviation)
 * - Fair: 0.5 ≤ |z| < 1 (between 0.5-1 standard deviations)
 * - Moderate: 1 ≤ |z| < 2 (between 1-2 standard deviations)
 * - Severe: |z| ≥ 2 (more than 2 standard deviations away)
 */
export function getSeverityFromZScore(
  value: number,
  config: MasterMetricConfig
): { severity: SeverityLevel; zScore: number; isInIdeal: boolean } {
  const { ideal, mean, std_dev } = config;
  const isInIdeal = value >= ideal[0] && value <= ideal[1];
  const zScore = calculateZScore(value, mean, std_dev);

  let severity: SeverityLevel;
  if (isInIdeal) {
    severity = 'ideal';
  } else if (zScore < 0.5) {
    severity = 'good';
  } else if (zScore < 1) {
    severity = 'fair';
  } else if (zScore < 2) {
    severity = 'moderate';
  } else {
    severity = 'severe';
  }

  return { severity, zScore, isInIdeal };
}

/**
 * Get severity for a metric value with ethnicity/gender awareness
 */
export function getSeverityForMetric(
  metricId: string,
  value: number,
  gender: Gender = 'male',
  ethnicity: Ethnicity = 'white'
): { severity: SeverityLevel; zScore: number; isInIdeal: boolean; config: MasterMetricConfig } | null {
  const config = getMetricConfig(metricId, gender, ethnicity);
  if (!config) return null;

  const result = getSeverityFromZScore(value, config);
  return { ...result, config };
}

/**
 * Get badge color for severity level (5-tier)
 */
export function getSeverityColor(severity: SeverityLevel): string {
  switch (severity) {
    case 'ideal': return 'green';
    case 'good': return 'blue';
    case 'fair': return 'cyan';
    case 'moderate': return 'yellow';
    case 'severe': return 'red';
    default: return 'gray';
  }
}

/**
 * Get severity label for display (5-tier)
 */
export function getSeverityLabel(severity: SeverityLevel): string {
  switch (severity) {
    case 'ideal': return 'Ideal';
    case 'good': return 'Good';
    case 'fair': return 'Fair';
    case 'moderate': return 'Moderate';
    case 'severe': return 'Severe';
    default: return 'Unknown';
  }
}

// ============================================
// CONFIDENCE CALCULATION (Z-Score Based)
// ============================================

/**
 * Calculate confidence level for flaw detection based on Z-score magnitude.
 *
 * Logic from FaceIQ:
 * - confirmed: |z| >= 2 (high statistical certainty - 2+ standard deviations)
 * - likely: 1 <= |z| < 2 (moderate certainty - 1-2 standard deviations)
 * - possible: 0.5 <= |z| < 1 (low certainty - 0.5-1 standard deviation)
 *
 * For metrics without Z-score data, we calculate confidence from the score:
 * Score 0-3 = confirmed (very poor score = high confidence it's a flaw)
 * Score 3-5 = likely (poor score = moderate confidence)
 * Score 5-7 = possible (below average = low confidence)
 *
 * @param zScore - The absolute Z-score value
 * @returns ConfidenceLevel - 'confirmed' | 'likely' | 'possible'
 */
export function calculateConfidenceFromZScore(zScore: number): ConfidenceLevel {
  if (zScore >= 2) return 'confirmed';
  if (zScore >= 1) return 'likely';
  return 'possible';
}

/**
 * Calculate confidence level from score when Z-score is unavailable.
 * Maps score ranges to confidence levels inversely (lower score = higher confidence).
 *
 * @param avgScore - Average score of the flaw (0-10 scale)
 * @returns ConfidenceLevel
 */
export function calculateConfidenceFromScore(avgScore: number): ConfidenceLevel {
  // Very low scores = high confidence it's a real flaw
  if (avgScore <= 3) return 'confirmed';
  // Poor scores = moderate confidence
  if (avgScore <= 5) return 'likely';
  // Below average scores = low confidence
  return 'possible';
}

/**
 * Calculate confidence for a set of matched metrics.
 * Uses Z-score when available, falls back to score-based calculation.
 *
 * @param matchedMetrics - Array of metrics contributing to the flaw
 * @param avgScore - Overall average score of the flaw
 * @returns ConfidenceLevel
 */
export function calculateFlawConfidence(
  matchedMetrics: MatchedMetric[],
  avgScore: number
): ConfidenceLevel {
  // Calculate Z-scores for each metric if we have the config
  const zScores: number[] = [];

  for (const metric of matchedMetrics) {
    const config = MASTER_SCORING_DB[metric.metricId];
    if (config && config.std_dev > 0) {
      const zScore = calculateZScore(metric.value, config.mean, config.std_dev);
      zScores.push(zScore);
    }
  }

  // If we have Z-scores, use the maximum (highest deviation = highest confidence)
  if (zScores.length > 0) {
    const maxZScore = Math.max(...zScores);
    return calculateConfidenceFromZScore(maxZScore);
  }

  // Fall back to score-based confidence
  return calculateConfidenceFromScore(avgScore);
}

// ============================================
// STANDARDIZED IMPACT CALCULATION
// ============================================

/**
 * FaceIQ-compatible standardizedImpact range constants
 * Range: 0.07 (minimal) to 1.29 (maximum severity)
 */
const STANDARDIZED_IMPACT_MIN = 0.07;
const STANDARDIZED_IMPACT_MAX = 1.29;
const STANDARDIZED_IMPACT_RANGE = STANDARDIZED_IMPACT_MAX - STANDARDIZED_IMPACT_MIN;

/**
 * Maximum Z-score for normalization (3+ standard deviations = maximum severity)
 */
const MAX_Z_SCORE_FOR_NORMALIZATION = 3.0;

/**
 * Calculate standardized impact score for consistent flaw ranking.
 *
 * This normalizes the deviation relative to metric's typical variance,
 * producing a score in the 0.07-1.29 range matching FaceIQ's system.
 * Higher values indicate more severe flaws.
 *
 * Formula:
 * 1. Calculate Z-score for each contributing metric
 * 2. Take the maximum Z-score (worst deviation = most impact)
 * 3. Normalize to 0.07-1.29 range:
 *    - Z = 0 maps to 0.07 (minimal impact)
 *    - Z >= 3 maps to 1.29 (maximum impact)
 *    - Linear interpolation in between
 *
 * @param matchedMetrics - Array of metrics contributing to the flaw
 * @param avgScore - Fallback: average score when Z-scores unavailable
 * @returns Normalized standardized impact (0.07-1.29)
 */
export function calculateStandardizedImpact(
  matchedMetrics: MatchedMetric[],
  avgScore: number
): number {
  // Calculate Z-scores for each metric if we have the config
  const zScores: number[] = [];

  for (const metric of matchedMetrics) {
    const config = MASTER_SCORING_DB[metric.metricId];
    if (config && config.std_dev > 0) {
      const zScore = calculateZScore(metric.value, config.mean, config.std_dev);
      zScores.push(zScore);
    }
  }

  let normalizedScore: number;

  if (zScores.length > 0) {
    // Use maximum Z-score (highest deviation = highest severity)
    const maxZScore = Math.max(...zScores);

    // Clamp Z-score to 0-3 range, then normalize
    const clampedZScore = Math.min(maxZScore, MAX_Z_SCORE_FOR_NORMALIZATION);
    const normalized = clampedZScore / MAX_Z_SCORE_FOR_NORMALIZATION;

    // Map to 0.07-1.29 range
    normalizedScore = STANDARDIZED_IMPACT_MIN + (normalized * STANDARDIZED_IMPACT_RANGE);
  } else {
    // Fallback: derive from score (0-10 scale)
    // Score 0 = max impact (1.29), Score 10 = min impact (0.07)
    const scoreNormalized = Math.max(0, Math.min(10, 10 - avgScore)) / 10;
    normalizedScore = STANDARDIZED_IMPACT_MIN + (scoreNormalized * STANDARDIZED_IMPACT_RANGE);
  }

  // Round to 2 decimal places for consistency
  return Math.round(normalizedScore * 100) / 100;
}

/**
 * Calculate standardized impact from score only (no Z-scores).
 * Used when metric values/configs are not available.
 *
 * @param score - Score on 0-10 scale (lower = worse)
 * @returns Normalized standardized impact (0.07-1.29)
 */
export function calculateStandardizedImpactFromScore(score: number): number {
  // Score 0 = max impact (1.29), Score 10 = min impact (0.07)
  const safeScore = Math.max(0, Math.min(10, score));
  const scoreNormalized = (10 - safeScore) / 10;
  const impact = STANDARDIZED_IMPACT_MIN + (scoreNormalized * STANDARDIZED_IMPACT_RANGE);
  return Math.round(impact * 100) / 100;
}

/**
 * Calculate cumulative/rolling standardized impact.
 * Sums multiple impacts with diminishing returns to prevent excessive totals.
 *
 * @param impacts - Array of individual standardizedImpact values
 * @returns Cumulative impact with diminishing returns applied
 */
export function calculateRollingStandardizedImpact(impacts: number[]): number {
  if (impacts.length === 0) return STANDARDIZED_IMPACT_MIN;

  // Sort by severity (highest first) for proper diminishing returns
  const sorted = [...impacts].sort((a, b) => b - a);

  let total = 0;
  sorted.forEach((impact, index) => {
    // Each subsequent impact contributes 80% of the previous
    const diminishingFactor = Math.pow(0.8, index);
    total += impact * diminishingFactor;
  });

  // Cap at a reasonable maximum (e.g., 5.0 for extreme cases)
  return Math.round(Math.min(total, 5.0) * 100) / 100;
}

