     1→/**
     2→ * API Client for LOOKSMAXX Backend
     3→ *
     4→ * Handles all authenticated requests to the Railway-hosted API
     5→ */
     6→
     7→import type { UserRank, LeaderboardEntry, LeaderboardData, UserProfile } from '@/types/results';
     8→import type {
     9→  Category,
    10→  SubForum,
    11→  Post,
    12→  PostListItem,
    13→  PostListResponse,
    14→  PostCreate,
    15→  PostUpdate,
    16→  Comment,
    17→  CommentCreate,
    18→  VoteType,
    19→  VoteResponse,
    20→  ReportCreate,
    21→  Report,
    22→  GuideSection,
    23→  RecommendedForum,
    24→  SortOrder,
    25→  CategoryListItem,
    26→  PostAuthor,
    27→  ForumSearchResult,
    28→  ForumSearchResponse,
    29→  SearchResultType,
    30→  ForumUserProfile,
    31→  UserPostItem,
    32→  UserPostListResponse,
    33→  UserCommentItem,
    34→  UserCommentListResponse,
    35→  BookmarkToggleResponse,
    36→  BookmarkStatus,
    37→  BookmarkedPostsResponse,
    38→  // Admin/Moderation types
    39→  ReportDetail,
    40→  AdminReportListResponse,
    41→  ResolveReportRequest,
    42→  ModerationStats,
    43→  BanUserRequest,
    44→  BanUserResponse,
    45→  BannedUser,
    46→  BannedUsersResponse,
    47→  ReportStatusEnum,
    48→  ActionTaken,
    49→  TargetType,
    50→  ReportReason,
    51→} from '@/types/forum';
    52→
    53→const API_URL = (process.env.NEXT_PUBLIC_API_URL || 'http://localhost:8000').trim();
    54→
    55→// API Response types (snake_case from backend)
    56→interface LeaderboardApiResponse {
    57→  user_id: string;
    58→  score: number;
    59→  global_rank: number;
    60→  gender_rank: number;
    61→  percentile: number;
    62→  total_users: number;
    63→  gender_total: number;
    64→  anonymous_name: string;
    65→  updated_at: string;
    66→}
    67→
    68→interface LeaderboardEntryResponse {
    69→  user_id: string;
    70→  rank: number;
    71→  score: number;
    72→  anonymous_name: string;
    73→  gender: 'male' | 'female';
    74→  face_photo_url: string | null;
    75→  is_current_user: boolean;
    76→  top_strengths: string[];
    77→  top_improvements: string[];
    78→}
    79→
    80→interface LeaderboardListResponse {
    81→  entries: LeaderboardEntryResponse[];
    82→  total_count: number;
    83→  user_rank: LeaderboardApiResponse | null;
    84→}
    85→
    86→interface UserProfileResponse {
    87→  user_id: string;
    88→  rank: number;
    89→  score: number;
    90→  anonymous_name: string;
    91→  gender: 'male' | 'female';
    92→  face_photo_url: string | null;
    93→  top_strengths: string[];
    94→  top_improvements: string[];
    95→}
    96→
    97→// Transform functions (snake_case -> camelCase)
    98→function transformUserRank(data: LeaderboardApiResponse): UserRank {
    99→  return {
   100→    userId: data.user_id,
   101→    score: data.score,
   102→    globalRank: data.global_rank,
   103→    genderRank: data.gender_rank,
   104→    percentile: data.percentile,
   105→    totalUsers: data.total_users,
   106→    genderTotal: data.gender_total,
   107→    anonymousName: data.anonymous_name,
   108→    updatedAt: data.updated_at,
   109→  };
   110→}
   111→
   112→function transformLeaderboardEntry(data: LeaderboardEntryResponse): LeaderboardEntry {
   113→  return {
   114→    userId: data.user_id,
   115→    rank: data.rank,
   116→    score: data.score,
   117→    anonymousName: data.anonymous_name,
   118→    gender: data.gender,
   119→    facePhotoUrl: data.face_photo_url,
   120→    isCurrentUser: data.is_current_user,
   121→    topStrengths: data.top_strengths || [],
   122→    topImprovements: data.top_improvements || [],
   123→  };
   124→}
   125→
   126→function transformLeaderboardData(data: LeaderboardListResponse): LeaderboardData {
   127→  return {
   128→    entries: data.entries.map(transformLeaderboardEntry),
   129→    totalCount: data.total_count,
   130→    userRank: data.user_rank ? transformUserRank(data.user_rank) : null,
   131→  };
   132→}
   133→
   134→function transformUserProfile(data: UserProfileResponse): UserProfile {
   135→  return {
   136→    userId: data.user_id,
   137→    rank: data.rank,
   138→    score: data.score,
   139→    anonymousName: data.anonymous_name,
   140→    gender: data.gender,
   141→    facePhotoUrl: data.face_photo_url,
   142→    topStrengths: data.top_strengths,
   143→    topImprovements: data.top_improvements,
   144→  };
   145→}
   146→
   147→// === FORUM API RESPONSE TYPES ===
   148→
   149→interface SubForumApiResponse {
   150→  id: string;
   151→  name: string;
   152→  slug: string;
   153→  description: string | null;
   154→  icon: string | null;
   155→  display_order: number;
   156→  post_count: number;
   157→}
   158→
   159→interface CategoryApiResponse {
   160→  id: string;
   161→  name: string;
   162→  slug: string;
   163→  description: string | null;
   164→  icon: string | null;
   165→  display_order: number;
   166→  post_count: number;
   167→  sub_forums: SubForumApiResponse[];
   168→}
   169→
   170→interface CategoryListApiResponse {
   171→  id: string;
   172→  name: string;
   173→  slug: string;
   174→  description: string | null;
   175→  icon: string | null;
   176→  display_order: number;
   177→  post_count: number;
   178→}
   179→
   180→interface PostAuthorApiResponse {
   181→  id: string;
   182→  username: string;
   183→}
   184→
   185→interface PostApiResponse {
   186→  id: string;
   187→  title: string;
   188→  content: string;
   189→  sub_forum_id: string;
   190→  sub_forum_slug: string;
   191→  category_slug: string;
   192→  author: PostAuthorApiResponse;
   193→  is_pinned: boolean;
   194→  is_guide: boolean;
   195→  vote_count: number;
   196→  comment_count: number;
