  1433→    userId: string,
  1434→    options?: { limit?: number; offset?: number }
  1435→  ): Promise<UserCommentListResponse> {
  1436→    const params = new URLSearchParams();
  1437→    if (options?.limit) params.set('limit', String(options.limit));
  1438→    if (options?.offset) params.set('offset', String(options.offset));
  1439→    const query = params.toString() ? `?${params}` : '';
  1440→
  1441→    const response = await this.request<UserCommentListApiResponse>(`/users/${userId}/comments${query}`);
  1442→    return transformUserCommentListResponse(response);
  1443→  }
  1444→
  1445→  // === ADMIN/MODERATION ===
  1446→
