  1463→    if (options?.limit) params.set('limit', String(options.limit));
  1464→    if (options?.offset) params.set('offset', String(options.offset));
  1465→    const query = params.toString() ? `?${params}` : '';
  1466→
  1467→    const response = await this.request<AdminReportListApiResponse>(`/admin/reports${query}`);
  1468→    return transformAdminReportListResponse(response);
