  1493→
  1494→  async restorePost(postId: string): Promise<{ success: boolean; post_id: string; message: string }> {
  1495→    return this.request(`/admin/posts/${postId}/restore`, { method: 'PUT' });
  1496→  }
  1497→
  1498→  async removeComment(commentId: string): Promise<{ success: boolean; comment_id: string; message: string }> {
  1499→    return this.request(`/admin/comments/${commentId}/remove`, { method: 'PUT' });
  1500→  }
  1501→
  1502→  async restoreComment(commentId: string): Promise<{ success: boolean; comment_id: string; message: string }> {
  1503→    return this.request(`/admin/comments/${commentId}/restore`, { method: 'PUT' });
  1504→  }
  1505→
  1506→  async banUser(userId: string, data?: BanUserRequest): Promise<BanUserResponse> {
