-- Migration 016: Add notifications table for forum notifications
-- This migration creates the notifications system for the forum

-- Create enum types if they don't exist
DO $$ BEGIN
    CREATE TYPE notification_type AS ENUM ('reply', 'mention', 'vote', 'system', 'milestone');
EXCEPTION
    WHEN duplicate_object THEN NULL;
END $$;

DO $$ BEGIN
    CREATE TYPE notification_source_type AS ENUM ('post', 'comment', 'system');
EXCEPTION
    WHEN duplicate_object THEN NULL;
END $$;

-- Create notifications table
CREATE TABLE IF NOT EXISTS notifications (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    user_id UUID NOT NULL REFERENCES users(id) ON DELETE CASCADE,

    -- Notification type and content
    type notification_type NOT NULL,
    title VARCHAR(200) NOT NULL,
    content TEXT,

    -- Source info (what triggered the notification)
    source_type notification_source_type,
    source_id UUID,
    post_id UUID REFERENCES forum_posts(id) ON DELETE SET NULL,

    -- Actor (who triggered the notification)
    actor_id UUID REFERENCES users(id) ON DELETE SET NULL,

    -- Link to navigate to when clicked
    link VARCHAR(500),

    -- Status
    read BOOLEAN NOT NULL DEFAULT FALSE,

    -- Timestamps
    created_at TIMESTAMP DEFAULT NOW(),
    read_at TIMESTAMP
);

-- Create indexes for performance
CREATE INDEX IF NOT EXISTS idx_notifications_user_read ON notifications(user_id, read);
CREATE INDEX IF NOT EXISTS idx_notifications_user_created ON notifications(user_id, created_at DESC);
CREATE INDEX IF NOT EXISTS idx_notifications_source ON notifications(source_type, source_id);
CREATE INDEX IF NOT EXISTS idx_notifications_actor ON notifications(actor_id);
CREATE INDEX IF NOT EXISTS idx_notifications_post ON notifications(post_id);

-- Add comment
COMMENT ON TABLE notifications IS 'User notifications for forum activity (replies, mentions, votes, system)';

