-- Migration: Add notifications table
-- Created: 2025-12-27
-- Description: Notifications system for forum activity (replies, mentions, votes, system messages)

-- Create notification type enum
DO $$ BEGIN
    CREATE TYPE notification_type AS ENUM ('reply', 'mention', 'vote', 'system', 'milestone');
EXCEPTION
    WHEN duplicate_object THEN null;
END $$;

-- Create notification source type enum
DO $$ BEGIN
    CREATE TYPE notification_source_type AS ENUM ('post', 'comment', 'system');
EXCEPTION
    WHEN duplicate_object THEN null;
END $$;

-- Create notifications table
CREATE TABLE IF NOT EXISTS notifications (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    user_id UUID NOT NULL REFERENCES users(id) ON DELETE CASCADE,

    -- Notification type and content
    type notification_type NOT NULL,
    title VARCHAR(200) NOT NULL,  -- Short title for the notification
    content TEXT,  -- Preview of the content that triggered notification (optional)

    -- Source info (what triggered the notification)
    source_type notification_source_type,
    source_id UUID,
    post_id UUID REFERENCES forum_posts(id) ON DELETE SET NULL,

    -- Actor (who triggered the notification)
    actor_id UUID REFERENCES users(id) ON DELETE SET NULL,

    -- Link to navigate to when clicked
    link VARCHAR(500),

    -- Status
    read BOOLEAN NOT NULL DEFAULT FALSE,

    -- Timestamps
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    read_at TIMESTAMP WITH TIME ZONE
);

-- Index for fetching user notifications (most common query)
CREATE INDEX IF NOT EXISTS idx_notifications_user_read
    ON notifications(user_id, read);

-- Index for fetching user notifications sorted by date
CREATE INDEX IF NOT EXISTS idx_notifications_user_created
    ON notifications(user_id, created_at DESC);

-- Index for looking up notifications by source (for deduplication)
CREATE INDEX IF NOT EXISTS idx_notifications_source
    ON notifications(source_type, source_id);

-- Index for counting unread notifications efficiently
CREATE INDEX IF NOT EXISTS idx_notifications_unread
    ON notifications(user_id)
    WHERE read = FALSE;

-- Add comment to table
COMMENT ON TABLE notifications IS 'User notifications for forum activity (replies, mentions, votes, system messages)';
COMMENT ON COLUMN notifications.type IS 'Type of notification: reply, mention, vote, system, or milestone';
COMMENT ON COLUMN notifications.title IS 'Short title for the notification';
COMMENT ON COLUMN notifications.content IS 'Preview of content that triggered notification';
COMMENT ON COLUMN notifications.source_type IS 'Type of content that triggered notification: post, comment, or system';
COMMENT ON COLUMN notifications.source_id IS 'ID of the post or comment that triggered the notification';
COMMENT ON COLUMN notifications.post_id IS 'ID of the related post (for navigation)';
COMMENT ON COLUMN notifications.actor_id IS 'User who triggered the notification (null for system notifications)';
COMMENT ON COLUMN notifications.link IS 'URL to navigate to when notification is clicked';

