/**
 * API Client for LOOKSMAXX Backend
 *
 * Handles all authenticated requests to the Railway-hosted API
 */

import type { UserRank, LeaderboardEntry, LeaderboardData, UserProfile } from '@/types/results';
import type {
  Category,
  SubForum,
  Post,
  PostListItem,
  PostListResponse,
  PostCreate,
  PostUpdate,
  Comment,
  CommentCreate,
  VoteType,
  VoteResponse,
  ReportCreate,
  Report,
  GuideSection,
  RecommendedForum,
  SortOrder,
  CategoryListItem,
  PostAuthor,
  ForumSearchResult,
  ForumSearchResponse,
  SearchResultType,
  ForumUserProfile,
  UserPostItem,
  UserPostListResponse,
  UserCommentItem,
  UserCommentListResponse,
  BookmarkToggleResponse,
  BookmarkStatus,
