/**
 * Notification Types - Matches backend Pydantic schemas
 */

// === ENUMS ===

export type NotificationType = 'reply' | 'mention' | 'vote' | 'system' | 'milestone';

export type NotificationSourceType = 'post' | 'comment' | 'system';

// === NOTIFICATION ACTOR ===

export interface NotificationActor {
  id: string;
  username: string;
}

// === NOTIFICATION ===

export interface Notification {
  id: string;
  type: NotificationType;
  title: string;
  content: string | null;
  sourceType: NotificationSourceType | null;
  sourceId: string | null;
  postId: string | null;
  actor: NotificationActor | null;
  link: string | null;
  read: boolean;
  createdAt: string;
  readAt: string | null;
}

// === LIST RESPONSE ===

export interface NotificationListResponse {
  notifications: Notification[];
  totalCount: number;
  unreadCount: number;
  hasMore: boolean;
}

// === UNREAD COUNT RESPONSE ===

export interface UnreadCountResponse {
  unreadCount: number;
}

// === ACTION RESPONSES ===

export interface MarkReadResponse {
  success: boolean;
  notificationId: string;
}

export interface MarkAllReadResponse {
  success: boolean;
  count: number;
}

export interface DeleteNotificationResponse {
  success: boolean;
  notificationId: string;
}

'use client';

import { useEffect, useState } from 'react';
import Link from 'next/link';
import { useRouter } from 'next/navigation';
import { Trophy, ShieldCheck, ArrowLeft, Search, X, Bookmark } from 'lucide-react';
import { useLeaderboardOptional } from '@/contexts/LeaderboardContext';
import { api } from '@/lib/api';

export function ForumHeader() {
  const router = useRouter();
  const leaderboard = useLeaderboardOptional();
  const userRank = leaderboard?.userRank;
  const isAuthenticated = !!api.getToken();
  const [showSearchInput, setShowSearchInput] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  // Auto-fetch user's rank on mount if authenticated
  useEffect(() => {
    if (isAuthenticated && !userRank && leaderboard?.fetchMyRank) {
      leaderboard.fetchMyRank();
    }
  }, [isAuthenticated, userRank, leaderboard]);

  // Handle search submit
  const handleSearchSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchQuery.trim().length >= 2) {
      router.push(`/forum/search?q=${encodeURIComponent(searchQuery.trim())}`);
      setShowSearchInput(false);
      setSearchQuery('');
    }
  };

  return (
    <header className="sticky top-0 z-50 bg-black/95 backdrop-blur-xl border-b border-white/5">
      <div className="max-w-6xl mx-auto px-6 h-16 flex items-center justify-between">
        <div className="flex items-center gap-6">
          <Link href="/" className="flex items-center gap-3">
            <div className="w-9 h-9 rounded-xl bg-gradient-to-br from-cyan-400 to-blue-600 flex items-center justify-center shadow-lg shadow-cyan-500/20">
              <ShieldCheck size={18} className="text-white" />
            </div>
            <span className="text-xs font-black uppercase tracking-widest text-white hidden sm:block">LOOKSMAXX</span>
          </Link>
