     1→/**
     2→ * Side Profile Detection using Image Processing
     3→ *
     4→ * Since MediaPipe doesn't work well with side profiles, this module uses
     5→ * edge detection and contour analysis to find the profile silhouette
     6→ * and estimate landmark positions.
     7→ *
     8→ * Performance optimizations:
     9→ * - Image downsampling before edge detection (reduces computation 4-9x)
    10→ */
    11→
    12→import { SIDE_PROFILE_LANDMARKS } from './landmarks';
    13→
    14→// Maximum dimension for edge detection processing
    15→const MAX_PROCESSING_SIZE = 480;
    16→
    17→interface Point {
    18→  x: number;
    19→  y: number;
    20→}
    21→
    22→interface ProfileContour {
    23→  points: Point[];
    24→  boundingBox: { x: number; y: number; width: number; height: number };
    25→  orientation: 'left' | 'right'; // Which direction the face is pointing
    26→}
    27→
    28→interface SideProfileResult {
    29→  landmarks: Array<{ id: string; x: number; y: number }>;
    30→  confidence: number;
    31→  contour: Point[];
    32→  orientation: 'left' | 'right';
    33→}
    34→
    35→/**
    36→ * Detect side profile landmarks from an image
    37→ */
    38→export async function detectSideProfile(
    39→  imageUrl: string
    40→): Promise<SideProfileResult | null> {
    41→  try {
    42→    const img = await loadImage(imageUrl);
    43→    const canvas = document.createElement('canvas');
    44→    const ctx = canvas.getContext('2d');
    45→    if (!ctx) return null;
    46→
    47→    // Downsample large images for faster edge detection
    48→    const { width, height } = downsampleDimensions(img.width, img.height, MAX_PROCESSING_SIZE);
    49→    canvas.width = width;
    50→    canvas.height = height;
    51→    ctx.imageSmoothingEnabled = true;
    52→    ctx.imageSmoothingQuality = 'high';
    53→    ctx.drawImage(img, 0, 0, width, height);
    54→
    55→    // Get image data (landmarks use normalized 0-1 coordinates, so no rescaling needed)
    56→    const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
    57→
    58→    // Step 1: Convert to grayscale
    59→    const grayscale = toGrayscale(imageData);
    60→
    61→    // Step 2: Apply Gaussian blur to reduce noise
    62→    const blurred = gaussianBlur(grayscale, canvas.width, canvas.height);
    63→
    64→    // Step 3: Edge detection (Sobel operator)
    65→    const edges = sobelEdgeDetection(blurred, canvas.width, canvas.height);
    66→
    67→    // Step 4: Find the profile contour
    68→    const contour = extractProfileContour(edges, canvas.width, canvas.height);
    69→
    70→    if (!contour || contour.points.length < 10) {
    71→      console.log('[SideProfile] Could not extract sufficient contour points');
    72→      return null;
    73→    }
    74→
    75→    // Step 5: Analyze contour to find key anchor points
    76→    const anchors = findAnchorPoints(contour);
    77→
    78→    // Step 6: Map anchors to our 28 landmarks
    79→    const landmarks = mapToLandmarks(anchors, contour);
    80→
    81→    return {
    82→      landmarks,
    83→      confidence: 0.7, // Lower confidence than MediaPipe frontal
    84→      contour: contour.points,
    85→      orientation: contour.orientation,
    86→    };
    87→  } catch (error) {
    88→    console.error('[SideProfile] Detection error:', error);
    89→    return null;
    90→  }
    91→}
    92→
    93→/**
    94→ * Load an image from URL
    95→ */
    96→function loadImage(url: string): Promise<HTMLImageElement> {
    97→  return new Promise((resolve, reject) => {
    98→    const img = new Image();
    99→    img.crossOrigin = 'anonymous';
   100→    img.onload = () => resolve(img);
   101→    img.onerror = reject;
   102→    img.src = url;
   103→  });
   104→}
   105→
   106→/**
   107→ * Calculate downsampled dimensions while maintaining aspect ratio
   108→ */
   109→function downsampleDimensions(
   110→  width: number,
   111→  height: number,
   112→  maxSize: number
   113→): { width: number; height: number } {
   114→  const maxDim = Math.max(width, height);
   115→  if (maxDim <= maxSize) {
   116→    return { width, height };
   117→  }
   118→
   119→  const scale = maxSize / maxDim;
   120→  return {
   121→    width: Math.round(width * scale),
   122→    height: Math.round(height * scale),
   123→  };
   124→}
   125→
   126→/**
   127→ * Convert image data to grayscale array
   128→ */
   129→function toGrayscale(imageData: ImageData): Uint8Array {
   130→  const data = imageData.data;
   131→  const gray = new Uint8Array(data.length / 4);
   132→
   133→  for (let i = 0; i < gray.length; i++) {
   134→    const idx = i * 4;
   135→    // Luminosity method for grayscale
   136→    gray[i] = Math.round(0.299 * data[idx] + 0.587 * data[idx + 1] + 0.114 * data[idx + 2]);
   137→  }
   138→
   139→  return gray;
   140→}
   141→
   142→/**
   143→ * Apply 3x3 Gaussian blur
   144→ */
   145→function gaussianBlur(gray: Uint8Array, width: number, height: number): Uint8Array {
   146→  const kernel = [
   147→    1/16, 2/16, 1/16,
   148→    2/16, 4/16, 2/16,
   149→    1/16, 2/16, 1/16
   150→  ];
   151→
   152→  const result = new Uint8Array(gray.length);
   153→
   154→  for (let y = 1; y < height - 1; y++) {
   155→    for (let x = 1; x < width - 1; x++) {
   156→      let sum = 0;
   157→      let k = 0;
   158→      for (let ky = -1; ky <= 1; ky++) {
   159→        for (let kx = -1; kx <= 1; kx++) {
   160→          sum += gray[(y + ky) * width + (x + kx)] * kernel[k++];
   161→        }
   162→      }
   163→      result[y * width + x] = sum;
   164→    }
   165→  }
   166→
   167→  return result;
   168→}
   169→
   170→/**
   171→ * Sobel edge detection
   172→ */
   173→function sobelEdgeDetection(gray: Uint8Array, width: number, height: number): Uint8Array {
   174→  const sobelX = [-1, 0, 1, -2, 0, 2, -1, 0, 1];
   175→  const sobelY = [-1, -2, -1, 0, 0, 0, 1, 2, 1];
   176→
   177→  const edges = new Uint8Array(gray.length);
   178→
   179→  for (let y = 1; y < height - 1; y++) {
   180→    for (let x = 1; x < width - 1; x++) {
   181→      let gx = 0, gy = 0;
   182→      let k = 0;
   183→
   184→      for (let ky = -1; ky <= 1; ky++) {
   185→        for (let kx = -1; kx <= 1; kx++) {
   186→          const pixel = gray[(y + ky) * width + (x + kx)];
   187→          gx += pixel * sobelX[k];
   188→          gy += pixel * sobelY[k];
   189→          k++;
   190→        }
   191→      }
   192→
   193→      // Magnitude
   194→      edges[y * width + x] = Math.min(255, Math.sqrt(gx * gx + gy * gy));
   195→    }
   196→  }
   197→
   198→  return edges;
   199→}
   200→
   201→/**
   202→ * Extract the profile contour from edge image
   203→ * Looks for the strongest vertical edge on each side
   204→ */
   205→function extractProfileContour(
   206→  edges: Uint8Array,
   207→  width: number,
   208→  height: number
   209→): ProfileContour | null {
   210→  // Determine if face is on left or right side of image
   211→  let leftEdgeSum = 0;
   212→  let rightEdgeSum = 0;
   213→
   214→  const midX = width / 2;
   215→
   216→  for (let y = 0; y < height; y++) {
   217→    for (let x = 0; x < width; x++) {
   218→      const val = edges[y * width + x];
   219→      if (x < midX) {
   220→        leftEdgeSum += val;
   221→      } else {
   222→        rightEdgeSum += val;
   223→      }
   224→    }
   225→  }
   226→
   227→  // Face is likely on the side with more edge activity
   228→  const faceOnLeft = leftEdgeSum > rightEdgeSum;
   229→  const orientation: 'left' | 'right' = faceOnLeft ? 'left' : 'right'; // profile edge direction
   230→
   231→  // Find the profile edge - scan from the face side inward
   232→  const contourPoints: Point[] = [];
   233→  const threshold = 30; // Edge threshold
   234→
   235→  // Scan each row to find the profile edge
   236→  for (let y = Math.floor(height * 0.1); y < Math.floor(height * 0.95); y++) {
   237→    let edgeX = -1;
   238→    let maxEdge = threshold;
   239→
   240→    if (faceOnLeft) {
   241→      // Scan from left, find rightmost strong edge in left half
   242→      for (let x = 0; x < midX; x++) {
   243→        if (edges[y * width + x] > maxEdge) {
   244→          maxEdge = edges[y * width + x];
   245→          edgeX = x;
   246→        }
   247→      }
   248→    } else {
   249→      // Scan from right, find leftmost strong edge in right half
   250→      for (let x = width - 1; x >= midX; x--) {
   251→        if (edges[y * width + x] > maxEdge) {
   252→          maxEdge = edges[y * width + x];
   253→          edgeX = x;
   254→        }
   255→      }
   256→    }
   257→
   258→    if (edgeX !== -1) {
   259→      contourPoints.push({ x: edgeX / width, y: y / height });
   260→    }
   261→  }
   262→
   263→  if (contourPoints.length < 10) return null;
   264→
   265→  // Smooth the contour using moving average
   266→  const smoothedPoints = smoothContour(contourPoints, 5);
   267→
   268→  // Calculate bounding box
   269→  let minX = 1, minY = 1, maxX = 0, maxY = 0;
   270→  smoothedPoints.forEach(p => {
   271→    minX = Math.min(minX, p.x);
   272→    minY = Math.min(minY, p.y);
   273→    maxX = Math.max(maxX, p.x);
   274→    maxY = Math.max(maxY, p.y);
   275→  });
   276→
   277→  return {
   278→    points: smoothedPoints,
   279→    boundingBox: { x: minX, y: minY, width: maxX - minX, height: maxY - minY },
   280→    orientation,
   281→  };
   282→}
   283→
   284→/**
   285→ * Smooth contour points using moving average
   286→ */
   287→function smoothContour(points: Point[], windowSize: number): Point[] {
   288→  const smoothed: Point[] = [];
   289→  const half = Math.floor(windowSize / 2);
   290→
   291→  for (let i = 0; i < points.length; i++) {
   292→    let sumX = 0, sumY = 0, count = 0;
   293→
   294→    for (let j = -half; j <= half; j++) {
   295→      const idx = i + j;
   296→      if (idx >= 0 && idx < points.length) {
   297→        sumX += points[idx].x;
   298→        sumY += points[idx].y;
   299→        count++;
   300→      }
   301→    }
   302→
   303→    smoothed.push({ x: sumX / count, y: sumY / count });
   304→  }
   305→
   306→  return smoothed;
   307→}
   308→
   309→interface AnchorPoints {
   310→  vertex: Point;      // Top of head
   311→  forehead: Point;    // Forehead profile
   312→  glabella: Point;    // Between brows
   313→  nasion: Point;      // Nasal root
   314→  noseTip: Point;     // Pronasale
   315→  subnasale: Point;   // Base of nose
   316→  upperLip: Point;    // Labrale superius
   317→  lowerLip: Point;    // Labrale inferius
   318→  chin: Point;        // Pogonion
   319→  menton: Point;      // Bottom of chin
   320→  neckPoint: Point;   // Neck
   321→  jawAngle: Point;    // Gonion
   322→}
   323→
   324→/**
   325→ * Find anchor points along the profile contour
   326→ */
   327→function findAnchorPoints(
   328→  contour: ProfileContour
   329→): AnchorPoints {
   330→  const points = contour.points;
   331→
   332→  // Helper to find point at approximate Y position
   333→  const findPointAtY = (targetY: number): Point => {
   334→    let closest = points[0];
   335→    let minDist = Math.abs(points[0].y - targetY);
   336→
   337→    for (const p of points) {
   338→      const dist = Math.abs(p.y - targetY);
   339→      if (dist < minDist) {
   340→        minDist = dist;
   341→        closest = p;
   342→      }
   343→    }
   344→    return closest;
   345→  };
   346→
   347→  // Helper to find the most protruding point in a Y range
   348→  const findProtrudingPoint = (yStart: number, yEnd: number, direction: 'left' | 'right'): Point => {
   349→    let best = points[0];
   350→
   351→    for (const p of points) {
   352→      if (p.y >= yStart && p.y <= yEnd) {
   353→        if (direction === 'left' && p.x < best.x) best = p;
   354→        if (direction === 'right' && p.x > best.x) best = p;
   355→      }
   356→    }
   357→    return best;
   358→  };
   359→
   360→  // Find key inflection points based on profile geometry
   361→  const isLeftFacing = contour.orientation === 'left';
   362→  const protrusion = isLeftFacing ? 'left' : 'right';
   363→
   364→  // Divide face into regions based on typical proportions
   365→  const vertex = findPointAtY(0.05);
   366→  const forehead = findPointAtY(0.15);
   367→  const glabella = findPointAtY(0.28);
   368→  const nasion = findPointAtY(0.32);
   369→
   370→  // Nose tip is usually the most protruding point between y=0.35 and y=0.55
   371→  const noseTip = findProtrudingPoint(0.35, 0.55, protrusion);
   372→
   373→  const subnasale = findPointAtY(0.58);
   374→  const upperLip = findPointAtY(0.62);
   375→  const lowerLip = findPointAtY(0.68);
   376→
   377→  // Chin (pogonion) is often protruding
   378→  const chin = findProtrudingPoint(0.75, 0.85, protrusion);
   379→
   380→  const menton = findPointAtY(0.88);
   381→  const neckPoint = findPointAtY(0.95);
   382→
   383→  // Jaw angle - find where contour curves from vertical to horizontal
   384→  // This is typically around y=0.7-0.8, and set back from the chin
   385→  const jawAngle = findPointAtY(0.75);
   386→
   387→  return {
   388→    vertex,
   389→    forehead,
   390→    glabella,
   391→    nasion,
   392→    noseTip,
   393→    subnasale,
   394→    upperLip,
   395→    lowerLip,
   396→    chin,
   397→    menton,
   398→    neckPoint,
   399→    jawAngle,
   400→  };
   401→}
   402→
   403→/**
   404→ * Map anchor points to our 28 side profile landmarks
   405→ */
   406→function mapToLandmarks(
   407→  anchors: AnchorPoints,
   408→  contour: ProfileContour
   409→): Array<{ id: string; x: number; y: number }> {
   410→  const isLeft = contour.orientation === 'left';
   411→
   412→  // Helper to interpolate between two points
   413→  const lerp = (p1: Point, p2: Point, t: number): Point => ({
   414→    x: p1.x + (p2.x - p1.x) * t,
   415→    y: p1.y + (p2.y - p1.y) * t,
   416→  });
   417→
   418→  // Helper to offset a point horizontally (for ear/back of head estimation)
   419→  const offsetX = (p: Point, offset: number): Point => ({
   420→    x: p.x + (isLeft ? offset : -offset),
   421→    y: p.y,
   422→  });
   423→
   424→  // Map each of our 28 landmarks
   425→  // Standard cephalometric order: vertex, occiput, pronasale, neckPoint, porion, orbitale,
   426→  // tragus, intertragicNotch, cornealApex, cheekbone, trichion, glabella, nasion,
   427→  // rhinion, supratip, infratip, columella, subnasale, subalare, labraleSuperius,
   428→  // cheilion, labraleInferius, sublabiale, pogonion, menton, cervicalPoint, gonionTop, gonionBottom
   429→
   430→  const landmarkPositions: Record<string, Point> = {
   431→    // 1. vertex - top of head
   432→    vertex: anchors.vertex,
   433→
   434→    // 2. occiput - back of head (estimate based on vertex, offset back)
   435→    occiput: offsetX(anchors.vertex, 0.15),
   436→
   437→    // 3. pronasale - nose tip
   438→    pronasale: anchors.noseTip,
   439→
   440→    // 4. neckPoint - lower neck
   441→    neckPoint: anchors.neckPoint,
   442→
   443→    // 5. porion - ear canal (estimate based on jaw angle, offset back)
   444→    porion: offsetX({ x: anchors.jawAngle.x, y: anchors.nasion.y }, 0.08),
   445→
   446→    // 6. orbitale - lowest orbital rim (between glabella and nasion level)
   447→    orbitale: { x: lerp(anchors.glabella, anchors.nasion, 0.5).x, y: anchors.nasion.y - 0.02 },
   448→
   449→    // 7. tragus - ear cartilage
   450→    tragus: offsetX({ x: anchors.jawAngle.x, y: lerp(anchors.nasion, anchors.jawAngle, 0.3).y }, 0.06),
   451→
   452→    // 8. intertragicNotch - notch in ear
   453→    intertragicNotch: offsetX({ x: anchors.jawAngle.x, y: lerp(anchors.nasion, anchors.jawAngle, 0.5).y }, 0.07),
   454→
   455→    // 9. cornealApex - forward point of cornea
   456→    cornealApex: { x: anchors.glabella.x + (isLeft ? -0.02 : 0.02), y: anchors.glabella.y + 0.02 },
   457→
   458→    // 10. cheekbone - zygomatic prominence
   459→    cheekbone: lerp(anchors.nasion, anchors.jawAngle, 0.4),
   460→
   461→    // 11. trichion - hairline
   462→    trichion: anchors.forehead,
   463→
   464→    // 12. glabella - between brows
   465→    glabella: anchors.glabella,
   466→
   467→    // 13. nasion - nasal root
   468→    nasion: anchors.nasion,
   469→
   470→    // 14. rhinion - mid-dorsum of nose
   471→    rhinion: lerp(anchors.nasion, anchors.noseTip, 0.4),
   472→
   473→    // 15. supratip - above nose tip
   474→    supratip: lerp(anchors.nasion, anchors.noseTip, 0.75),
   475→
   476→    // 16. infratip - below nose tip
   477→    infratip: lerp(anchors.noseTip, anchors.subnasale, 0.3),
   478→
   479→    // 17. columella - nasal septum
   480→    columella: lerp(anchors.noseTip, anchors.subnasale, 0.5),
   481→
   482→    // 18. subnasale - base of nose
   483→    subnasale: anchors.subnasale,
   484→
   485→    // 19. subalare - nostril wing
   486→    subalare: { x: anchors.subnasale.x + (isLeft ? -0.02 : 0.02), y: anchors.subnasale.y - 0.01 },
   487→
   488→    // 20. labraleSuperius - upper lip
   489→    labraleSuperius: anchors.upperLip,
   490→
   491→    // 21. cheilion - mouth corner
   492→    cheilion: { x: lerp(anchors.upperLip, anchors.jawAngle, 0.3).x, y: lerp(anchors.upperLip, anchors.lowerLip, 0.5).y },
   493→
   494→    // 22. labraleInferius - lower lip
   495→    labraleInferius: anchors.lowerLip,
   496→
   497→    // 23. sublabiale - below lower lip
   498→    sublabiale: lerp(anchors.lowerLip, anchors.chin, 0.3),
   499→
   500→    // 24. pogonion - chin prominence
   501→    pogonion: anchors.chin,
   502→
   503→    // 25. menton - bottom of chin
   504→    menton: anchors.menton,
   505→
   506→    // 26. cervicalPoint - high neck point
   507→    cervicalPoint: lerp(anchors.menton, anchors.neckPoint, 0.5),
   508→
   509→    // 27. gonionTop - upper jaw angle
   510→    gonionTop: { x: anchors.jawAngle.x, y: anchors.jawAngle.y - 0.03 },
   511→
   512→    // 28. gonionBottom - lower jaw angle
   513→    gonionBottom: anchors.jawAngle,
   514→  };
   515→
   516→  // Convert to array matching SIDE_PROFILE_LANDMARKS order
   517→  return SIDE_PROFILE_LANDMARKS.map(lm => {
   518→    const pos = landmarkPositions[lm.id] || { x: 0.5, y: 0.5 };
   519→    return {
   520→      id: lm.id,
   521→      x: Math.max(0, Math.min(1, pos.x)),
   522→      y: Math.max(0, Math.min(1, pos.y)),
   523→    };
   524→  });
   525→}
   526→
   527→/**
   528→ * Debug: Draw the detected contour on a canvas
   529→ */
   530→export function debugDrawContour(
   531→  canvas: HTMLCanvasElement,
   532→  contour: Point[],
   533→  landmarks: Array<{ id: string; x: number; y: number }>
   534→): void {
   535→  const ctx = canvas.getContext('2d');
   536→  if (!ctx) return;
   537→
   538→  const width = canvas.width;
   539→  const height = canvas.height;
   540→
   541→  // Draw contour
   542→  ctx.strokeStyle = '#00ff00';
   543→  ctx.lineWidth = 2;
   544→  ctx.beginPath();
   545→  contour.forEach((p, i) => {
   546→    const x = p.x * width;
   547→    const y = p.y * height;
   548→    if (i === 0) ctx.moveTo(x, y);
   549→    else ctx.lineTo(x, y);
   550→  });
   551→  ctx.stroke();
   552→
   553→  // Draw landmarks
   554→  landmarks.forEach(lm => {
   555→    ctx.fillStyle = '#ff0000';
   556→    ctx.beginPath();
   557→    ctx.arc(lm.x * width, lm.y * height, 4, 0, Math.PI * 2);
   558→    ctx.fill();
   559→
   560→    ctx.fillStyle = '#ffffff';
   561→    ctx.font = '10px sans-serif';
   562→    ctx.fillText(lm.id, lm.x * width + 6, lm.y * height);
   563→  });
   564→}
   565→

</system-reminder>

