     1→/**
     2→ * Side Profile Normalization Utilities - FaceIQ Parity Implementation
     3→ * 
     4→ * This module replicates FaceIQ's exact side profile normalization algorithm
     5→ * for 100% parity with their landmark detection and visualization.
     6→ * 
     7→ * FaceIQ Algorithm (reverse-engineered):
     8→ * - Uses landmark indices 10, 26, 75 for rotation calculation
     9→ * - Left profile: rotate based on landmarks[75] → landmarks[26]
    10→ * - Right profile: rotate based on landmarks[10] → landmarks[26]
    11→ * - Pivot point: landmarks[10] for right, landmarks[26] for left
    12→ * - Crops to 1024×1024 with 1.12 scale factor and 0.65 height ratio
    13→ */
    14→
    15→export interface Point2D {
    16→    x: number;
    17→    y: number;
    18→}
    19→
    20→export interface FaceIQNormalizationResult {
    21→    /** Rotation angle applied (in radians) */
    22→    rotationAngle: number;
    23→    /** Rotation angle in degrees */
    24→    rotationAngleDeg: number;
    25→    /** Direction the face is pointing */
    26→    direction: 'left' | 'right';
    27→    /** Pivot point used for rotation */
    28→    pivot: Point2D;
    29→    /** Rotated landmarks */
    30→    rotatedLandmarks: Array<{ id: string; x: number; y: number }>;
    31→    /** Rotated image as data URL (if image provided) */
    32→    rotatedImageUrl?: string;
    33→    /** Crop info */
    34→    cropInfo: {
    35→        x: number;
    36→        y: number;
    37→        size: number;
    38→        scale: number;
    39→    };
    40→}
    41→
    42→/**
    43→ * FaceIQ's landmark indices for rotation calculation
    44→ * 
    45→ * These correspond to face contour positions in their model:
    46→ * - Index 10: Upper face contour (forehead/temple area) - right anchor
    47→ * - Index 26: Mid-face contour (jaw level) - common reference
    48→ * - Index 75: Upper face contour (mirrored) - left anchor
    49→ * 
    50→ * Our InsightFace 106 mapping:
    51→ * - Index 10 → InsightFace contour point ~9-10 (right side)
    52→ * - Index 26 → InsightFace contour point ~25-26 (mid contour)
    53→ * - Index 75 → InsightFace right eye area ~71-75
    54→ */
    55→
    56→/**
    57→ * FaceIQ to our raw landmark array index mapping (documentation only)
    58→ * These indices map to the raw landmarks array returned by the API
    59→ *
    60→ * @internal Used for reference - actual implementation uses direct indices
    61→ */
    62→// eslint-disable-next-line @typescript-eslint/no-unused-vars
    63→const _FACEIQ_INDEX_REFERENCE = {
    64→    10: 'faceContour_10',  // Right anchor for rotation
    65→    26: 'faceContour_26',  // Common reference point
    66→    75: 'rightEyeLowerInner',  // Left anchor (near eye for left-facing)
    67→} as const;
    68→
    69→/**
    70→ * Calculate rotation angle using FaceIQ's exact algorithm
    71→ * 
    72→ * @param landmarks - Raw landmarks array from API
    73→ * @param direction - Face direction ("left" or "right")
    74→ * @returns Rotation angle in radians
    75→ */
    76→export function calculateFaceIQRotationAngle(
    77→    landmarks: Array<{ x: number; y: number }>,
    78→    direction: 'left' | 'right'
    79→): number {
    80→    // FaceIQ algorithm:
    81→    // anchor = (direction === "left") ? landmarks[75] : landmarks[10];
    82→    // reference = landmarks[26];
    83→    // angle = Math.atan2(reference.y - anchor.y, reference.x - anchor.x);
    84→
    85→    const anchor = direction === 'left' ? landmarks[75] : landmarks[10];
    86→    const reference = landmarks[26];
    87→
    88→    if (!anchor || !reference) {
    89→        console.warn('[FaceIQ Normalization] Missing landmark indices 10, 26, or 75');
    90→        return 0;
    91→    }
    92→
    93→    const angle = Math.atan2(reference.y - anchor.y, reference.x - anchor.x);
    94→    return angle;
    95→}
    96→
    97→/**
    98→ * Get the rotation pivot point using FaceIQ's logic
    99→ * 
   100→ * @param landmarks - Raw landmarks array
   101→ * @param direction - Face direction
   102→ * @returns Pivot point coordinates
   103→ */
   104→export function getFaceIQPivot(
   105→    landmarks: Array<{ x: number; y: number }>,
   106→    direction: 'left' | 'right'
   107→): Point2D {
   108→    // FaceIQ: pivot = (direction === "right") ? landmarks[10] : landmarks[26]
   109→    const pivotLandmark = direction === 'right' ? landmarks[10] : landmarks[26];
   110→
   111→    if (!pivotLandmark) {
   112→        return { x: 0.5, y: 0.5 };
   113→    }
   114→
   115→    return { x: pivotLandmark.x, y: pivotLandmark.y };
   116→}
   117→
   118→/**
   119→ * Rotate a point around a pivot using FaceIQ's formula
   120→ * 
   121→ * FaceIQ's rotation function:
   122→ * ```
   123→ * let j = e => {
   124→ *   let t = Math.sin(-g), i = Math.cos(-g), a = e.x - f, s = e.y - b;
   125→ *   return { x: f + a * i - s * t, y: b + a * t + s * i };
   126→ * };
   127→ * ```
   128→ */
   129→export function rotatePointFaceIQ(
   130→    point: Point2D,
   131→    pivot: Point2D,
   132→    angleRad: number
   133→): Point2D {
   134→    // FaceIQ rotates by NEGATIVE angle
   135→    const negAngle = -angleRad;
   136→    const sin = Math.sin(negAngle);
   137→    const cos = Math.cos(negAngle);
   138→
   139→    const dx = point.x - pivot.x;
   140→    const dy = point.y - pivot.y;
   141→
   142→    return {
   143→        x: pivot.x + dx * cos - dy * sin,
   144→        y: pivot.y + dx * sin + dy * cos,
   145→    };
   146→}
   147→
   148→/**
   149→ * Calculate crop dimensions using FaceIQ's formula
   150→ * 
   151→ * FaceIQ algorithm:
   152→ * - croppedHeight = bboxHeight * 1.12
   153→ * - cropSize = croppedHeight / 0.65
   154→ * - Constrained between 32 and 2 * max(width, height)
   155→ */
   156→export function calculateFaceIQCropSize(
   157→    bboxHeight: number,
   158→    imageWidth: number,
   159→    imageHeight: number
   160→): number {
   161→    const scaledHeight = bboxHeight * 1.12;
   162→    const cropSize = scaledHeight / 0.65;
   163→
   164→    // FaceIQ constraints
   165→    const minSize = 32;
   166→    const maxSize = 2 * Math.max(imageWidth, imageHeight);
   167→
   168→    return Math.min(Math.max(minSize, cropSize), maxSize);
   169→}
   170→
   171→/**
   172→ * Apply FaceIQ-exact normalization to named landmarks
   173→ * 
   174→ * This function works with our named landmark format and applies
   175→ * the FaceIQ rotation algorithm.
   176→ * 
   177→ * @param namedLandmarks - Landmarks as { id: string; x: number; y: number }[]
   178→ * @param rawLandmarks - Raw landmark array (for indices 10, 26, 75)
   179→ * @param direction - Face direction
   180→ * @param bbox - Bounding box for crop calculation
   181→ */
   182→export function applyFaceIQNormalization(
   183→    namedLandmarks: Array<{ id: string; x: number; y: number }>,
   184→    rawLandmarks: Array<{ x: number; y: number }>,
   185→    direction: 'left' | 'right',
   186→    bbox?: { x: number; y: number; width: number; height: number }
   187→): FaceIQNormalizationResult {
   188→    // Calculate rotation angle using FaceIQ's algorithm
   189→    const rotationAngle = calculateFaceIQRotationAngle(rawLandmarks, direction);
   190→    const rotationAngleDeg = rotationAngle * (180 / Math.PI);
   191→
   192→    // Get pivot point
   193→    const pivot = getFaceIQPivot(rawLandmarks, direction);
   194→
   195→    // Skip rotation if angle is very small (< 2 degrees)
   196→    if (Math.abs(rotationAngleDeg) < 2) {
   197→        return {
   198→            rotationAngle: 0,
   199→            rotationAngleDeg: 0,
   200→            direction,
   201→            pivot,
   202→            rotatedLandmarks: namedLandmarks,
   203→            cropInfo: {
   204→                x: 0,
   205→                y: 0,
   206→                size: 1024,
   207→                scale: 1,
   208→            },
   209→        };
   210→    }
   211→
   212→    // Rotate all named landmarks
   213→    const rotatedLandmarks = namedLandmarks.map(landmark => {
   214→        const rotated = rotatePointFaceIQ(
   215→            { x: landmark.x, y: landmark.y },
   216→            pivot,
   217→            rotationAngle
   218→        );
   219→        return {
   220→            id: landmark.id,
   221→            x: rotated.x,
   222→            y: rotated.y,
   223→        };
   224→    });
   225→
   226→    // Calculate crop info
   227→    const cropSize = bbox
   228→        ? calculateFaceIQCropSize(bbox.height, 1, 1)  // Normalized
   229→        : 1024;
   230→
   231→    console.log(`[FaceIQ Normalization] Direction: ${direction}, Rotation: ${rotationAngleDeg.toFixed(1)}°`);
   232→
   233→    return {
   234→        rotationAngle,
   235→        rotationAngleDeg,
   236→        direction,
   237→        pivot,
   238→        rotatedLandmarks,
   239→        cropInfo: {
   240→            x: pivot.x - cropSize / 2,
   241→            y: pivot.y - cropSize / 2,
   242→            size: cropSize,
   243→            scale: 1024 / cropSize,
   244→        },
   245→    };
   246→}
   247→
   248→/**
   249→ * Simplified wrapper for the detection flow
   250→ * 
   251→ * This is a drop-in replacement for applyFrankfortCorrection that uses
   252→ * FaceIQ's algorithm instead.
   253→ * 
   254→ * @param landmarks - Named landmarks from detection
   255→ * @param rawLandmarks - Raw landmark array (if available)
   256→ * @param direction - Face direction
   257→ * @returns Normalized landmarks
   258→ */
   259→export function applyFaceIQCorrection(
   260→    landmarks: Array<{ id: string; x: number; y: number }>,
   261→    rawLandmarks?: Array<{ x: number; y: number }>,
   262→    direction?: 'left' | 'right'
   263→): Array<{ id: string; x: number; y: number }> {
   264→    // If we don't have raw landmarks or direction, fall back to Frankfort-based
   265→    if (!rawLandmarks || rawLandmarks.length < 76 || !direction) {
   266→        console.log('[FaceIQ Normalization] Missing raw landmarks, using Frankfort fallback');
   267→        return applyFrankfortCorrectionFallback(landmarks);
   268→    }
   269→
   270→    const result = applyFaceIQNormalization(landmarks, rawLandmarks, direction);
   271→    return result.rotatedLandmarks;
   272→}
   273→
   274→/**
   275→ * Fallback to our original Frankfort-based correction
   276→ * when FaceIQ indices are not available
   277→ */
   278→function applyFrankfortCorrectionFallback(
   279→    landmarks: Array<{ id: string; x: number; y: number }>
   280→): Array<{ id: string; x: number; y: number }> {
   281→    const orbitale = landmarks.find(l => l.id === 'orbitale');
   282→    const porion = landmarks.find(l => l.id === 'porion');
   283→
   284→    if (!orbitale || !porion) {
   285→        return landmarks;
   286→    }
   287→
   288→    // Calculate Frankfort angle
   289→    const dx = porion.x - orbitale.x;
   290→    const dy = porion.y - orbitale.y;
   291→    const angle = Math.atan2(dy, dx);
   292→
   293→    if (Math.abs(angle) < 0.035) { // ~2 degrees
   294→        return landmarks;
   295→    }
   296→
   297→    const pivot: Point2D = {
   298→        x: (orbitale.x + porion.x) / 2,
   299→        y: (orbitale.y + porion.y) / 2,
   300→    };
   301→
   302→    return landmarks.map(landmark => {
   303→        const cos = Math.cos(-angle);
   304→        const sin = Math.sin(-angle);
   305→        const dx = landmark.x - pivot.x;
   306→        const dy = landmark.y - pivot.y;
   307→
   308→        return {
