// Deobfuscated & Explained
function calculateAndRenderScore(ratioData, userValue) {
    // 1. Setup Parameters
    const idealMin = ratioData.idealMin;
    const idealMax = ratioData.idealMax;
    const decayRate = ratioData.decayRate ?? 0.2; // Default decay if missing
    const customPoints = ratioData.curveConfig?.customPoints;

    // 2. Calculate Score Boundaries (The "Chart Range")
    // likely returns { min: 0, max: 10 } or similar scaled range
    const boundaries = calculateScoreBoundaries(idealMin, idealMax, decayRate, customPoints, InterpolationFn);
    let chartMin = boundaries.min;
    let chartMax = boundaries.max;

    // 3. Expand Chart Range to include User Value if it's outside
    if (typeof ratioData.value === "number") {
        if (ratioData.value < chartMin) chartMin = ratioData.value;
        if (ratioData.value > chartMax) chartMax = ratioData.value;
    }

    // 4. Calculate Percentage Position for Slider
    const totalRange = chartMax - chartMin;
    // Percentage = (UserValue - TotalMin) / TotalRange * 100
    // Clamped between 0 and 100
    const sliderPositionPercent = totalRange > 0
        ? Math.max(0, Math.min(100, (userValue - chartMin) / totalRange * 100))
        : 50; // Default to middle if range is 0

    // 5. Handle "Obfuscation" (Blurring results for unpaid users)
    const isLocked = !isRatioUnlocked(ratioData.name);
    const displayPosition = isLocked
        ? getObfuscatedSliderPosition(...) // Random/Fuzzy position if locked
        : sliderPositionPercent;           // Real position if unlocked

    // 6. Determine "Ideal" Status
    // Simple boolean check: Is value inside the [IdealMin, IdealMax] range?
    const isWithinIdeal = userValue >= idealMin && userValue <= idealMax;

    return {
        sliderPosition: displayPosition,
        isIdeal: isWithinIdeal
    };
}

