     1→"use client";
     2→
     3→import { useState, useEffect, useCallback, Suspense } from "react";
     4→import { useRouter, useSearchParams } from "next/navigation";
     5→import Link from "next/link";
     6→import { Eye, EyeOff } from "lucide-react";
     7→import { api } from "@/lib/api";
     8→
     9→function CheckIcon({ className }: { className?: string }) {
    10→  return (
    11→    <svg className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    12→      <polyline points="20 6 9 17 4 12" />
    13→    </svg>
    14→  );
    15→}
    16→
    17→function XIcon({ className }: { className?: string }) {
    18→  return (
    19→    <svg className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    20→      <line x1="18" y1="6" x2="6" y2="18" />
    21→      <line x1="6" y1="6" x2="18" y2="18" />
    22→    </svg>
    23→  );
    24→}
    25→
    26→function LoaderIcon({ className }: { className?: string }) {
    27→  return (
    28→    <svg className={`${className} animate-spin`} viewBox="0 0 24 24" fill="none">
    29→      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
    30→      <path
    31→        className="opacity-75"
    32→        fill="currentColor"
    33→        d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
    34→      />
    35→    </svg>
    36→  );
    37→}
    38→
    39→function SignupForm() {
    40→  const router = useRouter();
    41→  const searchParams = useSearchParams();
    42→
    43→  const [formData, setFormData] = useState({
    44→    email: "",
    45→    password: "",
    46→    confirmPassword: "",
    47→    username: "",
    48→    referralCode: "",
    49→    termsAccepted: false,
    50→  });
    51→  const [usernameStatus, setUsernameStatus] = useState<{
    52→    checking: boolean;
    53→    available: boolean | null;
    54→    reason: string | null;
    55→  }>({ checking: false, available: null, reason: null });
    56→  const [referralStatus, setReferralStatus] = useState<{
    57→    checking: boolean;
    58→    valid: boolean | null;
    59→    message: string | null;
    60→  }>({ checking: false, valid: null, message: null });
    61→  const [error, setError] = useState("");
    62→  const [isLoading, setIsLoading] = useState(false);
    63→  const [showPassword, setShowPassword] = useState(false);
    64→  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
    65→
    66→  // Pre-fill referral code from URL if present
    67→  useEffect(() => {
    68→    const ref = searchParams.get("ref") || searchParams.get("referral");
    69→    if (ref) {
    70→      setFormData(prev => ({ ...prev, referralCode: ref.toUpperCase() }));
    71→    }
    72→  }, [searchParams]);
    73→
    74→  // Debounced username check
    75→  const checkUsernameAvailability = useCallback(async (username: string) => {
    76→    if (username.length < 3) {
    77→      setUsernameStatus({ checking: false, available: false, reason: "Too short" });
    78→      return;
    79→    }
    80→
    81→    setUsernameStatus({ checking: true, available: null, reason: null });
    82→
    83→    try {
    84→      const result = await api.checkUsername(username);
    85→      setUsernameStatus({
    86→        checking: false,
    87→        available: result.available,
    88→        reason: result.reason,
    89→      });
    90→    } catch {
    91→      setUsernameStatus({ checking: false, available: null, reason: "Check failed" });
    92→    }
    93→  }, []);
    94→
    95→  // Debounced referral code validation
    96→  const validateReferralCode = useCallback(async (code: string) => {
    97→    if (!code) {
    98→      setReferralStatus({ checking: false, valid: null, message: null });
    99→      return;
   100→    }
   101→
   102→    setReferralStatus({ checking: true, valid: null, message: null });
   103→
   104→    try {
   105→      const result = await api.validateReferralCode(code);
   106→      setReferralStatus({
   107→        checking: false,
   108→        valid: result.valid,
   109→        message: result.message,
   110→      });
   111→    } catch {
   112→      setReferralStatus({ checking: false, valid: null, message: "Validation failed" });
   113→    }
   114→  }, []);
   115→
   116→  useEffect(() => {
   117→    if (formData.username.length >= 3) {
   118→      const timer = setTimeout(() => {
   119→        checkUsernameAvailability(formData.username);
   120→      }, 500);
   121→      return () => clearTimeout(timer);
   122→    } else if (formData.username.length > 0) {
   123→      setUsernameStatus({ checking: false, available: false, reason: "Too short" });
   124→    } else {
   125→      setUsernameStatus({ checking: false, available: null, reason: null });
   126→    }
   127→  }, [formData.username, checkUsernameAvailability]);
   128→
   129→  useEffect(() => {
   130→    if (formData.referralCode.length >= 3) {
   131→      const timer = setTimeout(() => {
   132→        validateReferralCode(formData.referralCode);
   133→      }, 500);
   134→      return () => clearTimeout(timer);
   135→    } else {
   136→      setReferralStatus({ checking: false, valid: null, message: null });
   137→    }
   138→  }, [formData.referralCode, validateReferralCode]);
   139→
   140→  const handleSubmit = async (e: React.FormEvent) => {
   141→    e.preventDefault();
   142→    setError("");
   143→
   144→    // Validation
   145→    if (!formData.username || usernameStatus.available !== true) {
   146→      setError("Please choose a valid, available username");
   147→      return;
   148→    }
   149→
   150→    if (formData.password.length < 8) {
   151→      setError("Password must be at least 8 characters");
   152→      return;
   153→    }
   154→
   155→    if (formData.password !== formData.confirmPassword) {
   156→      setError("Passwords do not match");
   157→      return;
   158→    }
   159→
   160→    if (!formData.termsAccepted) {
   161→      setError("You must accept the Terms & Conditions");
   162→      return;
   163→    }
   164→
   165→    // If referral code is provided but invalid, warn but allow signup
   166→    if (formData.referralCode && referralStatus.valid === false) {
   167→      setError("The referral code is invalid. You can continue without it or enter a valid code.");
   168→      return;
   169→    }
   170→
   171→    setIsLoading(true);
   172→
   173→    try {
   174→      const response = await api.register({
   175→        email: formData.email,
   176→        password: formData.password,
   177→        username: formData.username,
   178→        termsAccepted: formData.termsAccepted,
   179→        referralCode: formData.referralCode || undefined,
   180→      });
   181→
   182→      // Store token
   183→      localStorage.setItem("auth_token", response.access_token);
   184→      api.setToken(response.access_token);
   185→
   186→      // Store referral discount if valid code was used (extract from message)
   187→      if (formData.referralCode && referralStatus.valid && referralStatus.message) {
   188→        // Extract discount percentage from message like "Code applied! 40% discount from Nick J"
   189→        const match = referralStatus.message.match(/(\d+)%/);
   190→        if (match) {
   191→          localStorage.setItem("referral_discount", match[1]);
   192→        }
   193→      }
   194→
   195→      router.push("/gender");
   196→    } catch (err) {
   197→      setError(err instanceof Error ? err.message : "Registration failed");
   198→    } finally {
   199→      setIsLoading(false);
   200→    }
   201→  };
   202→
   203→  const isFormValid =
   204→    formData.email &&
   205→    formData.password.length >= 8 &&
   206→    formData.password === formData.confirmPassword &&
   207→    usernameStatus.available === true &&
   208→    formData.termsAccepted;
   209→
   210→  return (
   211→    <div className="min-h-screen flex flex-col items-center justify-center bg-black px-4 py-8">
   212→      <div className="w-full max-w-sm">
   213→        {/* Logo and Header */}
   214→        <div className="mb-8">
   215→          <div className="flex justify-center mb-6">
   216→            <div className="h-8 w-8 rounded bg-cyan-400/20 flex items-center justify-center">
   217→              <span className="text-cyan-400 text-sm font-bold">L</span>
   218→            </div>
   219→          </div>
   220→          <h1 className="text-2xl font-semibold tracking-tight text-center text-white mb-2">
   221→            Create Account
   222→          </h1>
   223→          <p className="text-sm text-neutral-400 text-center">
   224→            Join LOOKSMAXX and start your analysis
   225→          </p>
   226→        </div>
   227→
   228→        <form onSubmit={handleSubmit} className="space-y-4">
   229→          {/* Email */}
   230→          <div>
   231→            <label className="block text-sm text-neutral-400 mb-1.5">Email</label>
   232→            <input
   233→              type="email"
   234→              value={formData.email}
   235→              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
   236→              className="w-full h-11 px-3.5 text-sm bg-black border border-neutral-700 rounded-lg text-white focus:outline-none focus:border-cyan-400 transition-all"
   237→              placeholder="you@example.com"
   238→              required
   239→            />
   240→          </div>
   241→
   242→          {/* Username */}
   243→          <div>
   244→            <label className="block text-sm text-neutral-400 mb-1.5">
   245→              Username <span className="text-neutral-600">(shown on leaderboard)</span>
   246→            </label>
   247→            <div className="relative">
   248→              <input
   249→                type="text"
   250→                value={formData.username}
   251→                onChange={(e) => setFormData({ ...formData, username: e.target.value })}
   252→                placeholder="Choose a unique username"
   253→                className={`w-full h-11 px-3.5 pr-10 text-sm bg-black border rounded-lg text-white focus:outline-none transition-all ${
   254→                  usernameStatus.available === true
   255→                    ? "border-green-500"
   256→                    : usernameStatus.available === false
   257→                    ? "border-red-500"
   258→                    : "border-neutral-700 focus:border-cyan-400"
   259→                }`}
   260→                required
   261→              />
   262→              <div className="absolute right-3 top-1/2 -translate-y-1/2">
   263→                {usernameStatus.checking ? (
   264→                  <LoaderIcon className="w-4 h-4 text-neutral-500" />
   265→                ) : usernameStatus.available === true ? (
   266→                  <CheckIcon className="w-4 h-4 text-green-500" />
   267→                ) : usernameStatus.available === false ? (
   268→                  <XIcon className="w-4 h-4 text-red-500" />
   269→                ) : null}
   270→              </div>
   271→            </div>
   272→            {usernameStatus.reason && (
   273→              <p
   274→                className={`text-xs mt-1 ${
   275→                  usernameStatus.available ? "text-green-400" : "text-red-400"
   276→                }`}
   277→              >
   278→                {usernameStatus.reason}
   279→              </p>
   280→            )}
   281→            <p className="text-xs text-neutral-600 mt-1">
   282→              3-30 characters, letters, numbers, and underscores only
   283→            </p>
   284→          </div>
   285→
   286→          {/* Password */}
   287→          <div>
   288→            <label className="block text-sm text-neutral-400 mb-1.5">Password</label>
   289→            <div className="relative">
   290→              <input
   291→                type={showPassword ? "text" : "password"}
   292→                value={formData.password}
   293→                onChange={(e) => setFormData({ ...formData, password: e.target.value })}
   294→                className="w-full h-11 px-3.5 pr-10 text-sm bg-black border border-neutral-700 rounded-lg text-white focus:outline-none focus:border-cyan-400 transition-all"
   295→                placeholder="Minimum 8 characters"
   296→                required
   297→                minLength={8}
