'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import Link from 'next/link';
import { Check, X, Sparkles, Zap, ArrowLeft, Loader2, Tag } from 'lucide-react';
import { motion } from 'framer-motion';
import { api } from '@/lib/api';

interface Plan {
  id: 'basic' | 'pro' | 'consultation';
  name: string;
  price: number;
  period: string;
  description: string;
  features: { text: string; included: boolean }[];
  highlighted: boolean;
  badge?: string;
}

const plans: Plan[] = [
  {
    id: 'basic',
    name: 'Basic',
    price: 24.99,
    period: 'one-time',
    description: 'Perfect for non-surgical improvements',
    features: [
      { text: '60+ Facial Ratios', included: true },
      { text: 'Overall Harmony Score', included: true },
      { text: 'Full Strength & Flaws Analysis', included: true },
      { text: 'Non-Surgical Treatment Plan', included: true },
      { text: 'Surgical Treatment Options', included: false },
    ],
    highlighted: false,
  },
  {
    id: 'pro',
    name: 'Pro',
    price: 49.99,
    period: 'one-time',
    description: 'Complete transformation roadmap',
    features: [
      { text: '60+ Facial Ratios', included: true },
      { text: 'Overall Harmony Score', included: true },
      { text: 'Full Strength & Flaws Analysis', included: true },
      { text: 'Personalized Treatment Plan', included: true },
      { text: 'Surgical Treatment Options', included: true },
    ],
    highlighted: true,
    badge: 'Most Popular',
  },
  {
    id: 'consultation',
    name: 'Expert Review',
    price: 299.00,
    period: 'one-time',
    description: 'Manual review by aesthetic expert',
    features: [
      { text: 'Everything in Pro', included: true },
      { text: 'Video Consultation', included: true },
      { text: 'Custom Morphing', included: true },
      { text: 'Priority Support', included: true },
      { text: 'Surgeon Referral', included: true },
    ],
    highlighted: false,
    badge: 'Sold Out',
  },
];

export default function PricingPage() {
  const router = useRouter();
  const [loadingPlan, setLoadingPlan] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [discount, setDiscount] = useState<number | null>(null);

  useEffect(() => {
    // Check if user is authenticated
    const token = api.getToken();
    setIsAuthenticated(!!token);

    // Check for referral discount in localStorage (set during signup)
    const referralDiscount = localStorage.getItem('referral_discount');
    if (referralDiscount) {
      setDiscount(parseInt(referralDiscount, 10));
    }
  }, []);

  const handleCheckout = async (planId: 'basic' | 'pro' | 'consultation') => {
    if (!isAuthenticated) {
      // Redirect to signup
      router.push('/signup');
      return;
    }

    setLoadingPlan(planId);
    setError(null);

    try {
      const result = await api.createCheckout(planId);
      // Redirect to Stripe checkout
      window.location.href = result.checkout_url;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to start checkout');
      setLoadingPlan(null);
    }
  };

  const getDiscountedPrice = (price: number) => {
    if (!discount) return price;
    return price * (1 - discount / 100);
  };

  return (
    <main className="min-h-screen bg-black">
      {/* Header */}
      <header className="fixed top-0 left-0 right-0 bg-black/80 backdrop-blur-sm border-b border-neutral-800 z-50">
        <div className="max-w-5xl mx-auto px-4 h-16 flex items-center justify-between">
          <Link href="/" className="flex items-center gap-2">
            <div className="h-8 w-8 rounded bg-cyan-400/20 flex items-center justify-center">
              <span className="text-cyan-400 text-sm font-bold">L</span>
            </div>
            <span className="text-lg font-semibold text-white">LOOKSMAXX</span>
          </Link>
          <Link
            href="/results"
            className="flex items-center gap-2 text-sm text-neutral-400 hover:text-white transition-colors"
          >
            <ArrowLeft className="w-4 h-4" />
            Back to Results
          </Link>
        </div>
      </header>

      {/* Content */}
      <div className="pt-24 pb-16 px-4">
        <div className="max-w-4xl mx-auto">
          {/* Title */}
          <motion.div
            className="text-center mb-12"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
          >
            <h1 className="text-4xl font-bold text-white mb-4">
              Unlock Your Full Potential
            </h1>
            <p className="text-neutral-400 text-lg max-w-xl mx-auto">
              Get personalized recommendations and a complete roadmap to maximize your facial harmony.
            </p>

            {/* Discount Banner */}
            {discount && (
              <motion.div
                className="mt-6 inline-flex items-center gap-2 px-4 py-2 bg-green-500/20 border border-green-500/30 rounded-full"
                initial={{ scale: 0 }}
                animate={{ scale: 1 }}
                transition={{ delay: 0.3, type: 'spring' }}
              >
                <Tag className="w-4 h-4 text-green-400" />
                <span className="text-green-400 font-medium">
                  {discount}% discount applied!
                </span>
              </motion.div>
            )}
          </motion.div>

          {/* Error */}
          {error && (
            <div className="max-w-md mx-auto mb-8 p-4 bg-red-500/20 border border-red-500/30 rounded-xl text-center">
              <p className="text-red-400 text-sm">{error}</p>
            </div>
          )}

          {/* Plans */}
          <div className="grid md:grid-cols-2 gap-6 max-w-3xl mx-auto">
            {plans.map((plan, index) => (
              <motion.div
                key={plan.id}
                className={`relative rounded-2xl p-6 ${plan.highlighted
                  ? 'bg-gradient-to-br from-cyan-500/10 to-blue-600/10 border-2 border-cyan-500/50'
                  : 'bg-neutral-900/50 border border-neutral-800'
                  }`}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.1 * index }}
              >
                {/* Badge */}
                {plan.badge && (
                  <div className="absolute -top-3 left-1/2 -translate-x-1/2 px-3 py-1 bg-cyan-500 text-black text-xs font-bold rounded-full">
                    {plan.badge}
                  </div>
                )}

                {/* Plan Header */}
                <div className="text-center mb-6">
                  <h2 className="text-xl font-bold text-white mb-2">{plan.name}</h2>
                  <p className="text-neutral-400 text-sm mb-4">{plan.description}</p>

                  {/* Price */}
                  <div className="flex items-baseline justify-center gap-1">
                    {discount ? (
                      <>
                        <span className="text-2xl text-neutral-500 line-through">
                          ${plan.price}
                        </span>
                        <span className="text-4xl font-bold text-white">
                          ${getDiscountedPrice(plan.price).toFixed(2)}
                        </span>
                      </>
                    ) : (
                      <span className="text-4xl font-bold text-white">
                        ${plan.price}
                      </span>
                    )}
                    <span className="text-neutral-500 ml-1">{plan.period}</span>
                  </div>
                </div>

                {/* Features */}
                <ul className="space-y-3 mb-6">
                  {plan.features.map((feature, featureIndex) => (
                    <li
                      key={featureIndex}
                      className="flex items-center gap-3 text-sm"
                    >
                      {feature.included ? (
                        <Check className="w-5 h-5 text-green-400 flex-shrink-0" />
                      ) : (
                        <X className="w-5 h-5 text-neutral-600 flex-shrink-0" />
                      )}
                      <span className={feature.included ? 'text-white' : 'text-neutral-500'}>
                        {feature.text}
                      </span>
                    </li>
                  ))}
                </ul>

                {/* CTA Button */}
                <button
                  onClick={() => handleCheckout(plan.id)}
                  disabled={loadingPlan !== null || plan.badge === 'Sold Out'}
                  className={`w-full py-3 rounded-xl font-medium flex items-center justify-center gap-2 transition-all ${plan.badge === 'Sold Out'
                    ? 'bg-neutral-800 text-neutral-500 cursor-not-allowed border border-neutral-700'
                    : plan.highlighted
                      ? 'bg-cyan-500 text-black hover:bg-cyan-400 disabled:bg-cyan-500/50'
                      : 'bg-neutral-800 text-white hover:bg-neutral-700 disabled:bg-neutral-800/50'
                    } disabled:cursor-not-allowed`}
                >
                  {loadingPlan === plan.id ? (
                    <>
                      <Loader2 className="w-4 h-4 animate-spin" />
                      Processing...
                    </>
                  ) : plan.badge === 'Sold Out' ? (
                    'Sold Out / Waitlist'
                  ) : (
                    <>
                      {plan.highlighted ? (
                        <Sparkles className="w-4 h-4" />
                      ) : (
                        <Zap className="w-4 h-4" />
                      )}
                      {isAuthenticated ? 'Get Started' : 'Sign Up to Purchase'}
                    </>
                  )}
                </button>
              </motion.div>
            ))}
          </div>

          {/* Trust indicators */}
          <div className="mt-12 text-center">
            <p className="text-neutral-500 text-sm">
              Secure payment powered by Stripe. 30-day money-back guarantee.
            </p>
          </div>
        </div>
      </div>
    </main>
  );
}

'use client';

import React from 'react';
import Link from 'next/link';
import { ArrowLeft } from 'lucide-react';

const faqs = [
    {
        category: "General",
        items: [
            { q: "What is LooksMaxx AI?", a: "LooksMaxx AI is a premium facial morphometric engine that uses artificial intelligence to analyze facial landmarks, symmetry, and proportions based on established aesthetic scientific canons." },
            { q: "Is this medically accurate?", a: "While our algorithms are based on anthropometric research and neo-classical canons, this is an aesthetic validation tool, not a medical diagnosis device. Always consult with a board-certified plastic surgeon for medical advice." },
        ]
    },
    {
        category: "Analysis & Metrics",
        items: [
            { q: "How many ratios do you track?", a: "Our Pro engine tracks over 60 distinct facial ratios, including the golden ratio, canthal tilt, gonial angle, midface height, and more." },
            { q: "What is the PSL scale?", a: "The 'Pretty Scale Level' (PSL) is a standardized aesthetic metric used in high-fashion and modeling industries to quantify facial attractiveness on a 1-10 scale based on developmental stability and harmony." },
        ]
    },
    {
        category: "Privacy & Security",
        items: [
            { q: "Are my photos private?", a: "Yes. Your photos are processed securely and are never shared with third parties. You have the right to delete your data at any time from your settings." },
            { q: "Do you store my biometric data?", a: "We only store the landmark coordinates and resultant scores to provide your analysis history. Raw images can be deleted immediately after processing." },
        ]
    }
];

export default function FAQPage() {
    return (
        <main className="min-h-screen bg-black text-white selection:bg-cyan-500/30">
            <div className="max-w-4xl mx-auto px-6 pt-32 pb-20">
                <Link href="/" className="inline-flex items-center gap-2 text-neutral-500 hover:text-cyan-400 text-xs font-black uppercase tracking-widest mb-12 transition-colors">
                    <ArrowLeft size={14} />
                    Back to Labs
                </Link>

                <header className="mb-20">
                    <h1 className="text-5xl font-black tracking-tighter italic uppercase mb-4">
                        Knowledge <span className="text-cyan-400">Base</span>
                    </h1>
                    <p className="text-neutral-500 font-medium max-w-md uppercase text-xs tracking-[0.2em]">
                        Frequently Asked Questions & Technical Documentation
                    </p>
                </header>

                <div className="space-y-16">
                    {faqs.map((cat, idx) => (
                        <section key={idx}>
                            <h2 className="text-[10px] font-black uppercase tracking-[0.4em] text-neutral-600 mb-8 flex items-center gap-4">
                                {cat.category}
                                <div className="flex-1 h-px bg-neutral-900" />
                            </h2>
                            <div className="space-y-4">
                                {cat.items.map((item, iIdx) => (
                                    <div key={iIdx} className="p-8 rounded-3xl bg-neutral-900/30 border border-white/5 hover:border-white/10 transition-colors">
                                        <h3 className="text-lg font-black italic uppercase text-white mb-4">{item.q}</h3>
                                        <p className="text-neutral-400 text-sm font-medium leading-relaxed">{item.a}</p>
                                    </div>
                                ))}
                            </div>
                        </section>
                    ))}
                </div>

                <footer className="mt-20 pt-12 border-t border-neutral-900 flex flex-col items-center gap-6">
                    <p className="text-neutral-600 text-[10px] font-black uppercase tracking-widest">Still have technical queries?</p>
                    <Link href="mailto:support@looksmaxx.app" className="h-12 px-8 rounded-2xl bg-white/5 border border-white/10 text-white font-black uppercase tracking-widest text-xs flex items-center hover:bg-white/10 transition-all">
                        Contact Ops
                    </Link>
                </footer>
            </div>
        </main>
    );
}

