              <span className="text-2xl font-black italic text-yellow-400">#{userRank.genderRank}</span>
              <span className="text-[10px] font-black uppercase tracking-widest text-neutral-600">Top {userRank.percentile.toFixed(1)}%</span>
            </div>
          </div>
          <ChevronRight size={16} className="text-neutral-700 group-hover:text-yellow-400 transition-colors ml-1" />
        </>
      ) : (
        <>
          <div className="flex items-center justify-center w-11 h-11 rounded-xl bg-neutral-900 border border-white/10">
            <Trophy size={20} className="text-neutral-600" />
          </div>
          <div className="text-left">
            <p className="text-[9px] font-black uppercase tracking-widest text-neutral-600">Your Rank</p>
            <span className="text-xs font-medium text-neutral-500">
              {isAuthenticated ? 'Not ranked yet' : 'Sign in to rank'}
            </span>
          </div>
          <ChevronRight size={16} className="text-neutral-700 group-hover:text-neutral-500 transition-colors ml-1" />
        </>
      )}
    </motion.button>
  );
}

// ============================================
// MOBILE HEADER
// ============================================

interface MobileHeaderProps {
  onMenuClick: () => void;
}

function MobileHeader({ onMenuClick }: MobileHeaderProps) {
  const { activeTab, overallScore } = useResults();
  const currentTab = TABS.find((t) => t.id === activeTab);

  return (
    <header className="flex items-center justify-between px-4 py-4 bg-black border-b border-white/5">
      <button
        onClick={onMenuClick}
        className="p-2 hover:bg-white/5 rounded-xl transition-colors"
      >
        <Menu size={18} className="text-neutral-500" />
      </button>
      <div className="flex items-center gap-2">
        {currentTab?.icon}
        <span className="text-xs font-black uppercase tracking-widest text-white">{currentTab?.label}</span>
      </div>
      <div className="flex items-center gap-2">
        <RankBadge size="sm" alwaysShow />
        <div className="w-7 h-7 rounded-lg bg-cyan-500/20 flex items-center justify-center">
          <span className="text-[10px] text-cyan-400 font-black">{typeof overallScore === 'number' ? Math.round(overallScore) : overallScore}</span>
        </div>
      </div>
    </header>
  );
}

// ============================================
// MAIN LAYOUT
// ============================================

interface ResultsLayoutProps {
  children: React.ReactNode;
}

export function ResultsLayout({ children }: ResultsLayoutProps) {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const { activeTab } = useResults();

  return (
    <div className="flex min-h-screen bg-black">
      {/* Desktop Sidebar */}
      <aside className="hidden md:flex md:w-[280px] md:flex-col md:fixed md:inset-y-0 z-40">
        <Sidebar />
      </aside>

      {/* Mobile Menu Overlay */}
      <AnimatePresence>
        {mobileMenuOpen && (
          <>
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setMobileMenuOpen(false)}
              className="fixed inset-0 bg-black/80 backdrop-blur-sm z-40 md:hidden"
            />
            <motion.aside
              initial={{ x: -280 }}
              animate={{ x: 0 }}
              exit={{ x: -280 }}
              transition={{ type: 'spring', damping: 25, stiffness: 200 }}
              className="fixed inset-y-0 left-0 w-[280px] z-50 md:hidden"
            >
              <Sidebar onClose={() => setMobileMenuOpen(false)} />
            </motion.aside>
          </>
        )}
      </AnimatePresence>

      {/* Main Content */}
      <div className="flex-1 md:pl-[280px] flex flex-col min-h-screen">
        {/* Mobile Header */}
        <div className="md:hidden">
          <MobileHeader onMenuClick={() => setMobileMenuOpen(true)} />
        </div>

        {/* Desktop Floating Rank Indicator */}
        <div className="hidden md:block fixed top-5 right-5 z-30">
          <FloatingRankCard />
        </div>

        {/* Content Area - no overflow so sticky works */}
        <main className="flex-1">
          <AnimatePresence mode="wait">
            <motion.div
              key={activeTab}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
            >
              {children}
            </motion.div>
          </AnimatePresence>
        </main>
      </div>
    </div>
  );
}

// ============================================
// TAB CONTENT WRAPPER
// ============================================

interface TabContentProps {
  title: string | React.ReactNode;
  subtitle?: string;
  children: React.ReactNode;
  rightContent?: React.ReactNode;
  actions?: React.ReactNode;
}

export function TabContent({ title, subtitle, children, rightContent, actions }: TabContentProps) {
  const { frontRatios, sideRatios, overallScore, pslRating } = useResults();
  const { plan } = useQuota();
  const [isExporting, setIsExporting] = useState(false);
  const [toast, setToast] = useState<{ message: string; type: 'success' | 'error' } | null>(null);

  const showToast = useCallback((message: string, type: 'success' | 'error' = 'success') => {
    setToast({ message, type });
    setTimeout(() => setToast(null), 3000);
  }, []);

  const handleExport = useCallback(async () => {
    setIsExporting(true);
    try {
      const result = await exportToPDF('pdf-report-container-layout', {
        filename: `ProjectCassanova_Report_${new Date().toISOString().split('T')[0]}`,
        scale: 2,
        pageFormat: 'a4',
        margins: 10
      });

      if (result.success) {
        showToast('PDF exported successfully!');
      } else {
        showToast('Failed to generate PDF', 'error');
      }
    } catch {
      showToast('Export failed', 'error');
    } finally {
      setIsExporting(false);
    }
  }, [showToast]);

  const handleShare = useCallback(async () => {
    // Share URL with results
    const url = window.location.href;
    if (navigator.share) {
      try {
        await navigator.share({ title: 'My Looksmaxx Results', url });
        showToast('Shared successfully!');
      } catch {
        // User cancelled or share failed
      }
    } else {
      navigator.clipboard.writeText(url);
      showToast('Link copied to clipboard!');
    }
  }, [showToast]);

  // Handle both string titles (auto-highlight last word) and ReactNode titles (custom styling)
  const renderTitle = () => {
    if (typeof title === 'string') {
      const titleWords = title.split(' ');
      const lastWord = titleWords.pop();
      const firstWords = titleWords.join(' ');
      return <>{firstWords} <span className="text-cyan-400">{lastWord}</span></>;
    }
    return title;
  };

  return (
    <div className="p-6 md:p-10 lg:p-12 max-w-6xl mx-auto">
      {/* Toast notification */}
      <AnimatePresence>
        {toast && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className={`fixed top-4 right-4 z-50 px-4 py-3 rounded-lg shadow-lg flex items-center gap-2 ${
              toast.type === 'success'
                ? 'bg-neutral-800 border border-neutral-700'
                : 'bg-red-900/80 border border-red-700'
            }`}
          >
            <CheckCircle size={16} className={toast.type === 'success' ? 'text-cyan-400' : 'text-red-400'} />
            <span className="text-sm text-white">{toast.message}</span>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Header - pr-[180px] on desktop to prevent overlap with fixed FloatingRankCard */}
      <div className="flex flex-col md:flex-row md:items-start md:justify-between mb-10 md:mb-14 md:pr-[180px]">
        <div>
          <h1 className="text-4xl md:text-5xl font-black tracking-tighter italic uppercase mb-3">
            {renderTitle()}
          </h1>
          {subtitle && (
            <p className="text-neutral-500 font-medium uppercase text-xs tracking-[0.2em] max-w-lg">
              {subtitle}
            </p>
          )}
        </div>
        {(rightContent || actions) && (
          <div className="mt-6 md:mt-0 flex items-center gap-3">
            {actions && (
              <div className="flex items-center gap-2">
                <button
                  onClick={handleShare}
                  className="flex items-center gap-2 px-4 py-2.5 rounded-xl bg-cyan-500/20 border border-cyan-500/30 text-cyan-400 text-[10px] font-black uppercase tracking-widest hover:bg-cyan-500/30 transition-all"
                >
                  <Share2 size={14} />
                  Share
                </button>
                <button
                  onClick={handleExport}
                  disabled={isExporting}
                  className="flex items-center gap-2 p-2.5 rounded-xl bg-white/5 border border-white/10 text-neutral-400 hover:bg-white/10 transition-all disabled:opacity-50"
                >
                  {isExporting ? <Loader2 size={14} className="animate-spin" /> : <Download size={14} />}
                </button>
              </div>
            )}
            {rightContent}
          </div>
        )}
      </div>

      {/* Content */}
      {children}

      {/* Hidden PDF Report Container */}
      <div style={{ position: 'fixed', left: '-9999px', top: 0 }}>
        <div id="pdf-report-container-layout">
          <AnalysisReport
            analysis={{
              front_image_url: typeof window !== 'undefined' ? localStorage.getItem('looksmaxx_front_photo') : null,
              side_image_url: typeof window !== 'undefined' ? localStorage.getItem('looksmaxx_side_photo') : null,
              front_landmarks: (() => {
                try {
                  if (typeof window === 'undefined') return [];
                  const results = JSON.parse(localStorage.getItem('looksmaxx_results') || '{}');
                  return results?.analysis?.front_landmarks || [];
                } catch { return []; }
              })(),
              side_landmarks: (() => {
                try {
                  if (typeof window === 'undefined') return [];
                  const results = JSON.parse(localStorage.getItem('looksmaxx_results') || '{}');
                  return results?.analysis?.side_landmarks || [];
                } catch { return []; }
              })(),
              scores: { masculinity: 50, femininity: 50, symmetry: 50, skinQuality: 50, aging: 25 },
            }}
            results={{ overallScore, pslRating, frontRatios, sideRatios }}
            userName={(() => {
              try {
                if (typeof window === 'undefined') return 'User';
                return JSON.parse(localStorage.getItem('user') || '{}').username || 'User';
              } catch { return 'User'; }
            })()}
            isUnlocked={plan === 'pro' || plan === 'plus'}
          />
        </div>
      </div>
    </div>
  );
}

// ============================================
// SECTION HEADER (For use in tab content)
// ============================================

interface SectionHeaderProps {
  title: string;
  className?: string;
}

export function SectionHeader({ title, className = '' }: SectionHeaderProps) {
  return (
    <h2 className={`text-[10px] font-black uppercase tracking-[0.4em] text-neutral-600 mb-8 flex items-center gap-4 ${className}`}>
      {title}
      <div className="flex-1 h-px bg-neutral-900" />
    </h2>
  );
}

